   265→   265→// =============================================================================
   266→   266→// Stripe Webhook (must be before express.json())
   267→   267→// =============================================================================
   268→   268→
   269→   269→app.post('/webhooks/stripe', express.raw({ type: 'application/json' }), async (req, res) => {
   270→   270→  const sig = req.headers['stripe-signature'];
   271→   271→  const webhookSecret = process.env.STRIPE_WEBHOOK_SECRET;
   272→   272→
   273→   273→  let event;
   274→   274→
   275→   275→  try {
   276→   276→    if (webhookSecret) {
   277→   277→      event = stripe.webhooks.constructEvent(req.body, sig, webhookSecret);
   278→   278→    } else if (isProduction) {
   279→   279→      // SECURITY: Reject unsigned webhooks in production
   280→   280→      console.error('[SPLICE] CRITICAL: STRIPE_WEBHOOK_SECRET not set in production');
   281→   281→      return res.status(500).json({ error: 'Webhook configuration error: secret not configured' });
   282→   282→    } else {
   283→   283→      // For local development testing only
   284→   284→      // req.body is a Buffer from express.raw(), convert to string for JSON.parse
   285→   285→      const bodyString = typeof req.body === 'string' ? req.body : req.body.toString('utf8');
   286→   286→      event = JSON.parse(bodyString);
   287→   287→      console.warn('[SPLICE] Warning: Processing webhook without signature verification (dev only)');
   288→   288→    }
   289→   289→  } catch (err) {
   290→   290→    console.error('[SPLICE] Webhook signature verification failed:', err.message);
   291→   291→    return res.status(400).json({ error: 'Webhook signature verification failed' });
   292→   292→  }
   293→   293→
   294→   294→  console.log(`[SPLICE] Webhook received: ${event.type} (${event.id})`);
   295→   295→
   296→   296→  // Idempotency check - skip if already processed
   297→   297→  if (await usageTracking.isEventProcessed(event.id)) {
   298→   298→    console.log(`[SPLICE] Event ${event.id} already processed, skipping`);
   299→   299→    return res.json({ received: true, skipped: true });
   300→   300→  }
   301→   301→
   302→   302→  try {
   303→   303→    switch (event.type) {
   304→   304→      case 'customer.subscription.created':
   305→   305→      case 'customer.subscription.updated': {
   306→   306→        const subscription = event.data.object;
   307→   307→        const customerId = subscription.customer;
   308→   308→
   309→   309→        // Validate customerId
   310→   310→        if (!customerId) {
   311→   311→          console.error('[SPLICE] Missing customer ID in subscription event');
   312→   312→          return res.status(400).json({ error: 'Missing customer ID' });
   313→   313→        }
   314→   314→
   315→   315→        // Get tier from price ID
   316→   316→        const priceId = subscription.items?.data?.[0]?.price?.id;
   317→   317→        const tier = getTierFromPriceId(priceId);
   318→   318→
   319→   319→        // Update user tier and reset hours
   320→   320→        await usageTracking.updateTier(customerId, tier);
   321→   321→        console.log(`[SPLICE] Updated customer ${customerId} to tier: ${tier}`);
   322→   322→
   323→   323→        // Update trial end date if present
   324→   324→        if (subscription.trial_end) {
   325→   325→          await usageTracking.updateTrialEnd(customerId, subscription.trial_end);
   326→   326→        }
   327→   327→
   328→   328→        // Generate license key for new subscriptions with retry and delivery
   329→   329→        if (event.type === 'customer.subscription.created') {
   330→   330→          let licenseResult = null;
   331→   331→          let retryCount = 0;
   332→   332→          const maxRetries = 3;
   333→   333→
   334→   334→          // Retry mechanism for license key generation
   335→   335→          while (retryCount < maxRetries) {
   336→   336→            licenseResult = await licenseService.generateLicenseKey(customerId);
   337→   337→            if (licenseResult.success) {
   338→   338→              break;
   339→   339→            }
   340→   340→            retryCount++;
   341→   341→            console.warn(`[SPLICE] License key generation attempt ${retryCount}/${maxRetries} failed: ${licenseResult.error}`);
   342→   342→            // Wait before retry (exponential backoff)
   343→   343→            if (retryCount < maxRetries) {
   344→   344→              await new Promise(resolve => setTimeout(resolve, 1000 * retryCount));
   345→   345→            }
   346→   346→          }
   347→   347→
   348→   348→          if (licenseResult && licenseResult.success) {
   349→   349→            // SECURITY: Mask license key in logs
   350→   350→            console.log(`[SPLICE] Generated license key for ${maskSensitiveData(customerId)}: ${maskSensitiveData(licenseResult.key)}`);
   351→   351→
   352→   352→            // Store license key in Stripe subscription metadata as backup
   353→   353→            try {
   354→   354→              await stripe.subscriptions.update(subscription.id, {
   355→   355→                metadata: {
   356→   356→                  license_key: licenseResult.key,
   357→   357→                  license_generated_at: new Date().toISOString()
   358→   358→                }
   359→   359→              });
   360→   360→              console.log(`[SPLICE] Stored license key in Stripe metadata for subscription ${subscription.id}`);
   361→   361→            } catch (stripeErr) {
   362→   362→              console.error(`[SPLICE] Failed to store license key in Stripe metadata:`, stripeErr.message);
   363→   363→            }
   364→   364→
   365→   365→            // Get customer email and send license key
   366→   366→            try {
   367→   367→              const customer = await stripe.customers.retrieve(customerId);
   368→   368→              if (customer.email) {
   369→   369→                // SECURITY: Mask sensitive data in logs
   370→   370→                console.log(`[SPLICE] License key ready for delivery to ${maskSensitiveData(customer.email)}: ${maskSensitiveData(licenseResult.key)}`);
   371→   371→
   372→   372→                // Send license key email
   373→   373→                try {
   374→   374→                  await emailService.sendLicenseKeyEmail(customer.email, licenseResult.key, tier);
   375→   375→                  console.log(`[SPLICE] License key email sent to ${maskSensitiveData(customer.email)}`);
   376→   376→                } catch (sendErr) {
   377→   377→                  // Log but don't fail - license is stored in Stripe metadata as backup
   378→   378→                  console.error(`[SPLICE] Failed to send license email:`, sendErr.message);
   379→   379→                }
   380→   380→
   381→   381→                // Store email in database for reference
   382→   382→                await usageTracking.updateTier(customerId, tier, customer.email);
   383→   383→              } else {
   384→   384→                console.warn(`[SPLICE] No email found for customer ${customerId}`);
   385→   385→              }
   386→   386→            } catch (emailErr) {
   387→   387→              console.error(`[SPLICE] Error getting customer email:`, emailErr.message);
   388→   388→            }
   389→   389→          } else {
   390→   390→            // CRITICAL: License generation failed - return 500 to trigger Stripe retry
   391→   391→            const errorMsg = `Failed to generate license key after ${maxRetries} attempts: ${licenseResult?.error || 'Unknown error'}`;
   392→   392→            console.error(`[SPLICE] ${errorMsg}`);
   393→   393→            return res.status(500).json({ error: errorMsg });
   394→   394→          }
   395→   395→        }
   396→   396→        break;
   397→   397→      }
   398→   398→
   399→   399→      case 'customer.subscription.deleted': {
   400→   400→        const subscription = event.data.object;
   401→   401→        const customerId = subscription.customer;
   402→   402→
   403→   403→        // Validate customerId
   404→   404→        if (!customerId) {
   405→   405→          console.error('[SPLICE] Missing customer ID in subscription.deleted event');
   406→   406→          return res.status(400).json({ error: 'Missing customer ID' });
   407→   407→        }
   408→   408→
   409→   409→        // Downgrade to cancelled (0 hours)
   410→   410→        await usageTracking.updateTier(customerId, 'cancelled');
   411→   411→        console.log(`[SPLICE] Subscription cancelled for customer ${customerId}`);
   412→   412→        break;
   413→   413→      }
   414→   414→
   415→   415→      case 'invoice.payment_succeeded': {
   416→   416→        const invoice = event.data.object;
   417→   417→        const customerId = invoice.customer;
   418→   418→        const subscriptionId = invoice.subscription;
   419→   419→
   420→   420→        // Validate customerId
   421→   421→        if (!customerId) {
   422→   422→          console.error('[SPLICE] Missing customer ID in invoice event');
   423→   423→          return res.status(400).json({ error: 'Missing customer ID' });
   424→   424→        }
   425→   425→
   426→   426→        // Reset hours on successful payment (new billing period)
   427→   427→        let tier = 'starter';
   428→   428→        if (subscriptionId) {
   429→   429→          const subscription = await stripe.subscriptions.retrieve(subscriptionId);
   430→   430→          const priceId = subscription.items?.data?.[0]?.price?.id;
   431→   431→          tier = getTierFromPriceId(priceId);
   432→   432→
   433→   433→          await usageTracking.resetHours(customerId, tier);
   434→   434→          console.log(`[SPLICE] Reset hours for customer ${customerId} (tier: ${tier})`);
   435→   435→        }
   436→   436→
   437→   437→        // Check for affiliate coupon and record commission
   438→   438→        const discount = invoice.discount;
   439→   439→        if (discount && discount.coupon) {
   440→   440→          const couponId = discount.coupon.id;
   441→   441→          // Check if this is an affiliate code (like JIMMYN)
   442→   442→          if (referralService.AFFILIATE_CODES[couponId]) {
   443→   443→            const amountPaid = invoice.amount_paid / 100; // Convert cents to dollars
   444→   444→            await referralService.recordAffiliateCommission(
   445→   445→              couponId,
   446→   446→              customerId,
   447→   447→              amountPaid,
   448→   448→              tier
   449→   449→            );
   450→   450→            console.log(`[SPLICE] Recorded affiliate commission for ${couponId}`);
   451→   451→          }
   452→   452→        }
   453→   453→        break;
   454→   454→      }
   455→   455→
   456→   456→      case 'invoice.payment_failed': {
   457→   457→        const invoice = event.data.object;
   458→   458→        const customerId = invoice.customer;
   459→   459→        const attemptCount = invoice.attempt_count || 1;
   460→   460→
   461→   461→        // Validate customerId
   462→   462→        if (!customerId) {
   463→   463→          console.error('[SPLICE] Missing customer ID in payment_failed event');
   464→   464→          return res.status(400).json({ error: 'Missing customer ID' });
   465→   465→        }
   466→   466→
   467→   467→        console.warn(`[SPLICE] Payment failed for customer ${customerId} (attempt ${attemptCount})`);
   468→   468→
   469→   469→        // Stripe will retry automatically per retry settings
   470→   470→        // On final failure, Stripe will cancel subscription which triggers customer.subscription.deleted
   471→   471→        // Send warning email on final attempt
   472→   472→        if (attemptCount >= 3) {
   473→   473→          console.error(`[SPLICE] Final payment attempt failed for customer ${customerId}`);
   474→   474→          // Send warning email to customer about impending cancellation
   475→   475→          try {
   476→   476→            const customer = await stripe.customers.retrieve(customerId);
   477→   477→            if (customer.email) {
   478→   478→              await emailService.sendPaymentWarningEmail(customer.email, attemptCount);
   479→   479→              console.log(`[SPLICE] Payment warning email sent to ${maskSensitiveData(customer.email)}`);
   480→   480→            }
   481→   481→          } catch (emailErr) {
   482→   482→            console.error(`[SPLICE] Failed to send payment warning email:`, emailErr.message);
   483→   483→          }
   484→   484→        }
   485→   485→        break;
   486→   486→      }
   487→   487→
   488→   488→      case 'customer.deleted': {
   489→   489→        const customer = event.data.object;
   490→   490→        const customerId = customer.id;
   491→   491→
   492→   492→        // Validate customerId
   493→   493→        if (!customerId) {
   494→   494→          console.error('[SPLICE] Missing customer ID in customer.deleted event');
   495→   495→          return res.status(400).json({ error: 'Missing customer ID' });
   496→   496→        }
   497→   497→
   498→   498→        // Clean up user data - downgrade to cancelled
   499→   499→        await usageTracking.updateTier(customerId, 'cancelled');
   500→   500→        console.log(`[SPLICE] Customer deleted: ${customerId}`);
   501→   501→        break;
   502→   502→      }
   503→   503→
   504→   504→      default:
   505→   505→        console.log(`[SPLICE] Unhandled event type: ${event.type}`);
   506→   506→    }
   507→   507→
   508→   508→    // Record event as processed (idempotency)
   509→   509→    await usageTracking.recordWebhookEvent(event.id, event.type);
   510→   510→
   511→   511→    res.json({ received: true });
   512→   512→  } catch (err) {
   513→   513→    console.error('[SPLICE] Webhook handler error:', err);
   514→   514→    res.status(500).json({ error: err.message });
   515→   515→  }
   516→   516→});
