   684→   684→// =============================================================================
   685→   685→// Graceful Shutdown
   686→   686→// =============================================================================
   687→   687→
   688→   688→async function gracefulShutdown(signal) {
   689→   689→  console.log(`[SPLICE] Received ${signal}, starting graceful shutdown...`);
   690→   690→
   691→   691→  // Stop accepting new connections
   692→   692→  if (server) {
   693→   693→    server.close(() => {
   694→   694→      console.log('[SPLICE] HTTP server closed');
   695→   695→    });
   696→   696→  }
   697→   697→
   698→   698→  // Close database pool
   699→   699→  try {
   700→   700→    await usageTracking.closePool();
   701→   701→    console.log('[SPLICE] Database pool closed');
   702→   702→  } catch (err) {
   703→   703→    console.error('[SPLICE] Error closing database pool:', err.message);
   704→   704→  }
   705→   705→
   706→   706→  // Give ongoing requests time to complete (max 10 seconds)
   707→   707→  setTimeout(() => {
   708→   708→    console.log('[SPLICE] Graceful shutdown complete');
   709→   709→    process.exit(0);
   710→   710→  }, 10000);
   711→   711→}
   712→   712→
   713→   713→// Handle termination signals
   714→   714→process.on('SIGTERM', () => gracefulShutdown('SIGTERM'));
   715→   715→process.on('SIGINT', () => gracefulShutdown('SIGINT'));
   716→   716→
   717→   717→// Handle uncaught exceptions
   718→   718→process.on('uncaughtException', (err) => {
   719→   719→  console.error('[SPLICE] Uncaught exception:', err);
   720→   720→  gracefulShutdown('uncaughtException');
   721→   721→});
   722→   722→
   723→   723→process.on('unhandledRejection', (reason, promise) => {
   724→   724→  console.error('[SPLICE] Unhandled rejection at:', promise, 'reason:', reason);
   725→   725→});
   726→   726→
   727→   727→startServer();
