  1139→
  1140→      if (status.status === 'completed' || status.status === 'failed') {
  1141→        clearInterval(musicState.pollInterval);
  1142→        musicState.pollInterval = null;
  1143→        musicState.currentPollingJobId = null;
  1144→        if (status.status === 'completed') {
