   493â†’async function runDetection() {
   494â†’    if (isOperationInProgress) return;
   495â†’
   496â†’    // Check sequence
   497â†’    const hasSequence = await checkSequenceOpen();
   498â†’    if (!hasSequence) {
   499â†’        setStatus('Please open a sequence first', true);
   500â†’        return;
   501â†’    }
   502â†’
   503â†’    // Check online
   504â†’    if (!isOnline()) {
   505â†’        setStatus('Offline - Check your connection', true);
   506â†’        return;
   507â†’    }
   508â†’
   509â†’    isOperationInProgress = true;
   510â†’    ui.goBtn.disabled = true;
   511â†’    showProgress('Detecting silences...');
   512â†’
   513â†’    try {
   514â†’        const settings = getSettings();
   515â†’
   516â†’        // Get sequence info
   517â†’        const seqInfo = await jsx.call('getActiveSequence');
   518â†’        console.log('[SPLICE] Sequence info:', seqInfo);
   519â†’
   520â†’        // Store sequence info for later use
   521â†’        currentSequenceInfo = seqInfo;
   522â†’        window.spliceState.activeSequence = seqInfo;
   523â†’        if (seqInfo && seqInfo.videoPath) {
   524â†’            window.spliceState.currentVideoPath = seqInfo.videoPath;
   525â†’        }
   526â†’
   527â†’        // Export audio via JSX for silence detection
   528â†’        updateProgress(10, 'Exporting audio...');
   529â†’
   530â†’        let audioFilePath = null;
   531â†’        try {
   532â†’            // Get sequence info for audio export
   533â†’            const exportResult = await jsx.call('exportSequenceAudioForAnalysis');
   534â†’            if (exportResult && exportResult.success && exportResult.outputPath) {
   535â†’                audioFilePath = exportResult.outputPath;
   536â†’                console.log('[SPLICE] Audio exported to:', audioFilePath);
   537â†’            } else if (exportResult && exportResult.error) {
   538â†’                throw new Error(exportResult.error);
   539â†’            }
   540â†’        } catch (exportError) {
   541â†’            console.warn('[SPLICE] Audio export failed, trying fallback:', exportError.message);
   542â†’            // Try to get audio from first clip in timeline
   543â†’            try {
   544â†’                const clipInfo = await jsx.call('getFirstClipAudioPath');
   545â†’                if (clipInfo && clipInfo.path) {
   546â†’                    audioFilePath = clipInfo.path;
   547â†’                    console.log('[SPLICE] Using clip audio path:', audioFilePath);
   548â†’                }
   549â†’            } catch (clipError) {
   550â†’                console.warn('[SPLICE] Fallback audio path failed:', clipError.message);
   551â†’            }
   552â†’        }
   553â†’
   554â†’        if (!audioFilePath) {
   555â†’            throw new Error('Could not export or locate audio file. Please ensure sequence has audio tracks.');
   556â†’        }
   557â†’
   558â†’        // Store audio path globally for other modules
   559â†’        currentAudioPath = audioFilePath;
   560â†’        window.spliceState.audioPath = audioFilePath;
   561â†’
   562â†’        // Call backend for silence detection with audio file path
   563â†’        updateProgress(30, 'Detecting silences...');
   564â†’        const silenceResponse = await fetchWithTimeout(
   565â†’            `${getBackendUrl()}/silences-rms`,
   566â†’            {
   567â†’                method: 'POST',
   568â†’                headers: getAuthHeaders(),
   569â†’                body: JSON.stringify({
   570â†’                    wavPath: audioFilePath,
   571â†’                    sensitivity: settings.sensitivity,
   572â†’                    minSilenceLength: settings.minSilenceLength || 0.5
   573â†’                })
   574â†’            }
   575â†’        );
   576â†’
   577â†’        if (!silenceResponse.ok) {
   578â†’            throw new Error(await parseErrorResponse(silenceResponse));
   579â†’        }
   580â†’
   581â†’        const silenceData = await silenceResponse.json();
   582â†’        previewSilences = silenceData.silences || [];
   583â†’
   584â†’        // Detect takes if enabled
   585â†’        if (settings.enableTakesDetection) {
   586â†’            updateProgress(60, 'Detecting takes...');
   587â†’            const analyzeResponse = await fetchWithTimeout(
   588â†’                `${getBackendUrl()}/analyze`,
   589â†’                {
   590â†’                    method: 'POST',
   591â†’                    headers: getAuthHeaders(),
   592â†’                    body: JSON.stringify({
   593â†’                        wavPath: audioFilePath,
   594â†’                        detectTakes: true
   595â†’                    })
   596â†’                }
   597â†’            );
   598â†’
   599â†’            if (analyzeResponse.ok) {
   600â†’                const analyzeData = await analyzeResponse.json();
   601â†’                previewTakes = analyzeData.takes || [];
   602â†’
   603â†’                // Store transcript for captions/music modules
   604â†’                if (analyzeData.transcript) {
   605â†’                    currentTranscript = analyzeData.transcript;
   606â†’                    window.currentTranscript = analyzeData.transcript;
   607â†’                    window.spliceState.lastTranscript = analyzeData.transcript;
   608â†’                }
   609â†’            }
   610â†’        }
   611â†’
   612â†’        // Detect chapters if enabled
   613â†’        if (settings.enableChapters) {
   614â†’            updateProgress(80, 'Detecting chapters...');
   615â†’            try {
   616â†’                const chapterResponse = await fetchWithTimeout(
   617â†’                    `${getBackendUrl()}/chapters`,
   618â†’                    {
   619â†’                        method: 'POST',
   620â†’                        headers: getAuthHeaders(),
   621â†’                        body: JSON.stringify({
   622â†’                            wavPath: audioFilePath,
   623â†’                            transcript: currentTranscript,
   624â†’                            maxChapters: settings.maxChapters || 10,
   625â†’                            minChapterLength: settings.minChapterLength || 60
   626â†’                        })
   627â†’                    }
   628â†’                );
   629â†’
   630â†’                if (chapterResponse.ok) {
   631â†’                    const chapterData = await chapterResponse.json();
   632â†’                    currentChapters = chapterData.chapters || [];
   633â†’                }
   634â†’            } catch (e) {
   635â†’                console.warn('[SPLICE] Chapter detection failed:', e);
   636â†’            }
   637â†’        }
   638â†’
   639â†’        // Detect zoom points if enabled
   640â†’        if (settings.enableZoom) {
   641â†’            updateProgress(90, 'Detecting zoom points...');
   642â†’            try {
   643â†’                const zoomResponse = await fetchWithTimeout(
   644â†’                    `${getBackendUrl()}/zoom`,
   645â†’                    {
   646â†’                        method: 'POST',
   647â†’                        headers: getAuthHeaders(),
   648â†’                        body: JSON.stringify({
   649â†’                            wavPath: audioFilePath,
   650â†’                            transcript: currentTranscript,
   651â†’                            frequency: settings.zoomFrequency || 'medium',
   652â†’                            preset: settings.zoomPreset || 'medium',
   653â†’                            placement: settings.zoomPlacement || 'sentence_start'
   654â†’                        })
   655â†’                    }
   656â†’                );
   657â†’
   658â†’                if (zoomResponse.ok) {
   659â†’                    const zoomData = await zoomResponse.json();
   660â†’                    currentZooms = zoomData.zoomPoints || [];
   661â†’                }
   662â†’            } catch (e) {
   663â†’                console.warn('[SPLICE] Zoom detection failed:', e);
   664â†’            }
   665â†’        }
   666â†’
   667â†’        // Store results
   668â†’        lastDetectionResult = {
   669â†’            silences: previewSilences,
   670â†’            takes: previewTakes,
   671â†’            chapters: currentChapters,
   672â†’            zooms: currentZooms
   673â†’        };
   674â†’
   675â†’        // Show preview
   676â†’        hideProgress();
   677â†’        showCombinedPreview();
   678â†’        setStatus(`Found ${previewSilences.length} silences, ${previewTakes.length} takes`);
   679â†’
   680â†’    } catch (error) {
   681â†’        console.error('[SPLICE] Detection error:', error);
   682â†’        setStatus('Detection failed: ' + error.message, true);
   683â†’        hideProgress();
   684â†’    } finally {
   685â†’        isOperationInProgress = false;
   686â†’        ui.goBtn.disabled = false;
