import type { Metadata } from "next";
import { Geist, Geist_Mono } from "next/font/google";
import Script from "next/script";
import "./globals.css";

const geistSans = Geist({
  variable: "--font-geist-sans",
  subsets: ["latin"],
});

const geistMono = Geist_Mono({
  variable: "--font-geist-mono",
  subsets: ["latin"],
});

export const metadata: Metadata = {
  title: "SPLICE - AI-Powered Video Editing for Premiere Pro | Edit 10x Faster",
  description:
    "Automatically remove silences, detect takes, generate AI music, and create animated captions. The most powerful AI editing toolkit for Adobe Premiere Pro.",
  keywords: [
    "video editing",
    "premiere pro plugin",
    "ai editing",
    "silence removal",
    "take detection",
    "animated captions",
    "ai music generation",
  ],
  authors: [{ name: "SPLICE" }],
  openGraph: {
    title: "SPLICE - AI-Powered Video Editing for Premiere Pro",
    description:
      "Edit 10x faster with AI. Automatically remove silences, detect takes, generate music, and create viral captions.",
    type: "website",
    locale: "en_US",
    siteName: "SPLICE",
  },
  twitter: {
    card: "summary_large_image",
    title: "SPLICE - AI-Powered Video Editing for Premiere Pro",
    description:
      "Edit 10x faster with AI. Automatically remove silences, detect takes, generate music, and create viral captions.",
  },
  robots: {
    index: true,
    follow: true,
  },
};

const GA_MEASUREMENT_ID = process.env.NEXT_PUBLIC_GA_MEASUREMENT_ID;

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  return (
    <html lang="en" className="scroll-smooth dark">
      <head>
        {GA_MEASUREMENT_ID && (
          <>
            <Script
              src={`https://www.googletagmanager.com/gtag/js?id=${GA_MEASUREMENT_ID}`}
              strategy="afterInteractive"
            />
            <Script
              id="google-analytics"
              strategy="afterInteractive"
              dangerouslySetInnerHTML={{
                __html: `
                  window.dataLayer = window.dataLayer || [];
                  function gtag(){dataLayer.push(arguments);}
                  gtag('js', new Date());
                  gtag('config', '${GA_MEASUREMENT_ID}');
                `,
              }}
            />
          </>
        )}
      </head>
      <body
        className={`${geistSans.variable} ${geistMono.variable} antialiased`}
      >
        {children}
      </body>
    </html>
  );
}

