   271→// =============================================================================
   272→// Stripe Webhook (must be before express.json())
   273→// =============================================================================
   274→
   275→app.post('/webhooks/stripe', express.raw({ type: 'application/json' }), async (req, res) => {
   276→  const sig = req.headers['stripe-signature'];
   277→  const webhookSecret = process.env.STRIPE_WEBHOOK_SECRET;
   278→
   279→  let event;
   280→
   281→  try {
   282→    if (webhookSecret) {
   283→      event = stripe.webhooks.constructEvent(req.body, sig, webhookSecret);
   284→    } else if (isProduction) {
   285→      // SECURITY: Reject unsigned webhooks in production
   286→      console.error('[SPLICE] CRITICAL: STRIPE_WEBHOOK_SECRET not set in production');
   287→      return res.status(500).json({ error: 'Webhook configuration error: secret not configured' });
   288→    } else {
   289→      // For local development testing only
   290→      // req.body is a Buffer from express.raw(), convert to string for JSON.parse
   291→      const bodyString = typeof req.body === 'string' ? req.body : req.body.toString('utf8');
   292→      event = JSON.parse(bodyString);
   293→      console.warn('[SPLICE] Warning: Processing webhook without signature verification (dev only)');
   294→    }
   295→  } catch (err) {
   296→    console.error('[SPLICE] Webhook signature verification failed:', err.message);
   297→    return res.status(400).json({ error: 'Webhook signature verification failed' });
   298→  }
   299→
   300→  console.log(`[SPLICE] Webhook received: ${event.type} (${event.id})`);
   301→
   302→  // Idempotency check - skip if already processed
   303→  if (await usageTracking.isEventProcessed(event.id)) {
   304→    console.log(`[SPLICE] Event ${event.id} already processed, skipping`);
   305→    return res.json({ received: true, skipped: true });
   306→  }
   307→
   308→  try {
   309→    switch (event.type) {
   310→      case 'customer.subscription.created':
   311→      case 'customer.subscription.updated': {
   312→        const subscription = event.data.object;
   313→        const customerId = subscription.customer;
   314→
   315→        // Validate customerId
   316→        if (!customerId) {
   317→          console.error('[SPLICE] Missing customer ID in subscription event');
   318→          return res.status(400).json({ error: 'Missing customer ID' });
   319→        }
   320→
   321→        // Get tier from price ID
   322→        const priceId = subscription.items?.data?.[0]?.price?.id;
   323→        const tier = getTierFromPriceId(priceId);
   324→
   325→        // Update user tier and reset hours
   326→        await usageTracking.updateTier(customerId, tier);
   327→        console.log(`[SPLICE] Updated customer ${customerId} to tier: ${tier}`);
   328→
   329→        // Update trial end date if present
   330→        if (subscription.trial_end) {
   331→          await usageTracking.updateTrialEnd(customerId, subscription.trial_end);
   332→        }
   333→
   334→        // Generate license key for new subscriptions with retry and delivery
   335→        if (event.type === 'customer.subscription.created') {
   336→          let licenseResult = null;
   337→          let retryCount = 0;
   338→          const maxRetries = 3;
   339→
   340→          // Retry mechanism for license key generation
   341→          while (retryCount < maxRetries) {
   342→            licenseResult = await licenseService.generateLicenseKey(customerId);
   343→            if (licenseResult.success) {
   344→              break;
   345→            }
   346→            retryCount++;
   347→            console.warn(`[SPLICE] License key generation attempt ${retryCount}/${maxRetries} failed: ${licenseResult.error}`);
   348→            // Wait before retry (exponential backoff)
   349→            if (retryCount < maxRetries) {
   350→              await new Promise(resolve => setTimeout(resolve, 1000 * retryCount));
   351→            }
   352→          }
   353→
   354→          if (licenseResult && licenseResult.success) {
   355→            // SECURITY: Mask license key in logs
   356→            console.log(`[SPLICE] Generated license key for ${maskSensitiveData(customerId)}: ${maskSensitiveData(licenseResult.key)}`);
   357→
   358→            // Store license key in Stripe subscription metadata as backup
   359→            try {
   360→              await stripe.subscriptions.update(subscription.id, {
   361→                metadata: {
   362→                  license_key: licenseResult.key,
   363→                  license_generated_at: new Date().toISOString()
   364→                }
   365→              });
   366→              console.log(`[SPLICE] Stored license key in Stripe metadata for subscription ${subscription.id}`);
   367→            } catch (stripeErr) {
   368→              console.error(`[SPLICE] Failed to store license key in Stripe metadata:`, stripeErr.message);
   369→            }
   370→
   371→            // Get customer email and send license key
   372→            try {
   373→              const customer = await stripe.customers.retrieve(customerId);
   374→              if (customer.email) {
   375→                // SECURITY: Mask sensitive data in logs
   376→                console.log(`[SPLICE] License key ready for delivery to ${maskSensitiveData(customer.email)}: ${maskSensitiveData(licenseResult.key)}`);
   377→
   378→                // Send license key email
   379→                try {
   380→                  await emailService.sendLicenseKeyEmail(customer.email, licenseResult.key, tier);
   381→                  console.log(`[SPLICE] License key email sent to ${maskSensitiveData(customer.email)}`);
   382→                } catch (sendErr) {
   383→                  // Log but don't fail - license is stored in Stripe metadata as backup
   384→                  console.error(`[SPLICE] Failed to send license email:`, sendErr.message);
   385→                }
   386→
   387→                // Store email in database for reference
   388→                await usageTracking.updateTier(customerId, tier, customer.email);
   389→              } else {
   390→                console.warn(`[SPLICE] No email found for customer ${customerId}`);
   391→              }
   392→            } catch (emailErr) {
   393→              console.error(`[SPLICE] Error getting customer email:`, emailErr.message);
   394→            }
   395→          } else {
   396→            // CRITICAL: License generation failed - return 500 to trigger Stripe retry
   397→            const errorMsg = `Failed to generate license key after ${maxRetries} attempts: ${licenseResult?.error || 'Unknown error'}`;
   398→            console.error(`[SPLICE] ${errorMsg}`);
   399→            return res.status(500).json({ error: errorMsg });
   400→          }
   401→        }
   402→        break;
   403→      }
   404→
   405→      case 'customer.subscription.deleted': {
   406→        const subscription = event.data.object;
   407→        const customerId = subscription.customer;
   408→
   409→        // Validate customerId
   410→        if (!customerId) {
   411→          console.error('[SPLICE] Missing customer ID in subscription.deleted event');
   412→          return res.status(400).json({ error: 'Missing customer ID' });
   413→        }
   414→
   415→        // Downgrade to cancelled (0 hours)
   416→        await usageTracking.updateTier(customerId, 'cancelled');
   417→        console.log(`[SPLICE] Subscription cancelled for customer ${customerId}`);
   418→        break;
   419→      }
   420→
   421→      case 'invoice.payment_succeeded': {
   422→        const invoice = event.data.object;
   423→        const customerId = invoice.customer;
   424→        const subscriptionId = invoice.subscription;
   425→
   426→        // Validate customerId
   427→        if (!customerId) {
   428→          console.error('[SPLICE] Missing customer ID in invoice event');
   429→          return res.status(400).json({ error: 'Missing customer ID' });
   430→        }
   431→
   432→        // Reset hours on successful payment (new billing period)
   433→        let tier = 'starter';
   434→        if (subscriptionId) {
   435→          const subscription = await stripe.subscriptions.retrieve(subscriptionId);
   436→          const priceId = subscription.items?.data?.[0]?.price?.id;
   437→          tier = getTierFromPriceId(priceId);
   438→
   439→          await usageTracking.resetHours(customerId, tier);
   440→          console.log(`[SPLICE] Reset hours for customer ${customerId} (tier: ${tier})`);
   441→        }
   442→
   443→        // Check for affiliate coupon and record commission
   444→        const discount = invoice.discount;
   445→        if (discount && discount.coupon) {
   446→          const couponId = discount.coupon.id;
   447→          // Check if this is an affiliate code (like JIMMYN)
   448→          if (referralService.AFFILIATE_CODES[couponId]) {
   449→            const amountPaid = invoice.amount_paid / 100; // Convert cents to dollars
   450→            await referralService.recordAffiliateCommission(
   451→              couponId,
   452→              customerId,
   453→              amountPaid,
   454→              tier
   455→            );
   456→            console.log(`[SPLICE] Recorded affiliate commission for ${couponId}`);
   457→          }
   458→        }
   459→        break;
   460→      }
   461→
   462→      case 'invoice.payment_failed': {
   463→        const invoice = event.data.object;
   464→        const customerId = invoice.customer;
   465→        const attemptCount = invoice.attempt_count || 1;
   466→
   467→        // Validate customerId
   468→        if (!customerId) {
   469→          console.error('[SPLICE] Missing customer ID in payment_failed event');
   470→          return res.status(400).json({ error: 'Missing customer ID' });
   471→        }
   472→
   473→        console.warn(`[SPLICE] Payment failed for customer ${customerId} (attempt ${attemptCount})`);
   474→
   475→        // Stripe will retry automatically per retry settings
   476→        // On final failure, Stripe will cancel subscription which triggers customer.subscription.deleted
   477→        // Send warning email on final attempt
   478→        if (attemptCount >= 3) {
   479→          console.error(`[SPLICE] Final payment attempt failed for customer ${customerId}`);
   480→          // Send warning email to customer about impending cancellation
   481→          try {
   482→            const customer = await stripe.customers.retrieve(customerId);
   483→            if (customer.email) {
   484→              await emailService.sendPaymentWarningEmail(customer.email, attemptCount);
   485→              console.log(`[SPLICE] Payment warning email sent to ${maskSensitiveData(customer.email)}`);
   486→            }
   487→          } catch (emailErr) {
   488→            console.error(`[SPLICE] Failed to send payment warning email:`, emailErr.message);
   489→          }
   490→        }
   491→        break;
   492→      }
   493→
   494→      case 'customer.deleted': {
   495→        const customer = event.data.object;
   496→        const customerId = customer.id;
   497→
   498→        // Validate customerId
   499→        if (!customerId) {
   500→          console.error('[SPLICE] Missing customer ID in customer.deleted event');
   501→          return res.status(400).json({ error: 'Missing customer ID' });
   502→        }
   503→
   504→        // Clean up user data - downgrade to cancelled
   505→        await usageTracking.updateTier(customerId, 'cancelled');
   506→        console.log(`[SPLICE] Customer deleted: ${customerId}`);
   507→        break;
   508→      }
   509→
   510→      default:
   511→        console.log(`[SPLICE] Unhandled event type: ${event.type}`);
   512→    }
   513→
   514→    // Record event as processed (idempotency)
   515→    await usageTracking.recordWebhookEvent(event.id, event.type);
   516→
   517→    res.json({ received: true });
   518→  } catch (err) {
   519→    console.error('[SPLICE] Webhook handler error:', err);
   520→    res.status(500).json({ error: err.message });
   521→  }
   522→});
