   574→
   575→// Legacy XML processing endpoint (backwards compatibility)
   576→const { processXMLFile } = require('./services/xmlProcessor');
   577→app.post('/process-xml', requireCredits({ endpoint: 'process-xml' }), async (req, res) => {
   578→  const {
   579→    xmlPath,
   580→    silences,
   581→    removeGaps = true,
   582→    outputPath = null
   583→  } = req.body;
   584→
   585→  if (!xmlPath) {
   586→    return res.status(400).json({ error: 'xmlPath is required' });
   587→  }
   588→
   589→  if (!silences || !Array.isArray(silences)) {
   590→    return res.status(400).json({ error: 'silences array is required' });
   591→  }
   592→
   593→  if (!fs.existsSync(xmlPath)) {
   594→    return res.status(404).json({ error: `XML file not found: ${xmlPath}` });
   595→  }
   596→
   597→  console.log(`[SPLICE] Processing XML: ${xmlPath} with ${silences.length} silence(s)`);
   598→
   599→  try {
   600→    const result = await processXMLFile(xmlPath, silences, {
   601→      outputPath,
   602→      removeGaps
   603→    });
   604→
   605→    res.json({
   606→      success: true,
   607→      inputPath: xmlPath,
   608→      outputPath: result.outputPath,
   609→      stats: result.stats
   610→    });
   611→  } catch (err) {
   612→    console.error('[SPLICE] XML processing error:', err);
   613→    res.status(500).json({ error: err.message });
   614→  }
   615→});
   616→
   617→// Auto zoom endpoints
   618→app.use('/zoom', zoomRoutes(routeOptions));
   619→
   620→// Chapter detection endpoints
   621→app.use('/chapters', chaptersRoutes(routeOptions));
   622→
   623→// YouTube content generation
   624→app.use('/youtube', youtubeRoutes(routeOptions));
   625→
   626→// Animated captions endpoints
   627→app.use('/captions', captionsRoutes(routeOptions));
   628→
   629→// Text-based editing endpoints
   630→app.use('/text-edit', textEditRoutes(routeOptions));
   631→
   632→// Social reframe endpoints
   633→app.use('/reframe', reframeRoutes(routeOptions));
   634→app.use('/faces', reframeRoutes.facesRouter(routeOptions));
   635→
   636→// Batch processing endpoints
   637→app.use('/batch', batchRoutes(routeOptions));
   638→
   639→// Authentication endpoints
   640→app.use('/auth', authRoutes(routeOptions));
   641→
   642→// Billing and credits endpoints
   643→app.use('/', billingRoutes(routeOptions));
   644→
   645→// Referral system endpoints
   646→app.use('/referral', referralRoutes(routeOptions));
   647→
   648→// License key endpoints
   649→app.use('/license', licenseRoutes(routeOptions));
   650→
   651→// AI Music generation endpoints
   652→app.use('/music', musicRoutes(routeOptions));
   653→
   654→// =============================================================================
   655→// Start Server
   656→// =============================================================================
   657→
   658→// Track active server for graceful shutdown
   659→let server = null;
   660→
   661→// Initialize database and start server
   662→async function startServer() {
   663→  try {
   664→    await usageTracking.initDatabase();
   665→    await referralService.initReferralTables();
   666→    await licenseService.initLicenseTables();
   667→    console.log('[SPLICE] Database initialized');
   668→
   669→    // PERF-FIX: Initialize static response cache at startup
   670→    initializeStaticCache();
   671→
   672→    if (isProduction || !httpsOptions) {
   673→      // Production: Railway provides TLS termination, use HTTP
   674→      server = http.createServer(app).listen(PORT, () => {
   675→        console.log(`[SPLICE] Backend running at http://0.0.0.0:${PORT} (production)`);
   676→      });
   677→    } else {
   678→      // Development: Use HTTPS with local certificates
   679→      server = https.createServer(httpsOptions, app).listen(PORT, () => {
   680→        console.log(`[SPLICE] Backend running at https://127.0.0.1:${PORT} (development)`);
   681→        console.log(`[SPLICE] POST /analyze with { "wavPath": "/path/to/audio.wav" }`);
   682→      });
   683→    }
   684→  } catch (err) {
   685→    console.error('[SPLICE] Failed to start server:', err);
   686→    process.exit(1);
   687→  }
   688→}
   689→
   690→// =============================================================================
   691→// Graceful Shutdown
   692→// =============================================================================
   693→
   694→async function gracefulShutdown(signal) {
   695→  console.log(`[SPLICE] Received ${signal}, starting graceful shutdown...`);
   696→
   697→  // Stop accepting new connections
   698→  if (server) {
   699→    server.close(() => {
   700→      console.log('[SPLICE] HTTP server closed');
   701→    });
   702→  }
   703→
   704→  // Close database pool
   705→  try {
   706→    await usageTracking.closePool();
   707→    console.log('[SPLICE] Database pool closed');
   708→  } catch (err) {
   709→    console.error('[SPLICE] Error closing database pool:', err.message);
   710→  }
   711→
   712→  // Give ongoing requests time to complete (max 10 seconds)
   713→  setTimeout(() => {
   714→    console.log('[SPLICE] Graceful shutdown complete');
   715→    process.exit(0);
   716→  }, 10000);
   717→}
   718→
   719→// Handle termination signals
   720→process.on('SIGTERM', () => gracefulShutdown('SIGTERM'));
   721→process.on('SIGINT', () => gracefulShutdown('SIGINT'));
   722→
   723→// Handle uncaught exceptions
   724→process.on('uncaughtException', (err) => {
   725→  console.error('[SPLICE] Uncaught exception:', err);
   726→  gracefulShutdown('uncaughtException');
   727→});
   728→
   729→process.on('unhandledRejection', (reason, promise) => {
   730→  console.error('[SPLICE] Unhandled rejection at:', promise, 'reason:', reason);
   731→});
   732→
   733→startServer();
