/**
 * Billing Routes
 *
 * Credits and usage history endpoints
 */

const express = require('express');

/**
 * Create billing routes
 * @param {Object} options - Route configuration options
 * @param {Object} options.services - Shared services (usageTracking)
 * @param {Object} options.authHelpers - Auth helper functions (verifyToken)
 * @returns {express.Router}
 */
function createBillingRoutes(options = {}) {
  const router = express.Router();
  const { usageTracking } = options.services || {};
  const { verifyToken } = options.authHelpers || {};

  /**
   * GET /credits - Get user's credit balance
   *
   * Supports both JWT (Authorization: Bearer) and legacy x-stripe-customer-id header
   */
  router.get('/credits', async (req, res) => {
    // Support both JWT and legacy auth
    let stripeCustomerId = null;

    // Try JWT first
    const authHeader = req.headers['authorization'];
    if (authHeader && authHeader.startsWith('Bearer ') && verifyToken) {
      const token = authHeader.slice(7);
      const decoded = verifyToken(token);
      if (decoded && decoded.sub) {
        stripeCustomerId = decoded.sub;
      }
    }

    // Fallback to legacy header
    if (!stripeCustomerId) {
      stripeCustomerId = req.headers['x-stripe-customer-id'];
    }

    if (!stripeCustomerId) {
      return res.status(401).json({
        error: 'Authentication required',
        message: 'Please provide a valid Bearer token or log in again'
      });
    }

    try {
      const balance = await usageTracking.getBalance(stripeCustomerId);
      res.json({
        success: true,
        ...balance
      });
    } catch (err) {
      console.error('[SPLICE] Credits error:', err);
      res.status(500).json({ error: err.message });
    }
  });

  /**
   * GET /usage-history - Get user's usage history
   */
  router.get('/usage-history', async (req, res) => {
    // Support both JWT and legacy auth
    let stripeCustomerId = null;

    const authHeader = req.headers['authorization'];
    if (authHeader && authHeader.startsWith('Bearer ') && verifyToken) {
      const token = authHeader.slice(7);
      const decoded = verifyToken(token);
      if (decoded && decoded.sub) {
        stripeCustomerId = decoded.sub;
      }
    }

    if (!stripeCustomerId) {
      stripeCustomerId = req.headers['x-stripe-customer-id'];
    }

    if (!stripeCustomerId) {
      return res.status(401).json({
        error: 'Authentication required',
        message: 'Please provide a valid Bearer token or log in again'
      });
    }

    try {
      const history = await usageTracking.getUsageHistory(stripeCustomerId);
      res.json({
        success: true,
        history
      });
    } catch (err) {
      console.error('[SPLICE] Usage history error:', err);
      res.status(500).json({ error: err.message });
    }
  });

  return router;
}

module.exports = createBillingRoutes;

