/**
 * YouTube Routes
 *
 * YouTube content generation endpoints
 */

const express = require('express');

/**
 * Create YouTube routes
 * @param {Object} options - Route configuration options
 * @param {Object} options.middleware - Shared middleware (requireCredits)
 * @returns {express.Router}
 */
function createYouTubeRoutes(options = {}) {
  const router = express.Router();
  const { requireCredits } = options.middleware || {};

  /**
   * POST /description - Generate YouTube video description
   *
   * Creates SEO-optimized description with summary, key points,
   * timestamps, and hashtags from transcript.
   */
  router.post('/description', requireCredits({ endpoint: 'youtube' }), async (req, res) => {
    const { transcript, options: genOptions = {} } = req.body;

    if (!transcript) {
      return res.status(400).json({ error: 'transcript is required' });
    }

    try {
      const { generateDescription } = require('../services/youtubeGenerator');
      const result = await generateDescription(transcript, genOptions);

      if (!result.success) {
        return res.status(400).json({ error: result.error });
      }

      // Deduct usage based on description length
      if (req.deductUsage) {
        await req.deductUsage(0.5); // 0.5 credits per description
      }

      res.json(result);
    } catch (err) {
      console.error('[SPLICE] YouTube description error:', err);
      res.status(500).json({ error: err.message });
    }
  });

  /**
   * POST /title - Generate YouTube video titles
   *
   * Creates multiple title options with different styles.
   */
  router.post('/title', requireCredits({ endpoint: 'youtube' }), async (req, res) => {
    const { transcript, options: genOptions = {} } = req.body;

    if (!transcript) {
      return res.status(400).json({ error: 'transcript is required' });
    }

    try {
      const { generateTitle } = require('../services/youtubeGenerator');
      const result = await generateTitle(transcript, genOptions);

      if (!result.success) {
        return res.status(400).json({ error: result.error });
      }

      // Deduct usage
      if (req.deductUsage) {
        await req.deductUsage(0.25); // 0.25 credits per title generation
      }

      res.json(result);
    } catch (err) {
      console.error('[SPLICE] YouTube title error:', err);
      res.status(500).json({ error: err.message });
    }
  });

  /**
   * POST /shorts - Generate Shorts/TikTok metadata
   *
   * Creates title, caption, and hashtags for short-form content.
   */
  router.post('/shorts', requireCredits({ endpoint: 'youtube' }), async (req, res) => {
    const { transcript, options: genOptions = {} } = req.body;

    if (!transcript) {
      return res.status(400).json({ error: 'transcript is required' });
    }

    try {
      const { generateShortsMetadata } = require('../services/youtubeGenerator');
      const result = await generateShortsMetadata(transcript, genOptions);

      if (!result.success) {
        return res.status(400).json({ error: result.error });
      }

      // Deduct usage
      if (req.deductUsage) {
        await req.deductUsage(0.25); // 0.25 credits per shorts metadata
      }

      res.json(result);
    } catch (err) {
      console.error('[SPLICE] YouTube shorts error:', err);
      res.status(500).json({ error: err.message });
    }
  });

  /**
   * GET /options - Get available generation options
   */
  router.get('/options', (req, res) => {
    const { getGeneratorOptions } = require('../services/youtubeGenerator');
    res.json(getGeneratorOptions());
  });

  return router;
}

module.exports = createYouTubeRoutes;

