"use client";

import { motion } from "framer-motion";

const stats = [
  { value: "10,000+", label: "Editors" },
  { value: "500K+", label: "Hours Saved" },
  { value: "4.9/5", label: "Rating" },
];

const logos = [
  { name: "YouTube", className: "text-red-500" },
  { name: "TikTok", className: "text-white" },
  { name: "Netflix", className: "text-red-600" },
  { name: "Spotify", className: "text-green-500" },
  { name: "Adobe", className: "text-red-500" },
];

export function SocialProof() {
  return (
    <section className="py-12 border-y border-gray-800 bg-[#0a0f1a] dark">
      <div className="container mx-auto px-4">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5 }}
          className="text-center mb-8"
        >
          <p className="text-sm text-gray-400">
            Trusted by video editors at
          </p>
        </motion.div>

        {/* Logo strip */}
        <motion.div
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.2 }}
          className="flex flex-wrap items-center justify-center gap-8 md:gap-12 mb-10"
        >
          {logos.map((logo) => (
            <div
              key={logo.name}
              className={`text-xl font-bold opacity-50 hover:opacity-100 transition-opacity ${logo.className}`}
            >
              {logo.name}
            </div>
          ))}
        </motion.div>

        {/* Stats */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.3 }}
          className="grid grid-cols-3 gap-8 max-w-2xl mx-auto"
        >
          {stats.map((stat) => (
            <div key={stat.label} className="text-center">
              <div className="text-2xl md:text-3xl font-bold text-[#00D4FF]">
                {stat.value}
              </div>
              <div className="text-sm text-gray-400 mt-1">
                {stat.label}
              </div>
            </div>
          ))}
        </motion.div>
      </div>
    </section>
  );
}

"use client";

import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { motion } from "framer-motion";
import { Star, Quote, CheckCircle, Youtube, Mic2, Film, Twitter } from "lucide-react";

const testimonials = [
  {
    name: "Alex Rivera",
    role: "YouTube Creator",
    handle: "@alexrivera",
    platform: "youtube",
    subscribers: "2.1M subscribers",
    avatar: "AR",
    avatarGradient: "from-red-500 to-orange-500",
    content:
      "SPLICE cut my editing time from 8 hours to under 2 hours. The AI take detection alone is worth the subscription. I can finally focus on creating content instead of editing.",
    rating: 5,
    metric: "75% time saved",
    verified: true,
    date: "2 weeks ago",
  },
  {
    name: "Sarah Chen",
    role: "Podcast Editor",
    handle: "@sarahchen",
    platform: "podcast",
    subscribers: "TechTalks Network",
    avatar: "SC",
    avatarGradient: "from-purple-500 to-pink-500",
    content:
      "The silence removal is incredibly accurate. I process 10+ episodes per week and SPLICE handles the repetitive work perfectly. The multitrack auto-balance feature is a game-changer.",
    rating: 5,
    metric: "10+ episodes/week",
    verified: true,
    date: "1 week ago",
  },
  {
    name: "Marcus Johnson",
    role: "Freelance Editor",
    handle: "@marcuscuts",
    platform: "film",
    subscribers: "Netflix • HBO • Apple TV+",
    avatar: "MJ",
    avatarGradient: "from-blue-500 to-cyan-500",
    content:
      "Switched from FireCut after trying the AI music generation. Being able to create custom soundtracks that match my video's mood is incredible. No more stock music licensing headaches.",
    rating: 5,
    metric: "Switched from FireCut",
    verified: true,
    date: "3 days ago",
  },
];

const platformIcons = {
  youtube: Youtube,
  podcast: Mic2,
  film: Film,
  twitter: Twitter,
};

export function Testimonials() {
  return (
    <section className="py-24">
      <div className="container mx-auto px-4">
        {/* Section header */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5 }}
          className="text-center mb-12"
        >
          {/* Rating summary */}
          <div className="flex items-center justify-center gap-2 mb-4">
            <div className="flex items-center gap-0.5">
              {[1, 2, 3, 4, 5].map((i) => (
                <Star key={i} className="w-5 h-5 fill-yellow-500 text-yellow-500" />
              ))}
            </div>
            <span className="text-sm font-medium">4.9/5 from 500+ reviews</span>
          </div>

          <h2 className="text-3xl md:text-4xl font-bold mb-4">
            Loved by{" "}
            <span className="gradient-text">10,000+ Editors</span>
          </h2>
          <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
            Join thousands of video creators who have transformed their editing workflow.
          </p>
        </motion.div>

        {/* Testimonials grid */}
        <div className="grid md:grid-cols-3 gap-6">
          {testimonials.map((testimonial, index) => (
            <motion.div
              key={testimonial.name}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
            >
              <Card className="h-full bg-card/50 backdrop-blur border-border/50 hover:border-primary/30 transition-colors group">
                <CardContent className="p-6">
                  {/* Header with platform icon */}
                  <div className="flex items-start justify-between mb-4">
                    <Quote className="w-8 h-8 text-primary/30" />
                    {(() => {
                      const PlatformIcon = platformIcons[testimonial.platform as keyof typeof platformIcons];
                      return PlatformIcon ? (
                        <PlatformIcon className="w-5 h-5 text-muted-foreground/50" />
                      ) : null;
                    })()}
                  </div>

                  {/* Content */}
                  <p className="text-muted-foreground mb-6 leading-relaxed">
                    &ldquo;{testimonial.content}&rdquo;
                  </p>

                  {/* Rating with date */}
                  <div className="flex items-center justify-between mb-4">
                    <div className="flex items-center gap-1">
                      {[...Array(testimonial.rating)].map((_, i) => (
                        <Star
                          key={i}
                          className="w-4 h-4 fill-yellow-500 text-yellow-500"
                        />
                      ))}
                    </div>
                    <span className="text-xs text-muted-foreground">{testimonial.date}</span>
                  </div>

                  {/* Author */}
                  <div className="flex items-center gap-3">
                    <div className={`w-12 h-12 rounded-full bg-gradient-to-br ${testimonial.avatarGradient} flex items-center justify-center text-white font-bold text-sm ring-2 ring-background`}>
                      {testimonial.avatar}
                    </div>
                    <div className="flex-1">
                      <div className="flex items-center gap-1.5">
                        <span className="font-semibold">{testimonial.name}</span>
                        {testimonial.verified && (
                          <CheckCircle className="w-4 h-4 text-[#00D4FF] fill-[#00D4FF]/20" />
                        )}
                      </div>
                      <div className="text-sm text-muted-foreground">
                        {testimonial.role}
                      </div>
                      <div className="text-xs text-muted-foreground/70">
                        {testimonial.subscribers}
                      </div>
                    </div>
                  </div>

                  {/* Metric badge */}
                  <div className="mt-4 flex items-center gap-2">
                    <Badge variant="secondary" className="bg-green-500/10 text-green-600 dark:text-green-400 hover:bg-green-500/20">
                      {testimonial.metric}
                    </Badge>
                    <span className="text-xs text-muted-foreground">{testimonial.handle}</span>
                  </div>
                </CardContent>
              </Card>
            </motion.div>
          ))}
        </div>

        {/* Aggregate stats */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.3 }}
          className="mt-16 grid grid-cols-2 md:grid-cols-4 gap-8 text-center"
        >
          {[
            { value: "10,000+", label: "Video editors" },
            { value: "2.5M+", label: "Hours saved" },
            { value: "500K+", label: "Videos edited" },
            { value: "4.9★", label: "Average rating" },
          ].map((stat) => (
            <div key={stat.label}>
              <div className="text-3xl md:text-4xl font-bold gradient-text mb-1">
                {stat.value}
              </div>
              <div className="text-sm text-muted-foreground">{stat.label}</div>
            </div>
          ))}
        </motion.div>
      </div>
    </section>
  );
}

