import { NextRequest, NextResponse } from "next/server";
import Stripe from "stripe";

// Force Node.js runtime (not Edge) for better Stripe compatibility
export const runtime = "nodejs";
export const maxDuration = 30;

// Lazy initialization to avoid errors during build
let stripe: Stripe | null = null;
function getStripe(): Stripe {
  if (!stripe) {
    if (!process.env.STRIPE_SECRET_KEY) {
      throw new Error("STRIPE_SECRET_KEY is not configured");
    }
    stripe = new Stripe(process.env.STRIPE_SECRET_KEY, {
      timeout: 10000,
      maxNetworkRetries: 2,
    });
  }
  return stripe;
}

// Get price IDs at runtime to ensure env vars are loaded
function getPriceIds() {
  return {
    monthly: {
      starter: process.env.STRIPE_PRICE_STARTER || "",
      pro: process.env.STRIPE_PRICE_PRO || "",
      team: process.env.STRIPE_PRICE_TEAM || "",
    },
    annual: {
      starter: process.env.STRIPE_PRICE_STARTER_ANNUAL || "",
      pro: process.env.STRIPE_PRICE_PRO_ANNUAL || "",
      team: process.env.STRIPE_PRICE_TEAM_ANNUAL || "",
    },
  };
}

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const { plan, email, successUrl, cancelUrl, billing = "monthly" } = body;

    // Get price IDs at runtime
    const allPrices = getPriceIds();
    const priceIds = billing === "annual" ? allPrices.annual : allPrices.monthly;

    if (!plan || !priceIds[plan as keyof typeof priceIds]) {
      return NextResponse.json(
        { error: "Invalid plan selected" },
        { status: 400 }
      );
    }

    const priceId = priceIds[plan as keyof typeof priceIds];

    if (!priceId) {
      return NextResponse.json(
        { error: "Price ID not configured for this plan" },
        { status: 500 }
      );
    }

    // Create Stripe checkout session using raw fetch (workaround for Vercel SDK connectivity issues)
    const baseUrl = process.env.NEXT_PUBLIC_BASE_URL || "https://splice-website.vercel.app";
    const successUrlFinal = successUrl || `${baseUrl}/checkout/success`;
    const cancelUrlFinal = cancelUrl || `${baseUrl}/pricing`;

    const params = new URLSearchParams();
    params.append("mode", "subscription");
    params.append("payment_method_types[0]", "card");
    params.append("line_items[0][price]", priceId);
    params.append("line_items[0][quantity]", "1");
    if (email) params.append("customer_email", email);
    params.append("success_url", successUrlFinal);
    params.append("cancel_url", cancelUrlFinal);
    params.append("subscription_data[trial_period_days]", "14");
    params.append("subscription_data[metadata][plan]", plan);
    params.append("subscription_data[metadata][billing]", billing);
    params.append("metadata[plan]", plan);
    params.append("metadata[billing]", billing);
    params.append("allow_promotion_codes", "true");
    params.append("billing_address_collection", "required");

    const response = await fetch("https://api.stripe.com/v1/checkout/sessions", {
      method: "POST",
      headers: {
        "Authorization": `Bearer ${process.env.STRIPE_SECRET_KEY}`,
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: params,
    });

    if (!response.ok) {
      const errorData = await response.json();
      console.error("[Checkout] Stripe API error:", errorData);
      throw new Error(errorData.error?.message || "Stripe API error");
    }

    const session = await response.json();

    return NextResponse.json({
      sessionId: session.id,
      url: session.url,
    });
  } catch (error) {
    console.error("[Checkout] Error:", error instanceof Error ? error.message : error);
    return NextResponse.json(
      {
        error:
          error instanceof Error ? error.message : "Failed to create checkout session",
      },
      { status: 500 }
    );
  }
}

export async function GET(request: NextRequest) {
  const searchParams = request.nextUrl.searchParams;
  const sessionId = searchParams.get("session_id");

  if (!sessionId) {
    return NextResponse.json(
      { error: "Session ID is required" },
      { status: 400 }
    );
  }

  try {
    const session = await getStripe().checkout.sessions.retrieve(sessionId, {
      expand: ["subscription", "customer"],
    });

    return NextResponse.json({
      status: session.status,
      customerEmail: session.customer_details?.email,
      plan: session.metadata?.plan,
      subscriptionId:
        typeof session.subscription === "string"
          ? session.subscription
          : session.subscription?.id,
    });
  } catch (error) {
    console.error("Session retrieval error:", error);
    return NextResponse.json(
      {
        error:
          error instanceof Error
            ? error.message
            : "Failed to retrieve session",
      },
      { status: 500 }
    );
  }
}

