                  {/* Stats badge */}
                  <div className="inline-flex items-center px-3 py-1 rounded-full bg-[#00D4FF]/10 text-[#00D4FF] text-sm font-medium">
                    {feature.stats}
                  </div>
                </CardContent>
              </Card>
            </motion.div>
          ))}
        </motion.div>

        {/* Before/After comparison */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.3 }}
          className="mt-20"
        >
          <div className="bg-[#0d1420] rounded-2xl border border-gray-800 p-8 md:p-12">
            <div className="grid md:grid-cols-2 gap-8 items-center">
              {/* Before */}
