"use client";

import { motion } from "framer-motion";
import { Check, X, Zap, Music, Scissors, MessageSquare, Video, Cpu } from "lucide-react";
import { Badge } from "@/components/ui/badge";

const keyFeatures = [
  {
    name: "Silence Removal (UXP)",
    icon: Scissors,
    splice: true,
    firecut: true,
    autopod: true,
    descript: true,
  },
  {
    name: "AI Music Scoring",
    icon: Music,
    splice: true,
    firecut: false,
    autopod: false,
    descript: false,
  },
  {
    name: "Dynamic Captions",
    icon: MessageSquare,
    splice: true,
    firecut: false,
    autopod: false,
    descript: true,
  },
  {
    name: "Text-Based Trimming",
    icon: Cpu,
    splice: true,
    firecut: false,
    autopod: false,
    descript: true,
  },
  {
    name: "Vocal Isolation",
    icon: Video,
    splice: true,
    firecut: false,
    autopod: false,
    descript: false,
  },
  {
    name: "Native UXP Architecture",
    icon: Zap,
    splice: true,
    firecut: false,
    autopod: false,
    descript: false,
  },
];

const competitors = [
  { key: "splice", name: "SPLICE", price: "$15/mo", highlight: true },
  { key: "firecut", name: "FireCut", price: "$14/mo", highlight: false },
  { key: "autopod", name: "AutoPod", price: "$29/yr", highlight: false },
  { key: "descript", name: "Descript", price: "$24/mo", highlight: false },
];

function FeatureCheck({ value }: { value: boolean }) {
  return value ? (
    <div className="w-6 h-6 rounded-full bg-[#00D4FF]/20 flex items-center justify-center">
      <Check className="w-4 h-4 text-[#00D4FF]" />
    </div>
  ) : (
    <div className="w-6 h-6 rounded-full bg-gray-800 flex items-center justify-center">
      <X className="w-4 h-4 text-gray-500" />
    </div>
  );
}

export function Comparison() {
  return (
    <section id="comparison" className="py-24 bg-[#0a0f1a] dark">
      <div className="container mx-auto px-4">
        {/* Section header */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5 }}
          className="text-center mb-16"
        >
          <Badge variant="outline" className="mb-4 px-3 py-1 text-sm border-[#00D4FF]/30 bg-[#00D4FF]/10 text-[#00D4FF]">
            Architecture Comparison
          </Badge>
          <h2 className="text-3xl md:text-4xl font-bold mb-4 text-white">
            Built for{" "}
            <span className="gradient-text">Professional Workflows</span>
          </h2>
          <p className="text-lg text-gray-400 max-w-2xl mx-auto">
            The only AI suite built directly into Premiere Pro&apos;s UXP architecture
            for maximum stability, performance, and future-proof compatibility.
          </p>
        </motion.div>

        {/* Simple comparison table */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.2 }}
          className="max-w-4xl mx-auto"
        >
          <div className="overflow-x-auto rounded-xl border border-gray-800 bg-[#0d1420]">
            <table className="w-full">
              {/* Header */}
              <thead>
                <tr className="border-b border-gray-800">
                  <th className="text-left p-4 font-medium text-gray-400">Feature</th>
                  {competitors.map((comp) => (
                    <th key={comp.key} className={`p-4 text-center ${comp.highlight ? 'bg-[#00D4FF]/5' : ''}`}>
                      <div className={`font-bold ${comp.highlight ? 'text-[#00D4FF] text-lg' : 'text-white text-sm'}`}>
                        {comp.name}
                      </div>
                      <div className="text-xs text-gray-500 mt-0.5">{comp.price}</div>
                    </th>
                  ))}
                </tr>
              </thead>

              {/* Body */}
              <tbody>
                {keyFeatures.map((feature, index) => (
                  <tr
                    key={feature.name}
                    className={`border-b border-gray-800/50 ${index % 2 === 0 ? '' : 'bg-[#0a0f1a]/50'
                      }`}
                  >
                    <td className="p-4">
                      <div className="flex items-center gap-3">
                        <feature.icon className="w-5 h-5 text-gray-500" />
                        <span className="font-medium text-white">{feature.name}</span>
                      </div>
                    </td>
                    <td className="p-4 bg-[#00D4FF]/5">
                      <div className="flex justify-center">
                        <FeatureCheck value={feature.splice} />
                      </div>
                    </td>
                    <td className="p-4">
                      <div className="flex justify-center">
                        <FeatureCheck value={feature.firecut} />
                      </div>
                    </td>
                    <td className="p-4">
                      <div className="flex justify-center">
                        <FeatureCheck value={feature.autopod} />
                      </div>
                    </td>
                    <td className="p-4">
                      <div className="flex justify-center">
                        <FeatureCheck value={feature.descript} />
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </motion.div>

        {/* Unique selling points */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.4 }}
          className="mt-12 grid md:grid-cols-3 gap-6 max-w-4xl mx-auto"
        >
          {[
            {
              title: "Native Integration",
              description: "No external apps or XML round-tripping. Everything happens directly inside Premiere Pro.",
              icon: Zap,
            },
            {
              title: "Future-Proof Architecture",
              description: "Built on Adobe's modern UXP platform, ensuring compatibility with all future Premiere updates.",
              icon: Cpu,
