  1029→    }
  1030→
  1031→    // Remove all audio clips
  1032→    for (var t = 0; t < seq.audioTracks.numTracks; t++) {
  1033→        var clips = seq.audioTracks[t].clips;
  1034→        for (var i = clips.numItems - 1; i >= 0; i--) {
  1035→            clips[i].remove(false, false);
  1036→        }
  1037→    }
  1038→
  1039→    return true;
  1040→}
