   472→                console.log('[SPLICE] Audio exported to:', audioFilePath);
   473→            } else if (exportResult && exportResult.error) {
   474→                throw new Error(exportResult.error);
   475→            }
   476→        } catch (exportError) {
   477→            console.warn('[SPLICE] Audio export failed, trying fallback:', exportError.message);
   478→            // Try to get audio from first clip in timeline
   479→            try {
   480→                const clipInfo = await jsx.call('getFirstClipAudioPath');
   481→                if (clipInfo && clipInfo.path) {
   482→                    audioFilePath = clipInfo.path;
   483→                    console.log('[SPLICE] Using clip audio path:', audioFilePath);
   484→                }
   485→            } catch (clipError) {
   486→                console.warn('[SPLICE] Fallback audio path failed:', clipError.message);
   487→            }
   488→        }
   489→
   490→        if (!audioFilePath) {
   491→            throw new Error('Could not export or locate audio file. Please ensure sequence has audio tracks.');
   492→        }
   493→
   494→        // Call backend for silence detection with audio file path
   495→        updateProgress(30, 'Detecting silences...');
   496→        const silenceResponse = await fetchWithTimeout(
   497→            `${getBackendUrl()}/silences-rms`,
   498→            {
   499→                method: 'POST',
   500→                headers: getAuthHeaders(),
   501→                body: JSON.stringify({
   502→                    wavPath: audioFilePath,
   503→                    sensitivity: settings.sensitivity,
   504→                    minSilenceLength: settings.minSilenceLength || 0.5
   505→                })
   506→            }
   507→        );
   508→
   509→        if (!silenceResponse.ok) {
   510→            throw new Error(await parseErrorResponse(silenceResponse));
   511→        }
   512→
   513→        const silenceData = await silenceResponse.json();
   514→        previewSilences = silenceData.silences || [];
   515→
   516→        // Detect takes if enabled
   517→        if (settings.enableTakesDetection) {
   518→            updateProgress(60, 'Detecting takes...');
   519→            const analyzeResponse = await fetchWithTimeout(
   520→                `${getBackendUrl()}/analyze`,
   521→                {
   522→                    method: 'POST',
   523→                    headers: getAuthHeaders(),
   524→                    body: JSON.stringify({
   525→                        detectTakes: true
   526→                    })
   527→                }
   528→            );
   529→
   530→            if (analyzeResponse.ok) {
   531→                const analyzeData = await analyzeResponse.json();
   532→                previewTakes = analyzeData.takes || [];
   533→            }
   534→        }
   535→
   536→        // Detect chapters if enabled
   537→        if (settings.enableChapters) {
   538→            updateProgress(80, 'Detecting chapters...');
   539→            try {
   540→                const chapterResponse = await fetchWithTimeout(
   541→                    `${getBackendUrl()}/chapters`,
   542→                    {
   543→                        method: 'POST',
   544→                        headers: getAuthHeaders(),
   545→                        body: JSON.stringify({
   546→                            maxChapters: settings.maxChapters || 10,
   547→                            minChapterLength: settings.minChapterLength || 60
   548→                        })
   549→                    }
   550→                );
   551→
   552→                if (chapterResponse.ok) {
   553→                    const chapterData = await chapterResponse.json();
   554→                    currentChapters = chapterData.chapters || [];
   555→                }
   556→            } catch (e) {
   557→                console.warn('[SPLICE] Chapter detection failed:', e);
   558→            }
   559→        }
   560→
   561→        // Detect zoom points if enabled
   562→        if (settings.enableZoom) {
   563→            updateProgress(90, 'Detecting zoom points...');
   564→            try {
   565→                const zoomResponse = await fetchWithTimeout(
   566→                    `${getBackendUrl()}/zoom`,
   567→                    {
   568→                        method: 'POST',
   569→                        headers: getAuthHeaders(),
   570→                        body: JSON.stringify({
   571→                            frequency: settings.zoomFrequency || 'medium',
   572→                            preset: settings.zoomPreset || 'medium',
   573→                            placement: settings.zoomPlacement || 'sentence_start'
   574→                        })
   575→                    }
   576→                );
   577→
   578→                if (zoomResponse.ok) {
   579→                    const zoomData = await zoomResponse.json();
   580→                    currentZooms = zoomData.zoomPoints || [];
   581→                }
   582→            } catch (e) {
   583→                console.warn('[SPLICE] Zoom detection failed:', e);
   584→            }
   585→        }
   586→
   587→        // Store results
   588→        lastDetectionResult = {
   589→            silences: previewSilences,
   590→            takes: previewTakes,
   591→            chapters: currentChapters,
   592→            zooms: currentZooms
   593→        };
   594→
   595→        // Show preview
   596→        hideProgress();
   597→        showCombinedPreview();
   598→        setStatus(`Found ${previewSilences.length} silences, ${previewTakes.length} takes`);
   599→
   600→    } catch (error) {
   601→        console.error('[SPLICE] Detection error:', error);
   602→        setStatus('Detection failed: ' + error.message, true);
   603→        hideProgress();
   604→    } finally {
   605→        isOperationInProgress = false;
   606→        ui.goBtn.disabled = false;
   607→    }
   608→}
