  1074→async function lookupLicense() {
  1075→    const lookupEmailInput = document.getElementById('lookupEmailInput');
  1076→    const licenseKeyInput = document.getElementById('licenseKeyInput');
  1077→
  1078→    if (!lookupEmailInput) return;
  1079→
  1080→    const email = lookupEmailInput.value.trim().toLowerCase();
  1081→    if (!email) {
  1082→        setStatus('Please enter your email address', true);
  1083→        return;
  1084→    }
  1085→
  1086→    // Basic email validation
  1087→    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1088→    if (!emailRegex.test(email)) {
  1089→        setStatus('Please enter a valid email address', true);
  1090→        return;
  1091→    }
  1092→
  1093→    const lookupBtn = document.getElementById('lookupLicenseBtn');
  1094→    if (lookupBtn) {
  1095→        lookupBtn.disabled = true;
  1096→        lookupBtn.textContent = 'Looking up...';
  1097→    }
  1098→
  1099→    try {
  1100→        const response = await fetchWithTimeout(
  1101→            `${getBackendUrl()}/license/lookup`,
  1102→            {
