   424→    generateRefreshToken,
   425→    verifyToken,
   426→    maskSensitiveData
   427→  },
   428→  staticCache: STATIC_RESPONSE_CACHE,
   429→  sendCachedResponse
   430→};
   431→
   432→// =============================================================================
   433→// Mount Routes
   434→// =============================================================================
   435→
   436→// Health check and system endpoints (no prefix)
   437→app.use('/', healthRoutes(routeOptions));
