     2→ * JWT Authentication Middleware
     3→ *
     4→ * Replaces insecure x-stripe-customer-id header with JWT tokens.
     5→ * Tokens are signed with a secret and contain the customer ID.
     6→ */
     7→
     8→const jwt = require('jsonwebtoken');
     9→
    10→// JWT configuration
    11→const JWT_SECRET = process.env.JWT_SECRET || 'splice-dev-secret-change-in-production';
    12→const JWT_EXPIRES_IN = process.env.JWT_EXPIRES_IN || '24h';
    13→const JWT_REFRESH_EXPIRES_IN = process.env.JWT_REFRESH_EXPIRES_IN || '7d';
    14→
    15→// Check if running in production
    16→const isProduction = process.env.NODE_ENV === 'production' || process.env.RAILWAY_ENVIRONMENT;
    17→
    18→// Warn if using default secret in production
    19→if (isProduction && JWT_SECRET === 'splice-dev-secret-change-in-production') {
    20→  console.error('[SECURITY] CRITICAL: JWT_SECRET not set in production! Set JWT_SECRET environment variable.');
    21→}
    22→
    23→/**
    24→ * Generate a JWT token for a customer
    25→ * @param {string} stripeCustomerId - Stripe customer ID (cus_xxx)
    26→ * @param {Object} options - Additional options
    27→ * @param {string} options.tier - User's subscription tier
    28→ * @param {string} options.email - User's email (optional)
    29→ * @returns {Object} { token, expiresIn }
    30→ */
    31→function generateToken(stripeCustomerId, options = {}) {
    32→  if (!stripeCustomerId || !stripeCustomerId.startsWith('cus_')) {
    33→    throw new Error('Invalid Stripe customer ID');
    34→  }
    35→
    36→  const payload = {
    37→    sub: stripeCustomerId,
    38→    type: 'access',
    39→    tier: options.tier || 'starter',
    40→    iat: Math.floor(Date.now() / 1000)
    41→  };
    42→
    43→  if (options.email) {
    44→    payload.email = options.email;
    45→  }
    46→
    47→  const token = jwt.sign(payload, JWT_SECRET, { expiresIn: JWT_EXPIRES_IN });
    48→
    49→  return {
    50→    token,
    51→    expiresIn: JWT_EXPIRES_IN,
    52→    tokenType: 'Bearer'
    53→  };
    54→}
    55→
    56→/**
    57→ * Generate a refresh token for a customer
    58→ * @param {string} stripeCustomerId - Stripe customer ID
    59→ * @returns {Object} { refreshToken, expiresIn }
    60→ */
    61→function generateRefreshToken(stripeCustomerId) {
    62→  if (!stripeCustomerId || !stripeCustomerId.startsWith('cus_')) {
    63→    throw new Error('Invalid Stripe customer ID');
    64→  }
    65→
    66→  const payload = {
    67→    sub: stripeCustomerId,
    68→    type: 'refresh',
    69→    iat: Math.floor(Date.now() / 1000)
    70→  };
    71→
    72→  const refreshToken = jwt.sign(payload, JWT_SECRET, { expiresIn: JWT_REFRESH_EXPIRES_IN });
    73→
    74→  return {
    75→    refreshToken,
    76→    expiresIn: JWT_REFRESH_EXPIRES_IN
    77→  };
    78→}
    79→
    80→/**
    81→ * Verify and decode a JWT token
    82→ * @param {string} token - JWT token to verify
    83→ * @returns {Object|null} Decoded payload or null if invalid
    84→ */
    85→function verifyToken(token) {
    86→  try {
    87→    const decoded = jwt.verify(token, JWT_SECRET);
    88→    return decoded;
    89→  } catch (err) {
    90→    // Log specific error types for debugging
    91→    if (err.name === 'TokenExpiredError') {
    92→      console.log('[Auth] Token expired');
    93→    } else if (err.name === 'JsonWebTokenError') {
    94→      console.log('[Auth] Invalid token:', err.message);
    95→    }
    96→    return null;
    97→  }
    98→}
    99→
   100→/**
   101→ * Extract Bearer token from Authorization header
   102→ * @param {string} authHeader - Authorization header value
   103→ * @returns {string|null} Token or null if not found
   104→ */
   105→function extractBearerToken(authHeader) {
   106→  if (!authHeader || typeof authHeader !== 'string') {
   107→    return null;
   108→  }
   109→
   110→  const parts = authHeader.split(' ');
   111→  if (parts.length !== 2 || parts[0].toLowerCase() !== 'bearer') {
   112→    return null;
   113→  }
   114→
   115→  return parts[1];
   116→}
   117→
   118→/**
   119→ * Middleware to authenticate JWT tokens
   120→ * Sets req.stripeCustomerId if valid token found
   121→ *
   122→ * Supports both:
   123→ * - Authorization: Bearer <token> (preferred)
   124→ * - x-stripe-customer-id header (legacy, will be deprecated)
   125→ */
   126→function authenticateToken(req, res, next) {
   127→  // First, try JWT token from Authorization header
   128→  const authHeader = req.headers['authorization'];
   129→  const token = extractBearerToken(authHeader);
   130→
   131→  if (token) {
   132→    const decoded = verifyToken(token);
   133→    if (decoded && decoded.sub) {
   134→      req.stripeCustomerId = decoded.sub;
   135→      req.tokenData = decoded;
   136→      return next();
   137→    }
   138→    // Token present but invalid
   139→    return res.status(401).json({
   140→      error: 'Invalid or expired token',
   141→      message: 'Please log in again to continue'
   142→    });
   143→  }
   144→
   145→  // Fallback: Legacy x-stripe-customer-id header (for backward compatibility)
   146→  // This will be deprecated in future versions
   147→  const legacyCustomerId = req.headers['x-stripe-customer-id'];
   148→  if (legacyCustomerId) {
   149→    // Log deprecation warning (once per request)
   150→    if (!req._legacyAuthWarned) {
   151→      console.warn('[Auth] DEPRECATED: Using x-stripe-customer-id header. Migrate to JWT tokens.');
   152→      req._legacyAuthWarned = true;
   153→    }
   154→
   155→    // Validate format
   156→    if (!legacyCustomerId.startsWith('cus_')) {
   157→      return res.status(401).json({
   158→        error: 'Invalid customer ID format',
   159→        message: 'Customer ID must start with cus_'
   160→      });
   161→    }
   162→
   163→    req.stripeCustomerId = legacyCustomerId;
   164→    req.isLegacyAuth = true;
   165→    return next();
   166→  }
   167→
   168→  // No authentication provided
   169→  return res.status(401).json({
   170→    error: 'Authentication required',
   171→    message: 'Please provide a valid Bearer token in the Authorization header'
   172→  });
   173→}
   174→
   175→/**
   176→ * Optional authentication middleware
   177→ * Allows unauthenticated access but sets req.stripeCustomerId if token present
   178→ */
   179→function optionalAuthentication(req, res, next) {
   180→  const authHeader = req.headers['authorization'];
   181→  const token = extractBearerToken(authHeader);
   182→
