   188→    }
   189→  } else {
   190→    // Check legacy header
   191→    const legacyCustomerId = req.headers['x-stripe-customer-id'];
   192→    if (legacyCustomerId && legacyCustomerId.startsWith('cus_')) {
   193→      req.stripeCustomerId = legacyCustomerId;
   194→      req.isLegacyAuth = true;
   195→    }
   196→  }
   197→
   198→  next();
   199→}
