   201→/**
   202→ * Mask sensitive data for logging
   203→ * @param {string} key - License key or other sensitive string
   204→ * @returns {string} Masked string (e.g., "SPLICE-ABC1-****-****-****")
   205→ */
   206→function maskSensitiveData(data) {
   207→  if (!data || typeof data !== 'string') {
   208→    return '****';
   209→  }
   210→
   211→  // For license keys (SPLICE-XXXX-XXXX-XXXX-XXXX format)
   212→  if (data.startsWith('SPLICE-')) {
   213→    const parts = data.split('-');
   214→    if (parts.length === 5) {
   215→      return `${parts[0]}-${parts[1]}-****-****-****`;
   216→    }
   217→  }
   218→
   219→  // For customer IDs (cus_xxxx format)
   220→  if (data.startsWith('cus_')) {
   221→    if (data.length > 10) {
   222→      return `cus_${data.slice(4, 8)}****`;
   223→    }
   224→    return 'cus_****';
   225→  }
   226→
   227→  // For JWTs (eyJ... format)
   228→  if (data.startsWith('eyJ')) {
   229→    return `${data.slice(0, 10)}...****`;
   230→  }
   231→
   232→  // For emails
   233→  if (data.includes('@')) {
   234→    const [local, domain] = data.split('@');
   235→    const maskedLocal = local.length > 2 ? `${local[0]}***${local[local.length-1]}` : '***';
   236→    return `${maskedLocal}@${domain}`;
   237→  }
   238→
   239→  // Generic masking - show first 4 chars
   240→  if (data.length > 8) {
   241→    return `${data.slice(0, 4)}****`;
   242→  }
   243→
   244→  return '****';
   245→}
   246→
   247→module.exports = {
   248→  generateToken,
   249→  generateRefreshToken,
   250→  verifyToken,
   251→  extractBearerToken,
   252→  authenticateToken,
   253→  optionalAuthentication,
   254→  maskSensitiveData,
   255→  JWT_SECRET,
