     1→/**
     2→ * Usage Tracking Service
     3→ *
     4→ * Tracks user credit balances and usage for billing.
     5→ * Uses PostgreSQL for persistence.
     6→ */
     7→
     8→const { Pool } = require('pg');
     9→
    10→// PostgreSQL connection pool with proper limits
    11→const pool = new Pool({
    12→  connectionString: process.env.DATABASE_URL,
    13→  ssl: process.env.NODE_ENV === 'production' ? { rejectUnauthorized: false } : false,
    14→  // PERFORMANCE: Set connection pool limits
    15→  max: 20,                    // Maximum number of clients in the pool
    16→  idleTimeoutMillis: 30000,   // Close idle connections after 30 seconds
    17→  connectionTimeoutMillis: 5000  // Timeout after 5 seconds when getting connection
    18→});
    19→
    20→// Hours per tier (regular processing)
    21→const TIER_HOURS = {
    22→  starter: 15,
    23→  pro: 50,
    24→  team: 150,
    25→  cancelled: 0
    26→};
    27→
    28→// Isolation minutes per tier (sized to maintain 80% margins)
    29→const TIER_ISOLATION_MINUTES = {
    30→  starter: 0,     // No isolation access
    31→  pro: 45,        // 45 minutes included
    32→  team: 180,      // 3 hours included
    33→  cancelled: 0
    34→};
    35→
    36→// Convert to hours for database storage
    37→const TIER_ISOLATION_HOURS = {
    38→  starter: 0,
    39→  pro: TIER_ISOLATION_MINUTES.pro / 60,      // 0.75 hours
    40→  team: TIER_ISOLATION_MINUTES.team / 60,    // 3 hours
    41→  cancelled: 0
    42→};
    43→
    44→// Overage rate for isolation beyond included minutes
    45→const ISOLATION_OVERAGE_RATE = 0.10; // $0.10 per minute (85% margin)
    46→
    47→/**
    48→ * Initialize database tables
    49→ */
    50→async function initDatabase() {
    51→  const client = await pool.connect();
    52→  try {
    53→    await client.query(`
    54→      CREATE TABLE IF NOT EXISTS users (
    55→        id SERIAL PRIMARY KEY,
    56→        stripe_customer_id VARCHAR(255) UNIQUE NOT NULL,
    57→        email VARCHAR(255),
    58→        tier VARCHAR(50) DEFAULT 'starter',
    59→        hours_remaining DECIMAL(10,4) DEFAULT 15,
    60→        hours_total DECIMAL(10,4) DEFAULT 15,
    61→        isolation_hours_remaining DECIMAL(10,4) DEFAULT 0,
    62→        isolation_hours_total DECIMAL(10,4) DEFAULT 0,
    63→        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    64→        updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
    65→      )
    66→    `);
    67→
    68→    // Add isolation columns if they don't exist (for existing databases)
    69→    await client.query(`
    70→      DO $$
    71→      BEGIN
    72→        IF NOT EXISTS (SELECT 1 FROM information_schema.columns WHERE table_name = 'users' AND column_name = 'isolation_hours_remaining') THEN
    73→          ALTER TABLE users ADD COLUMN isolation_hours_remaining DECIMAL(10,4) DEFAULT 0;
    74→        END IF;
    75→        IF NOT EXISTS (SELECT 1 FROM information_schema.columns WHERE table_name = 'users' AND column_name = 'isolation_hours_total') THEN
    76→          ALTER TABLE users ADD COLUMN isolation_hours_total DECIMAL(10,4) DEFAULT 0;
    77→        END IF;
    78→      END $$;
    79→    `);
    80→
    81→    await client.query(`
    82→      CREATE TABLE IF NOT EXISTS usage_log (
    83→        id SERIAL PRIMARY KEY,
    84→        user_id INTEGER REFERENCES users(id),
    85→        stripe_customer_id VARCHAR(255),
    86→        audio_duration_seconds DECIMAL(10,2),
    87→        hours_used DECIMAL(10,6),
    88→        endpoint VARCHAR(100),
    89→        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
    90→      )
    91→    `);
    92→
    93→    await client.query(`
    94→      CREATE INDEX IF NOT EXISTS idx_users_stripe_customer_id ON users(stripe_customer_id)
    95→    `);
    96→
    97→    await client.query(`
    98→      CREATE INDEX IF NOT EXISTS idx_usage_log_user_id ON usage_log(user_id)
    99→    `);
   100→
   101→    // PERFORMANCE: Add composite index for efficient customer usage history queries
   102→    await client.query(`
   103→      CREATE INDEX IF NOT EXISTS idx_usage_log_customer_created
   104→      ON usage_log(stripe_customer_id, created_at DESC)
   105→    `);
   106→
   107→    // Webhook events table for idempotency
   108→    await client.query(`
   109→      CREATE TABLE IF NOT EXISTS webhook_events (
   110→        id SERIAL PRIMARY KEY,
   111→        event_id VARCHAR(255) UNIQUE NOT NULL,
   112→        event_type VARCHAR(100),
   113→        processed_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
   114→      )
   115→    `);
   116→
   117→    await client.query(`
   118→      CREATE INDEX IF NOT EXISTS idx_webhook_events_event_id ON webhook_events(event_id)
   119→    `);
   120→
   121→    console.log('[UsageTracking] Database initialized');
   122→  } finally {
   123→    client.release();
   124→  }
   125→}
   126→
   127→/**
   128→ * Get or create a user by Stripe customer ID
   129→ * PERF-005: Uses INSERT...ON CONFLICT upsert for single-query operation
   130→ */
   131→async function getOrCreateUser(stripeCustomerId, email = null) {
   132→  // Use upsert with ON CONFLICT DO UPDATE to ensure RETURNING works for both cases
   133→  // The update sets stripe_customer_id = EXCLUDED.stripe_customer_id (no-op) to trigger RETURNING
   134→  const result = await pool.query(
   135→    `INSERT INTO users (stripe_customer_id, email, tier, hours_remaining, hours_total, isolation_hours_remaining, isolation_hours_total)
   136→     VALUES ($1, $2, 'starter', $3, $3, $4, $4)
   137→     ON CONFLICT (stripe_customer_id) DO UPDATE
   138→     SET stripe_customer_id = EXCLUDED.stripe_customer_id
   139→     RETURNING *`,
   140→    [stripeCustomerId, email, TIER_HOURS.starter, TIER_ISOLATION_HOURS.starter]
   141→  );
   142→
   143→  return result.rows[0];
   144→}
   145→
   146→/**
   147→ * Get user's current credit balance
   148→ */
   149→async function getBalance(stripeCustomerId) {
   150→  const user = await getOrCreateUser(stripeCustomerId);
   151→  const hoursTotal = parseFloat(user.hours_total);
   152→  const hoursRemaining = parseFloat(user.hours_remaining);
   153→  const isolationHoursTotal = parseFloat(user.isolation_hours_total || 0);
   154→  const isolationHoursRemaining = parseFloat(user.isolation_hours_remaining || 0);
   155→
   156→  // Tier display names
   157→  const tierNames = {
   158→    starter: 'Starter',
   159→    pro: 'Pro',
   160→    team: 'Team',
   161→    cancelled: 'Cancelled'
   162→  };
   163→
   164→  return {
   165→    hoursRemaining,
   166→    hoursTotal,
   167→    isolationHoursRemaining,
   168→    isolationHoursTotal,
   169→    tier: user.tier,
   170→    tierName: tierNames[user.tier] || user.tier,
   171→    percentUsed: hoursTotal > 0 ? ((hoursTotal - hoursRemaining) / hoursTotal * 100).toFixed(1) : '0.0',
   172→    isolationPercentUsed: isolationHoursTotal > 0 ? ((isolationHoursTotal - isolationHoursRemaining) / isolationHoursTotal * 100).toFixed(1) : '0.0',
   173→    hasIsolationAccess: TIER_ISOLATION_HOURS[user.tier] > 0,
   174→    isolationOverageRate: ISOLATION_OVERAGE_RATE
   175→  };
   176→}
   177→
   178→/**
   179→ * Check if user has enough credits for estimated duration
   180→ */
   181→async function hasCredits(stripeCustomerId, estimatedSeconds = 0) {
   182→  const balance = await getBalance(stripeCustomerId);
   183→  const estimatedHours = estimatedSeconds / 3600;
   184→  return balance.hoursRemaining >= estimatedHours;
   185→}
   186→
   187→/**
   188→ * Reserve credits atomically using SELECT ... FOR UPDATE
   189→ * This prevents race conditions where multiple requests pass credit check
   190→ * before any deduction occurs.
   191→ *
   192→ * @param {string} stripeCustomerId - Customer ID
   193→ * @param {number} estimatedSeconds - Estimated processing time in seconds
   194→ * @param {string} endpoint - Endpoint name for tracking
   195→ * @returns {Promise<{success: boolean, reservationId?: string, error?: string}>}
   196→ */
   197→async function reserveCredits(stripeCustomerId, estimatedSeconds = 0, endpoint = 'unknown') {
   198→  const estimatedHours = estimatedSeconds / 3600;
   199→  const client = await pool.connect();
   200→
   201→  try {
   202→    await client.query('BEGIN');
   203→
   204→    // Lock the row for this customer - prevents concurrent modifications
   205→    const result = await client.query(
   206→      `SELECT id, hours_remaining FROM users
   207→       WHERE stripe_customer_id = $1
   208→       FOR UPDATE`,
   209→      [stripeCustomerId]
   210→    );
   211→
   212→    if (result.rows.length === 0) {
   213→      // Create user if doesn't exist (within transaction)
   214→      const newUser = await client.query(
   215→        `INSERT INTO users (stripe_customer_id, tier, hours_remaining, hours_total)
   216→         VALUES ($1, 'starter', $2, $2)
   217→         RETURNING id, hours_remaining`,
   218→        [stripeCustomerId, TIER_HOURS.starter]
   219→      );
   220→      result.rows = newUser.rows;
   221→    }
   222→
   223→    const user = result.rows[0];
   224→    const hoursRemaining = parseFloat(user.hours_remaining);
   225→
   226→    // Check if enough credits
   227→    if (hoursRemaining < estimatedHours) {
   228→      await client.query('ROLLBACK');
   229→      return {
   230→        success: false,
   231→        error: 'Insufficient credits',
   232→        hoursRemaining,
   233→        hoursRequired: estimatedHours
   234→      };
   235→    }
   236→
   237→    // Generate unique reservation ID
   238→    const reservationId = `res_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
   239→
   240→    // Reserve credits by deducting the estimated amount
   241→    await client.query(
   242→      `UPDATE users
   243→       SET hours_remaining = hours_remaining - $1,
   244→           updated_at = CURRENT_TIMESTAMP
   245→       WHERE stripe_customer_id = $2`,
   246→      [estimatedHours, stripeCustomerId]
   247→    );
   248→
   249→    // Log the reservation
   250→    await client.query(
   251→      `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   252→       VALUES ($1, $2, $3, $4, $5)`,
   253→      [user.id, stripeCustomerId, estimatedSeconds, estimatedHours, `${endpoint}:reserved`]
   254→    );
   255→
   256→    await client.query('COMMIT');
   257→
   258→    console.log(`[UsageTracking] Reserved ${estimatedHours.toFixed(4)}hrs for ${stripeCustomerId} (${reservationId})`);
   259→
   260→    return {
   261→      success: true,
   262→      reservationId,
   263→      hoursReserved: estimatedHours,
   264→      hoursRemaining: hoursRemaining - estimatedHours
   265→    };
   266→  } catch (err) {
   267→    await client.query('ROLLBACK');
   268→    console.error('[UsageTracking] Reservation error:', err);
   269→    throw err;
   270→  } finally {
   271→    client.release();
   272→  }
   273→}
   274→
   275→/**
   276→ * Confirm a reservation (finalize usage) or adjust for actual duration
   277→ * Optimized: uses RETURNING to avoid extra SELECT after UPDATE
   278→ *
   279→ * @param {string} stripeCustomerId - Customer ID
   280→ * @param {number} actualSeconds - Actual processing time in seconds
   281→ * @param {number} reservedSeconds - Originally reserved seconds
   282→ * @param {string} endpoint - Endpoint name
   283→ * @returns {Promise<object>} Updated balance
   284→ */
   285→async function confirmReservation(stripeCustomerId, actualSeconds, reservedSeconds, endpoint = 'unknown') {
   286→  const actualHours = actualSeconds / 3600;
   287→  const reservedHours = reservedSeconds / 3600;
   288→  const difference = reservedHours - actualHours;
   289→
   290→  const client = await pool.connect();
   291→  try {
   292→    await client.query('BEGIN');
   293→
   294→    // Lock the row and get current state
   295→    const result = await client.query(
   296→      `SELECT id, hours_remaining, hours_total, isolation_hours_remaining, isolation_hours_total, tier
   297→       FROM users
   298→       WHERE stripe_customer_id = $1
   299→       FOR UPDATE`,
   300→      [stripeCustomerId]
   301→    );
   302→
   303→    if (result.rows.length === 0) {
   304→      await client.query('ROLLBACK');
   305→      throw new Error('User not found');
   306→    }
   307→
   308→    let user = result.rows[0];
   309→
   310→    // If actual usage was less than reserved, refund the difference
   311→    if (difference > 0) {
   312→      const updateResult = await client.query(
   313→        `UPDATE users
   314→         SET hours_remaining = hours_remaining + $1,
   315→             updated_at = CURRENT_TIMESTAMP
   316→         WHERE stripe_customer_id = $2
   317→         RETURNING hours_remaining, hours_total, isolation_hours_remaining, isolation_hours_total, tier`,
   318→        [difference, stripeCustomerId]
   319→      );
   320→      user = { ...user, ...updateResult.rows[0] };
   321→
   322→      // Log the adjustment
   323→      await client.query(
   324→        `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   325→         VALUES ($1, $2, $3, $4, $5)`,
   326→        [user.id, stripeCustomerId, -(reservedSeconds - actualSeconds), -difference, `${endpoint}:adjusted`]
   327→      );
   328→    } else if (difference < 0) {
   329→      // Actual usage was more than reserved - deduct the extra
   330→      const updateResult = await client.query(
   331→        `UPDATE users
   332→         SET hours_remaining = GREATEST(0, hours_remaining - $1),
   333→             updated_at = CURRENT_TIMESTAMP
   334→         WHERE stripe_customer_id = $2
   335→         RETURNING hours_remaining, hours_total, isolation_hours_remaining, isolation_hours_total, tier`,
   336→        [-difference, stripeCustomerId]
   337→      );
   338→      user = { ...user, ...updateResult.rows[0] };
   339→
   340→      // Log the additional usage
   341→      await client.query(
   342→        `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   343→         VALUES ($1, $2, $3, $4, $5)`,
   344→        [user.id, stripeCustomerId, actualSeconds - reservedSeconds, -difference, `${endpoint}:additional`]
   345→      );
   346→    }
   347→
   348→    await client.query('COMMIT');
   349→
   350→    console.log(`[UsageTracking] Confirmed ${actualHours.toFixed(4)}hrs (reserved: ${reservedHours.toFixed(4)}) for ${stripeCustomerId}`);
   351→
   352→    // Build balance from data we already have (no extra SELECT needed)
   353→    const hoursTotal = parseFloat(user.hours_total);
   354→    const hoursRemaining = parseFloat(user.hours_remaining);
   355→    const isolationHoursTotal = parseFloat(user.isolation_hours_total || 0);
   356→    const isolationHoursRemaining = parseFloat(user.isolation_hours_remaining || 0);
   357→
   358→    const tierNames = {
   359→      starter: 'Starter',
   360→      pro: 'Pro',
   361→      team: 'Team',
   362→      cancelled: 'Cancelled'
   363→    };
   364→
   365→    return {
   366→      hoursRemaining,
   367→      hoursTotal,
   368→      isolationHoursRemaining,
   369→      isolationHoursTotal,
   370→      tier: user.tier,
   371→      tierName: tierNames[user.tier] || user.tier,
   372→      percentUsed: hoursTotal > 0 ? ((hoursTotal - hoursRemaining) / hoursTotal * 100).toFixed(1) : '0.0',
   373→      isolationPercentUsed: isolationHoursTotal > 0 ? ((isolationHoursTotal - isolationHoursRemaining) / isolationHoursTotal * 100).toFixed(1) : '0.0',
   374→      hasIsolationAccess: TIER_ISOLATION_HOURS[user.tier] > 0,
   375→      isolationOverageRate: ISOLATION_OVERAGE_RATE
   376→    };
   377→  } catch (err) {
   378→    await client.query('ROLLBACK');
   379→    throw err;
   380→  } finally {
   381→    client.release();
   382→  }
   383→}
   384→
   385→/**
   386→ * Release a reservation (refund credits on processing failure)
   387→ * Optimized: uses RETURNING to avoid extra SELECT after UPDATE
   388→ *
   389→ * @param {string} stripeCustomerId - Customer ID
   390→ * @param {number} reservedSeconds - Originally reserved seconds
   391→ * @param {string} endpoint - Endpoint name
   392→ * @param {string} reason - Reason for release
   393→ * @returns {Promise<object>} Updated balance
   394→ */
   395→async function releaseReservation(stripeCustomerId, reservedSeconds, endpoint = 'unknown', reason = 'failed') {
   396→  const reservedHours = reservedSeconds / 3600;
   397→
