   524→/**
   525→ * Deduct isolation usage from user's balance
   526→ * Returns overage cost if beyond included hours
   527→ * Uses SELECT...FOR UPDATE to prevent race conditions (like confirmReservation)
   528→ */
   529→async function deductIsolationUsage(stripeCustomerId, audioDurationSeconds, endpoint = 'isolate-vocals') {
   530→  const durationMinutes = audioDurationSeconds / 60;
   531→  const durationHours = audioDurationSeconds / 3600;
   532→
   533→  const client = await pool.connect();
   534→  try {
   535→    await client.query('BEGIN');
   536→
   537→    // Lock the row for this customer - prevents concurrent modifications
   538→    const userResult = await client.query(
   539→      `SELECT id, isolation_hours_remaining, isolation_hours_total, hours_remaining, hours_total, tier
   540→       FROM users WHERE stripe_customer_id = $1
   541→       FOR UPDATE`,
   542→      [stripeCustomerId]
   543→    );
   544→
   545→    if (userResult.rows.length === 0) {
   546→      await client.query('ROLLBACK');
   547→      throw new Error('User not found');
   548→    }
   549→
   550→    const user = userResult.rows[0];
   551→    const remainingIncluded = parseFloat(user.isolation_hours_remaining || 0);
   552→
   553→    let overageCost = 0;
   554→    let hoursFromIncluded = 0;
   555→    let overageMinutes = 0;
   556→
   557→    if (remainingIncluded >= durationHours) {
   558→      // Fully covered by included hours
   559→      hoursFromIncluded = durationHours;
   560→    } else {
   561→      // Partial or full overage
   562→      hoursFromIncluded = remainingIncluded;
   563→      overageMinutes = durationMinutes - (remainingIncluded * 60);
   564→      overageCost = overageMinutes * ISOLATION_OVERAGE_RATE;
   565→    }
   566→
   567→    // Update isolation balance with RETURNING to get new values
   568→    const updateResult = await client.query(
   569→      `UPDATE users
   570→       SET isolation_hours_remaining = GREATEST(0, isolation_hours_remaining - $1),
   571→           updated_at = CURRENT_TIMESTAMP
   572→       WHERE stripe_customer_id = $2
   573→       RETURNING isolation_hours_remaining, isolation_hours_total, hours_remaining, hours_total, tier`,
   574→      [durationHours, stripeCustomerId]
   575→    );
   576→
   577→    const updatedUser = updateResult.rows[0];
   578→
   579→    // Log usage (mark as isolation)
   580→    await client.query(
   581→      `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   582→       VALUES ($1, $2, $3, $4, $5)`,
   583→      [user.id, stripeCustomerId, audioDurationSeconds, durationHours, endpoint]
   584→    );
   585→
   586→    await client.query('COMMIT');
   587→
   588→    // Build balance from RETURNING data (no extra SELECT needed)
   589→    const hoursTotal = parseFloat(updatedUser.hours_total);
   590→    const hoursRemaining = parseFloat(updatedUser.hours_remaining);
   591→    const isolationHoursTotal = parseFloat(updatedUser.isolation_hours_total || 0);
   592→    const isolationHoursRemaining = parseFloat(updatedUser.isolation_hours_remaining || 0);
   593→
   594→    const tierNames = {
   595→      starter: 'Starter',
   596→      pro: 'Pro',
   597→      team: 'Team',
   598→      cancelled: 'Cancelled'
   599→    };
   600→
   601→    return {
   602→      hoursRemaining,
   603→      hoursTotal,
   604→      isolationHoursRemaining,
   605→      isolationHoursTotal,
   606→      tier: updatedUser.tier,
   607→      tierName: tierNames[updatedUser.tier] || updatedUser.tier,
   608→      percentUsed: hoursTotal > 0 ? ((hoursTotal - hoursRemaining) / hoursTotal * 100).toFixed(1) : '0.0',
   609→      isolationPercentUsed: isolationHoursTotal > 0 ? ((isolationHoursTotal - isolationHoursRemaining) / isolationHoursTotal * 100).toFixed(1) : '0.0',
   610→      hasIsolationAccess: TIER_ISOLATION_HOURS[updatedUser.tier] > 0,
   611→      isolationOverageRate: ISOLATION_OVERAGE_RATE,
   612→      isolationUsed: {
   613→        durationMinutes,
   614→        hoursFromIncluded,
   615→        overageMinutes,
   616→        overageCost
   617→      }
   618→    };
   619→  } catch (err) {
   620→    await client.query('ROLLBACK');
   621→    throw err;
   622→  } finally {
   623→    client.release();
   624→  }
   625→}
