   893→ */
   894→async function deductMusicCredit(stripeCustomerId) {
   895→  const client = await pool.connect();
   896→  try {
   897→    await client.query('BEGIN');
   898→
   899→    // Lock row for update
   900→    const result = await client.query(
   901→      `SELECT music_credits_remaining, tier
   902→       FROM users
   903→       WHERE stripe_customer_id = $1
   904→       FOR UPDATE`,
