   910→      return { success: false, remaining: 0, charged: 0, error: 'User not found' };
   911→    }
   912→
   913→    const { music_credits_remaining, tier } = result.rows[0];
   914→    let charged = 0;
   915→
   916→    if (music_credits_remaining > 0) {
   917→      // Deduct from included credits
   918→      await client.query(
   919→        `UPDATE users
   920→         SET music_credits_remaining = music_credits_remaining - 1,
   921→             updated_at = CURRENT_TIMESTAMP
   922→         WHERE stripe_customer_id = $1`,
   923→        [stripeCustomerId]
   924→      );
   925→    } else {
