const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');
console.log("🔍 Looking for Claude Code...");
// 1. Find where Claude is installed
let cliPath;
try {
    // Ask npm where global modules are
    const npmRoot = execSync('npm root -g', { encoding: 'utf8' }).trim();
    cliPath = path.join(npmRoot, '@anthropic-ai', 'claude-code', 'cli.js');
} catch (e) {
    console.error("❌ Could not find npm root. Is Node installed?");
    process.exit(1);
}
if (!fs.existsSync(cliPath)) {
    console.error(`❌ Could not find cli.js at: ${cliPath}`);
    console.error("   Make sure you ran: npm install -g @anthropic-ai/claude-code");
    process.exit(1);
}
console.log(`✅ Found: ${cliPath}`);
// 2. Read the file
let content = fs.readFileSync(cliPath, 'utf8');
let modified = false;
// 3. Apply the patches
// Patch 1: Remove the "Refuse requests..." security policy
// We look for the "IMPORTANT: Assist with..." block and blank it out.
const securityPolicyRegex = /("IMPORTANT: Assist with authorized security testing.*?defensive use cases\.")/g;
if (securityPolicyRegex.test(content)) {
    content = content.replace(securityPolicyRegex, '""');
    console.log("🔓 Patch 1 applied: Removed security refusal policy.");

