   473→/**
   474→ * Check if user can use vocal isolation
   475→ * Returns: { allowed, hasIncludedHours, overageRequired, overageCost }
   476→ */
   477→async function checkIsolationAccess(stripeCustomerId, estimatedMinutes = 0) {
   478→  const balance = await getBalance(stripeCustomerId);
   479→
   480→  // Starter tier has no isolation access
   481→  if (!balance.hasIsolationAccess) {
   482→    return {
   483→      allowed: false,
   484→      reason: 'upgrade_required',
   485→      message: 'Vocal isolation requires Pro or Team tier',
   486→      hasIncludedHours: false,
   487→      overageRequired: false,
   488→      overageCost: 0
   489→    };
   490→  }
   491→
   492→  const estimatedHours = estimatedMinutes / 60;
   493→  const remainingIncluded = balance.isolationHoursRemaining;
   494→
   495→  // Check if within included hours
   496→  if (remainingIncluded >= estimatedHours) {
   497→    return {
   498→      allowed: true,
   499→      reason: 'included',
   500→      message: `Using ${estimatedMinutes.toFixed(1)} min of included isolation time`,
   501→      hasIncludedHours: true,
   502→      overageRequired: false,
   503→      overageCost: 0,
   504→      isolationHoursRemaining: remainingIncluded - estimatedHours
   505→    };
   506→  }
   507→
   508→  // Calculate overage
   509→  const overageMinutes = estimatedMinutes - (remainingIncluded * 60);
   510→  const overageCost = overageMinutes * ISOLATION_OVERAGE_RATE;
   511→
   512→  return {
   513→    allowed: true,
   514→    reason: 'overage',
   515→    message: `${remainingIncluded > 0 ? `Using ${(remainingIncluded * 60).toFixed(1)} included min + ` : ''}${overageMinutes.toFixed(1)} min overage ($${overageCost.toFixed(2)})`,
   516→    hasIncludedHours: remainingIncluded > 0,
   517→    overageRequired: true,
   518→    overageMinutes,
   519→    overageCost,
   520→    isolationHoursRemaining: 0
   521→  };
   522→}
