   524→/**
   525→ * Deduct isolation usage from user's balance
   526→ * Returns overage cost if beyond included hours
   527→ */
   528→async function deductIsolationUsage(stripeCustomerId, audioDurationSeconds, endpoint = 'isolate-vocals') {
   529→  const durationMinutes = audioDurationSeconds / 60;
   530→  const durationHours = audioDurationSeconds / 3600;
   531→
   532→  const client = await pool.connect();
   533→  try {
   534→    // Get user
   535→    const userResult = await client.query(
   536→      'SELECT * FROM users WHERE stripe_customer_id = $1',
   537→      [stripeCustomerId]
   538→    );
   539→
   540→    if (userResult.rows.length === 0) {
   541→      throw new Error('User not found');
   542→    }
   543→
   544→    const user = userResult.rows[0];
   545→    const remainingIncluded = parseFloat(user.isolation_hours_remaining || 0);
   546→
   547→    let overageCost = 0;
   548→    let hoursFromIncluded = 0;
   549→    let overageMinutes = 0;
   550→
   551→    if (remainingIncluded >= durationHours) {
   552→      // Fully covered by included hours
   553→      hoursFromIncluded = durationHours;
   554→    } else {
   555→      // Partial or full overage
   556→      hoursFromIncluded = remainingIncluded;
   557→      overageMinutes = durationMinutes - (remainingIncluded * 60);
   558→      overageCost = overageMinutes * ISOLATION_OVERAGE_RATE;
   559→    }
   560→
   561→    // Update isolation balance
   562→    await client.query(
   563→      `UPDATE users
   564→       SET isolation_hours_remaining = GREATEST(0, isolation_hours_remaining - $1),
   565→           updated_at = CURRENT_TIMESTAMP
   566→       WHERE stripe_customer_id = $2`,
   567→      [durationHours, stripeCustomerId]
   568→    );
   569→
   570→    // Log usage (mark as isolation)
   571→    await client.query(
   572→      `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   573→       VALUES ($1, $2, $3, $4, $5)`,
   574→      [user.id, stripeCustomerId, audioDurationSeconds, durationHours, endpoint]
   575→    );
   576→
   577→    // Return updated balance and overage info
   578→    const newBalance = await getBalance(stripeCustomerId);
   579→    return {
   580→      ...newBalance,
   581→      isolationUsed: {
   582→        durationMinutes,
   583→        hoursFromIncluded,
   584→        overageMinutes,
   585→        overageCost
   586→      }
   587→    };
   588→  } finally {
   589→    client.release();
   590→  }
   591→}
