   727→  const hoursRemaining = parseFloat(user.hours_remaining);
   728→  const isolationHoursTotal = parseFloat(user.isolation_hours_total || 0);
   729→  const isolationHoursRemaining = parseFloat(user.isolation_hours_remaining || 0);
   730→
   731→  const tierNames = {
   732→    starter: 'Starter',
   733→    pro: 'Pro',
   734→    team: 'Team',
   735→    cancelled: 'Cancelled'
   736→  };
   737→
   738→  return {
   739→    hoursRemaining,
   740→    hoursTotal,
   741→    isolationHoursRemaining,
   742→    isolationHoursTotal,
