   145→const DEFAULT_PROFANITY_FR = [
   146→  'merde', 'putain', 'bordel', 'salaud', 'salope', 'connard', 'connasse',
   147→  'enculé', 'nique', 'bite', 'couille', 'foutre', 'baiser', 'chier', 'con'
   148→];
   149→
   150→/**
   151→ * Default German profanity list
   152→ */
   153→const DEFAULT_PROFANITY_DE = [
   154→  'scheiße', 'scheisse', 'arschloch', 'hurensohn', 'wichser', 'fotze',
   155→  'fick', 'ficken', 'verdammt', 'schlampe', 'hure', 'mistkerl'
   156→];
   157→
   158→// Language mapping
   159→const PROFANITY_LISTS = {
   160→  'en': DEFAULT_PROFANITY_EN,
   161→  'es': DEFAULT_PROFANITY_ES,
   162→  'fr': DEFAULT_PROFANITY_FR,
   163→  'de': DEFAULT_PROFANITY_DE
   164→};
   165→
   166→// =============================================================================
   167→// Core Detection
   168→// =============================================================================
   169→
   170→/**
   171→ * Detect profanity in transcript words
   172→ *
   173→ * @param {Object} transcript - Transcript with words array
   174→ * @param {Object} options - Detection options
   175→ * @returns {Object} Detection results with marked words and segments
   176→ */
   177→function detectProfanity(transcript, options = {}) {
   178→  const {
   179→    language = 'en',
   180→    customBlocklist = [],
   181→    customAllowlist = [],
   182→    caseSensitive = false,
   183→    includePartialMatches = true,
   184→    frameRate = 30  // For frame alignment
   185→  } = options;
   186→
   187→  // Get base profanity list for language
   188→  const baseProfanity = PROFANITY_LISTS[language] || PROFANITY_LISTS['en'];
   189→
   190→  // Build final profanity set
   191→  const profanitySet = new Set([
   192→    ...baseProfanity,
   193→    ...customBlocklist.map(w => caseSensitive ? w : w.toLowerCase())
   194→  ]);
   195→
   196→  // Remove allowlist items
   197→  const allowlistSet = new Set(customAllowlist.map(w => caseSensitive ? w : w.toLowerCase()));
   198→  for (const allowed of allowlistSet) {
   199→    profanitySet.delete(allowed);
   200→  }
   201→
   202→  // Also add common variants (e.g., "fucking" -> "fuckin'")
   203→  const variants = new Set();
   204→  for (const word of profanitySet) {
   205→    if (word.endsWith('ing')) {
   206→      variants.add(word.slice(0, -1)); // fuckin
   207→      variants.add(word.slice(0, -3) + "in'"); // fuckin'
   208→    }
   209→    if (word.endsWith('ed')) {
   210→      variants.add(word.slice(0, -2) + "'d"); // fuck'd
   211→    }
   212→  }
   213→  for (const v of variants) {
   214→    profanitySet.add(v);
   215→  }
   216→
   217→  // Check each word in transcript
   218→  const words = transcript.words || [];
   219→  const markedWords = [];
   220→
   221→  // Create false positive set once for performance
   222→  const falsePositiveSet = new Set(FALSE_POSITIVE_ALLOWLIST);
   223→
   224→  for (let i = 0; i < words.length; i++) {
   225→    const word = words[i];
   226→    const wordText = word.word || word.text || '';
   227→    const normalizedWord = caseSensitive ? wordText : wordText.toLowerCase();
   228→
   229→    // Remove punctuation for matching
   230→    const cleanWord = normalizedWord.replace(/[^\w\s'-]/g, '').trim();
   231→
   232→    // Check for exact match
   233→    let isProfanity = profanitySet.has(cleanWord);
   234→
   235→    // Check for partial matches (word contains profanity)
   236→    // But skip words that are known false positives
   237→    if (!isProfanity && includePartialMatches) {
   238→      // Skip if word is in the false positive allowlist
   239→      if (!falsePositiveSet.has(cleanWord)) {
   240→        for (const profane of profanitySet) {
   241→          if (cleanWord.includes(profane) && profane.length >= 4) {
   242→            isProfanity = true;
   243→            break;
   244→          }
   245→        }
   246→      }
   247→    }
   248→
   249→    markedWords.push({
   250→      text: wordText,
   251→      start: word.start,
   252→      end: word.end,
   253→      index: i,
   254→      isProfanity,
   255→      // Can be toggled by user in UI
   256→      shouldCensor: isProfanity
   257→    });
   258→  }
   259→
   260→  // Group contiguous profanity into segments
   261→  const segments = groupProfanitySegments(markedWords, { frameRate });
   262→
   263→  // Calculate statistics
   264→  const profanityCount = markedWords.filter(w => w.isProfanity).length;
   265→  const totalDuration = segments.reduce((sum, s) => sum + s.duration, 0);
   266→
   267→  console.log(`[SPLICE Profanity] Detected ${profanityCount} profane word(s) in ${segments.length} segment(s)`);
   268→
   269→  return {
   270→    words: markedWords,
   271→    segments,
   272→    metadata: {
   273→      totalWords: words.length,
   274→      profanityCount,
   275→      segmentCount: segments.length,
   276→      totalCensorDuration: parseFloat(totalDuration.toFixed(3)),
   277→      language,
   278→      customBlocklistCount: customBlocklist.length,
   279→      customAllowlistCount: customAllowlist.length
   280→    }
   281→  };
   282→}
   283→
   284→/**
   285→ * Group contiguous profanity words into segments
   286→ *
   287→ * @param {Array} markedWords - Array of words with isProfanity flag
   288→ * @param {Object} options - Grouping options
   289→ * @returns {Array} Array of profanity segments
   290→ */
   291→function groupProfanitySegments(markedWords, options = {}) {
   292→  const { frameRate = 30, maxGap = 0.3 } = options;
   293→
   294→  const segments = [];
   295→  let currentSegment = null;
   296→
   297→  for (const word of markedWords) {
   298→    if (!word.isProfanity) {
   299→      // End current segment if exists
   300→      if (currentSegment) {
   301→        segments.push(finalizeSeg(currentSegment, frameRate));
   302→        currentSegment = null;
   303→      }
   304→      continue;
   305→    }
   306→
   307→    if (!currentSegment) {
   308→      // Start new segment
   309→      currentSegment = {
   310→        start: word.start,
   311→        end: word.end,
   312→        words: [word.text],
   313→        wordIndices: [word.index]
   314→      };
   315→    } else if (word.start - currentSegment.end <= maxGap) {
   316→      // Extend current segment (words are close together)
   317→      currentSegment.end = word.end;
   318→      currentSegment.words.push(word.text);
   319→      currentSegment.wordIndices.push(word.index);
   320→    } else {
   321→      // Gap too large - finalize current and start new
   322→      segments.push(finalizeSeg(currentSegment, frameRate));
   323→      currentSegment = {
   324→        start: word.start,
   325→        end: word.end,
   326→        words: [word.text],
   327→        wordIndices: [word.index]
   328→      };
   329→    }
   330→  }
   331→
   332→  // Finalize last segment
   333→  if (currentSegment) {
   334→    segments.push(finalizeSeg(currentSegment, frameRate));
   335→  }
   336→
   337→  return segments;
   338→}
   339→
   340→/**
   341→ * Finalize a segment with frame-aligned boundaries
   342→ */
   343→function finalizeSeg(segment, frameRate) {
   344→  // Add small padding around profanity (50ms before, 100ms after)
   345→  const paddedStart = Math.max(0, segment.start - 0.05);
   346→  const paddedEnd = segment.end + 0.1;
   347→
   348→  // Align to frame boundaries
   349→  const alignedStart = alignToFrame(paddedStart, frameRate, 'floor');
   350→  const alignedEnd = alignToFrame(paddedEnd, frameRate, 'ceil');
   351→
   352→  return {
   353→    start: parseFloat(alignedStart.toFixed(4)),
   354→    end: parseFloat(alignedEnd.toFixed(4)),
   355→    duration: parseFloat((alignedEnd - alignedStart).toFixed(4)),
   356→    words: segment.words,
   357→    wordCount: segment.words.length,
   358→    text: segment.words.join(' ')
   359→  };
   360→}
   361→
   362→/**
   363→ * Align time to frame boundary
   364→ */
   365→function alignToFrame(time, frameRate, mode = 'round') {
   366→  const frameDuration = 1 / frameRate;
   367→  switch (mode) {
   368→    case 'floor':
   369→      return Math.floor(time / frameDuration) * frameDuration;
   370→    case 'ceil':
   371→      return Math.ceil(time / frameDuration) * frameDuration;
   372→    default:
   373→      return Math.round(time / frameDuration) * frameDuration;
   374→  }
   375→}
   376→
   377→// =============================================================================
   378→// Bleep Sound Management
   379→// =============================================================================
   380→
   381→/**
   382→ * Available bleep sounds
   383→ */
   384→const BLEEP_SOUNDS = {
   385→  'standard': {
   386→    name: 'Standard Censor Beep',
   387→    frequency: 1000,
   388→    description: 'Classic 1kHz censor beep'
   389→  },
   390→  'tv': {
   391→    name: 'TV Bleep',
   392→    frequency: 1000,
   393→    description: 'Television-style censor beep'
   394→  },
   395→  'radio': {
   396→    name: 'Radio Bleep',
   397→    frequency: 880,
   398→    description: 'Radio broadcast censor tone'
   399→  },
   400→  'duck': {
   401→    name: 'Audio Duck',
   402→    frequency: 0,
   403→    description: 'Volume reduction (no beep)'
   404→  },
   405→  'mute': {
   406→    name: 'Complete Mute',
   407→    frequency: 0,
   408→    description: 'Complete silence'
   409→  }
   410→};
   411→
   412→/**
   413→ * Get list of available bleep sounds
   414→ */
   415→function getAvailableBleepSounds() {
   416→  return Object.entries(BLEEP_SOUNDS).map(([id, info]) => ({
   417→    id,
   418→    ...info
   419→  }));
   420→}
   421→
   422→// =============================================================================
   423→// Bleep Audio Generation
   424→// =============================================================================
   425→
   426→/**
   427→ * Generate a bleep/censor tone audio file
   428→ *
   429→ * @param {Object} options - Generation options
   430→ * @param {number} options.duration - Duration in seconds
   431→ * @param {string} options.bleepType - Type of bleep ('standard', 'tv', 'radio', 'duck', 'mute')
   432→ * @param {number} options.sampleRate - Sample rate (default: 44100)
   433→ * @param {number} options.volume - Volume level 0-1 (default: 0.5)
   434→ * @param {string} options.outputPath - Optional output path (generates temp file if not provided)
   435→ * @returns {Promise<Object>} Result with path to generated file
   436→ */
   437→async function generateBleepAudio(options = {}) {
   438→  const {
   439→    duration = 1.0,
   440→    bleepType = 'standard',
   441→    sampleRate = 44100,
   442→    volume = 0.5,
   443→    outputPath = null
   444→  } = options;
   445→
   446→  const bleepConfig = BLEEP_SOUNDS[bleepType] || BLEEP_SOUNDS.standard;
   447→  const frequency = bleepConfig.frequency;
   448→
   449→  console.log(`[SPLICE Bleep] Generating ${bleepType} bleep: ${duration}s @ ${frequency}Hz`);
   450→
   451→  // Calculate number of samples
   452→  const numSamples = Math.ceil(duration * sampleRate);
   453→  const numChannels = 1; // Mono
   454→  const bitsPerSample = 16;
   455→  const bytesPerSample = bitsPerSample / 8;
   456→
   457→  // Generate audio samples
   458→  const samples = new Int16Array(numSamples);
   459→
   460→  if (frequency > 0) {
   461→    // Generate sine wave at specified frequency
   462→    const angularFrequency = 2 * Math.PI * frequency / sampleRate;
   463→    const amplitude = Math.floor(32767 * volume);
   464→
   465→    // Apply fade in/out to avoid clicks (10ms each)
   466→    const fadeLength = Math.min(Math.floor(0.01 * sampleRate), numSamples / 4);
   467→
   468→    for (let i = 0; i < numSamples; i++) {
   469→      // Base sine wave
   470→      let sample = Math.sin(angularFrequency * i) * amplitude;
   471→
   472→      // Apply fade in
   473→      if (i < fadeLength) {
   474→        sample *= i / fadeLength;
   475→      }
   476→
   477→      // Apply fade out
   478→      if (i > numSamples - fadeLength) {
   479→        sample *= (numSamples - i) / fadeLength;
   480→      }
   481→
   482→      samples[i] = Math.round(sample);
   483→    }
   484→  } else {
   485→    // Mute/duck: generate silence
   486→    samples.fill(0);
   487→  }
   488→
   489→  // Create WAV file
   490→  const dataSize = numSamples * numChannels * bytesPerSample;
   491→  const fileSize = 44 + dataSize; // 44-byte WAV header + data
   492→
   493→  const buffer = Buffer.alloc(fileSize);
   494→
   495→  // WAV header
   496→  buffer.write('RIFF', 0);
   497→  buffer.writeUInt32LE(fileSize - 8, 4);
   498→  buffer.write('WAVE', 8);
   499→  buffer.write('fmt ', 12);
   500→  buffer.writeUInt32LE(16, 16); // Subchunk1Size (16 for PCM)
   501→  buffer.writeUInt16LE(1, 20); // AudioFormat (1 = PCM)
   502→  buffer.writeUInt16LE(numChannels, 22);
   503→  buffer.writeUInt32LE(sampleRate, 24);
   504→  buffer.writeUInt32LE(sampleRate * numChannels * bytesPerSample, 28); // ByteRate
   505→  buffer.writeUInt16LE(numChannels * bytesPerSample, 32); // BlockAlign
   506→  buffer.writeUInt16LE(bitsPerSample, 34);
   507→  buffer.write('data', 36);
   508→  buffer.writeUInt32LE(dataSize, 40);
   509→
   510→  // Write samples
   511→  for (let i = 0; i < numSamples; i++) {
   512→    buffer.writeInt16LE(samples[i], 44 + i * bytesPerSample);
   513→  }
   514→
   515→  // Write to file
   516→  const filePath = outputPath || path.join('/tmp', `splice_bleep_${crypto.randomBytes(4).toString('hex')}.wav`);
   517→
   518→  await fs.promises.writeFile(filePath, buffer);
   519→
   520→  console.log(`[SPLICE Bleep] Generated: ${filePath}`);
   521→
   522→  return {
   523→    success: true,
   524→    path: filePath,
   525→    duration,
   526→    frequency,
   527→    bleepType,
   528→    sampleRate,
   529→    fileSize
   530→  };
   531→}
