   533→/**
   534→ * Generate bleep audio for multiple segments
   535→ *
   536→ * @param {Array} segments - Array of profanity segments with start/end
   537→ * @param {Object} options - Generation options
   538→ * @returns {Promise<Array>} Array of generated bleep info for each segment
   539→ */
   540→async function generateBleepsForSegments(segments, options = {}) {
   541→  const { bleepType = 'standard', volume = 0.5, outputDir = '/tmp' } = options;
   542→
   543→  // Generate all bleeps in parallel (I/O bound, safe to parallelize)
   544→  const results = await Promise.all(
   545→    segments.map(async (segment, i) => {
   546→      const duration = segment.end - segment.start;
   547→      const outputPath = path.join(outputDir, `splice_bleep_segment_${i}_${crypto.randomBytes(4).toString('hex')}.wav`);
   548→
   549→      try {
   550→        const result = await generateBleepAudio({
   551→          duration,
   552→          bleepType,
   553→          volume,
   554→          outputPath
   555→        });
   556→
   557→        return {
   558→          segmentIndex: i,
   559→          segmentStart: segment.start,
   560→          segmentEnd: segment.end,
   561→          ...result
   562→        };
   563→      } catch (err) {
   564→        console.error(`[SPLICE Bleep] Failed to generate bleep for segment ${i}:`, err);
   565→        return {
   566→          segmentIndex: i,
   567→          success: false,
   568→          error: err.message
   569→        };
   570→      }
   571→    })
   572→  );
   573→
   574→  return results;
   575→}
   576→
   577→/**
   578→ * Clean up generated bleep files
   579→ *
   580→ * @param {Array} bleepPaths - Array of bleep file paths to delete
   581→ */
   582→async function cleanupBleepFiles(bleepPaths) {
   583→  for (const filePath of bleepPaths) {
   584→    try {
   585→      await fs.promises.unlink(filePath);
   586→    } catch (_err) {
   587→      // Ignore errors (file may already be deleted)
   588→    }
   589→  }
   590→}
   591→
   592→// =============================================================================
   593→// Profanity List Management
   594→// =============================================================================
   595→
   596→/**
   597→ * Get the default profanity list for a language
   598→ *
   599→ * @param {string} language - Language code (en, es, fr, de)
   600→ * @returns {Array<string>} Profanity word list
   601→ */
   602→function getProfanityList(language = 'en') {
   603→  return PROFANITY_LISTS[language] || PROFANITY_LISTS['en'];
   604→}
   605→
   606→/**
   607→ * Get all supported languages
   608→ */
   609→function getSupportedLanguages() {
   610→  return [
   611→    { code: 'en', name: 'English' },
   612→    { code: 'es', name: 'Spanish' },
   613→    { code: 'fr', name: 'French' },
   614→    { code: 'de', name: 'German' }
   615→  ];
   616→}
   617→
   618→/**
   619→ * Validate and clean a custom word list
   620→ *
   621→ * @param {string|Array} input - Comma-separated string or array
   622→ * @returns {Array<string>} Cleaned word list
   623→ */
   624→function parseWordList(input) {
   625→  if (!input) return [];
   626→
   627→  if (Array.isArray(input)) {
   628→    return input.map(w => w.trim().toLowerCase()).filter(w => w.length > 0);
   629→  }
   630→
   631→  if (typeof input === 'string') {
   632→    return input
   633→      .split(',')
   634→      .map(w => w.trim().toLowerCase())
   635→      .filter(w => w.length > 0);
   636→  }
   637→
   638→  return [];
   639→}
   640→
   641→// =============================================================================
   642→// Segment Operations
   643→// =============================================================================
   644→
   645→/**
   646→ * Merge profanity segments that are close together
   647→ *
   648→ * @param {Array} segments - Array of profanity segments
   649→ * @param {number} maxGap - Maximum gap to merge (seconds)
   650→ * @returns {Array} Merged segments
   651→ */
   652→function mergeAdjacentSegments(segments, maxGap = 0.5) {
   653→  if (segments.length <= 1) return segments;
   654→
   655→  const sorted = [...segments].sort((a, b) => a.start - b.start);
   656→  const merged = [];
   657→  let current = { ...sorted[0] };
   658→
   659→  for (let i = 1; i < sorted.length; i++) {
   660→    const next = sorted[i];
   661→
   662→    if (next.start - current.end <= maxGap) {
   663→      // Merge
   664→      current.end = next.end;
   665→      current.duration = current.end - current.start;
   666→      current.words = [...current.words, ...next.words];
   667→      current.wordCount = current.words.length;
   668→      current.text = current.words.join(' ');
   669→    } else {
   670→      merged.push(current);
   671→      current = { ...next };
   672→    }
   673→  }
   674→
   675→  merged.push(current);
   676→  return merged;
   677→}
   678→
   679→/**
   680→ * Filter segments by user selection (for UI toggle)
   681→ *
   682→ * @param {Array} segments - All profanity segments
   683→ * @param {Array<number>} selectedIndices - Indices of segments to keep
   684→ * @returns {Array} Filtered segments
   685→ */
   686→function filterSelectedSegments(segments, selectedIndices) {
   687→  const indexSet = new Set(selectedIndices);
   688→  return segments.filter((_, i) => indexSet.has(i));
   689→}
   690→
   691→/**
   692→ * Convert segments to timeline format for cut list generator
   693→ *
   694→ * @param {Array} segments - Profanity segments
   695→ * @returns {Array<{start: number, end: number, type: string}>} Timeline-ready segments
   696→ */
   697→function segmentsToTimeline(segments) {
   698→  return segments.map(seg => ({
   699→    start: seg.start,
   700→    end: seg.end,
   701→    duration: seg.duration,
   702→    type: 'profanity',
   703→    label: `Profanity: ${seg.text.substring(0, 30)}${seg.text.length > 30 ? '...' : ''}`
   704→  }));
   705→}
   706→
   707→// =============================================================================
   708→// Exports
   709→// =============================================================================
   710→
   711→module.exports = {
   712→  // Core detection
   713→  detectProfanity,
   714→  groupProfanitySegments,
   715→
   716→  // Profanity lists
   717→  getProfanityList,
   718→  getSupportedLanguages,
   719→  parseWordList,
   720→  PROFANITY_LISTS,
   721→
   722→  // Bleep sounds
   723→  getAvailableBleepSounds,
   724→  BLEEP_SOUNDS,
   725→
   726→  // Bleep audio generation
   727→  generateBleepAudio,
   728→  generateBleepsForSegments,
   729→  cleanupBleepFiles,
   730→
   731→  // Segment operations
   732→  mergeAdjacentSegments,
   733→  filterSelectedSegments,
   734→  segmentsToTimeline,
   735→
   736→  // Utilities
   737→  alignToFrame
   738→};
