  'ass', 'asshole', 'assholes', 'asses', 'dumbass', 'jackass', 'badass',
  'bitch', 'bitches', 'bitchy', 'bitching', 'son of a bitch',
  'bastard', 'bastards',
  'crap', 'crappy',
  'hell', 'hellish',
  'piss', 'pissed', 'pissing',

  // Slurs and offensive terms (heavily censored in most media)
  'cunt', 'cunts',
  'cock', 'cocks', 'cocksucker',
  'dick', 'dicks', 'dickhead',
