   117→    : tunedDecisions;
   118→
   119→  // Step 8: Calculate statistics
   120→  const stats = calculateMultitrackStats(finalDecisions, trackLevels[0].duration);
   121→
   122→  console.log(`[SPLICE Multitrack] Generated ${finalDecisions.length} switching decision(s)`);
   123→
   124→  return {
   125→    decisions: finalDecisions,
   126→    metadata: {
   127→      trackCount: audioPaths.length,
   128→      speakerNames: opts.speakerNames || audioPaths.map((_, i) => `Speaker ${i + 1}`),
   129→      duration: maxDuration,
   130→      frameRate: opts.frameRate,
   131→      ...stats
   132→    },
   133→    levels: {
   134→      raw: trackLevels.map(t => ({ speaker: t.speakerName, sampleCount: t.levels.length })),
   135→      // Don't include full level data in response (too large)
   136→    }
   137→  };
   138→}
   139→
   140→/**
   141→ * Analyze multitrack using pre-extracted levels (PERF-008)
   142→ * Skips FFmpeg processing for faster iteration during auto-balance
   143→ *
   144→ * @param {Array<Object>} cachedLevels - Pre-extracted track levels from extractTrackLevels
   145→ * @param {Object} options - Analysis options
   146→ * @returns {Object} Analysis results with switching decisions
   147→ */
   148→function analyzeMultitrackFromLevels(cachedLevels, options = {}) {
   149→  const opts = { ...DEFAULT_OPTIONS, ...options };
   150→
   151→  // Validate all tracks have same duration
   152→  const durations = cachedLevels.map(t => t.duration);
   153→  const maxDuration = Math.max(...durations);
   154→  const minDuration = Math.min(...durations);
   155→  if (maxDuration - minDuration > 1) {
   156→    console.warn('[SPLICE Multitrack] Track durations differ significantly');
   157→  }
   158→
   159→  // Step 2: Calculate noise floor and clean up levels
   160→  const cleanedLevels = cleanupAudioLevels(cachedLevels, opts);
   161→
   162→  // Step 3: Apply speaker boosts
   163→  const boostedLevels = applySpeakerBoosts(cleanedLevels, opts.speakerBoosts);
   164→
   165→  // Step 4: Apply Gaussian smoothing
   166→  const smoothedLevels = opts.smoothingWindow > 0
   167→    ? smoothAudioLevels(boostedLevels, opts.smoothingWindow)
   168→    : boostedLevels;
   169→
   170→  // Step 5: Generate switching decisions
   171→  const decisions = generateSwitchingDecisions(smoothedLevels, {
   172→    videoTrackMapping: opts.videoTrackMapping,
   173→    minShotDuration: opts.minShotDuration,
   174→    switchingFrequency: opts.switchingFrequency,
   175→    wideShotEnabled: opts.wideShotEnabled,
   176→    wideShotTolerance: opts.wideShotTolerance,
   177→    wideShotTracks: opts.wideShotTracks,
   178→    frameRate: opts.frameRate
   179→  });
   180→
   181→  // Step 6: Fine-tune wide shot percentage
   182→  const tunedDecisions = opts.wideShotEnabled
   183→    ? tuneWideShotPercentage(decisions, opts.wideShotPercentage)
   184→    : decisions;
   185→
   186→  // Step 7: Insert cutaways if enabled
   187→  const finalDecisions = opts.cutawayEnabled
   188→    ? insertCutaways(tunedDecisions, {
   189→        cutawayTracks: opts.cutawayTracks,
   190→        minDuration: opts.cutawayMinDuration,
   191→        maxDuration: opts.cutawayMaxDuration
   192→      })
   193→    : tunedDecisions;
   194→
   195→  // Step 8: Calculate statistics
   196→  const stats = calculateMultitrackStats(finalDecisions, cachedLevels[0].duration);
   197→
   198→  return {
   199→    decisions: finalDecisions,
   200→    metadata: {
   201→      trackCount: cachedLevels.length,
   202→      speakerNames: opts.speakerNames || cachedLevels.map((_, i) => `Speaker ${i + 1}`),
   203→      duration: maxDuration,
   204→      frameRate: opts.frameRate,
   205→      ...stats
   206→    },
   207→    levels: {
   208→      raw: cachedLevels.map(t => ({ speaker: t.speakerName, sampleCount: t.levels.length })),
   209→    }
   210→  };
   211→}
   212→
   213→// =============================================================================
   214→// Audio Level Extraction
   215→// =============================================================================
   216→
   217→/**
   218→ * Extract RMS levels from audio file
   219→ *
   220→ * @param {string} audioPath - Path to audio file
   221→ * @param {Object} options - Extraction options
   222→ * @returns {Promise<Object>} Track levels with timing
   223→ */
   224→async function extractTrackLevels(audioPath, options = {}) {
   225→  const { windowMs = 100, speakerName = 'Unknown' } = options;
   226→
   227→  // SECURITY: Validate input path to prevent path traversal
   228→  const pathValidation = await validateAudioPath(audioPath);
   229→  if (!pathValidation.valid) {
   230→    throw new Error(`Invalid audio path: ${pathValidation.error}`);
   231→  }
   232→  const validatedPath = pathValidation.path;
   233→
   234→  console.log(`[SPLICE Multitrack] Extracting levels: ${speakerName}`);
   235→
   236→  // SECURITY: Use execFile with array arguments to prevent command injection
   237→  const infoArgs = [
   238→    '-v', 'error',
   239→    '-select_streams', 'a:0',
   240→    '-show_entries', 'stream=sample_rate,duration',
   241→    '-of', 'json',
   242→    validatedPath
   243→  ];
   244→  let duration = 0;
   245→
   246→  try {
   247→    const { stdout } = await safeFFprobe(infoArgs);
   248→    const info = JSON.parse(stdout);
   249→    if (info.streams?.[0]) {
   250→      // Note: sample_rate available in info.streams[0].sample_rate for future resampling support
   251→      duration = parseFloat(info.streams[0].duration) || 0;
   252→    }
   253→  } catch (err) {
   254→    console.warn(`[SPLICE Multitrack] Could not get audio info: ${err.message}`);
   255→  }
   256→
   257→  // Get duration from format if not in stream
   258→  if (!duration) {
   259→    try {
   260→      const durationArgs = [
   261→        '-v', 'error',
   262→        '-show_entries', 'format=duration',
   263→        '-of', 'default=noprint_wrappers=1:nokey=1',
   264→        validatedPath
   265→      ];
   266→      const { stdout } = await safeFFprobe(durationArgs);
   267→      duration = parseFloat(stdout.trim()) || 0;
   268→    } catch (err) {
   269→      console.warn(`[SPLICE Multitrack] Could not get duration: ${err.message}`);
   270→    }
   271→  }
   272→
   273→  // SECURITY: Use safe temp path generation
   274→  const tempFile = safeTempPath('splice_multitrack', '.raw');
   275→
   276→  try {
   277→    // SECURITY: Use execFile with array arguments to prevent command injection
   278→    const extractArgs = [
   279→      '-y',
   280→      '-i', validatedPath,
   281→      '-ac', '1',
   282→      '-ar', '16000',
   283→      '-f', 's16le',
   284→      '-acodec', 'pcm_s16le',
   285→      tempFile
   286→    ];
   287→    await safeFFmpeg(extractArgs);
   288→
   289→    // PERFORMANCE: Use async file read to avoid blocking event loop
   290→    const rawData = await fsPromises.readFile(tempFile);
   291→    const samples = new Float32Array(rawData.length / 2);
   292→    for (let i = 0; i < samples.length; i++) {
   293→      samples[i] = rawData.readInt16LE(i * 2) / 32768.0;
   294→    }
   295→
   296→    // Calculate RMS per window
   297→    const windowSamples = Math.floor((windowMs / 1000) * 16000);
   298→    const hopSamples = windowSamples; // Non-overlapping windows
   299→    const numWindows = Math.floor(samples.length / hopSamples);
   300→
   301→    const levels = new Float32Array(numWindows);
   302→    const times = new Float32Array(numWindows);
   303→
   304→    for (let i = 0; i < numWindows; i++) {
   305→      const start = i * hopSamples;
   306→      const end = Math.min(start + windowSamples, samples.length);
   307→
   308→      let sumSquares = 0;
   309→      for (let j = start; j < end; j++) {
   310→        sumSquares += samples[j] * samples[j];
   311→      }
   312→
   313→      const rms = Math.sqrt(sumSquares / (end - start));
   314→      const dbfs = rms > 0 ? 20 * Math.log10(rms) : -100;
   315→
   316→      levels[i] = Math.max(-100, dbfs);
   317→      times[i] = (start / 16000);
   318→    }
   319→
   320→    // PERFORMANCE: Use async file operations
   321→    await fsPromises.unlink(tempFile);
   322→
   323→    return { levels, times, duration, speakerName, sampleRate: 16000 };
   324→  } catch (err) {
   325→    // PERFORMANCE: Use async file operations for cleanup
   326→    try {
   327→      await fsPromises.access(tempFile);
   328→      await fsPromises.unlink(tempFile);
   329→    } catch {
   330→      // File doesn't exist
   331→    }
   332→    throw new Error(`Failed to extract track levels: ${err.message}`);
   333→  }
   334→}
   335→
   336→// =============================================================================
   337→// Level Processing
   338→// =============================================================================
   339→
   340→/**
   341→ * Clean up audio levels by subtracting noise floor
   342→ *
   343→ * @param {Array} trackLevels - Array of track level objects
   344→ * @param {Object} options - Cleanup options
   345→ * @returns {Array} Cleaned level arrays
   346→ */
   347→function cleanupAudioLevels(trackLevels, _options = {}) {
   348→  const numTracks = trackLevels.length;
   349→  const numSamples = trackLevels[0].levels.length;
   350→
   351→  // Calculate average level (noise floor estimate)
   352→  const averageLevels = new Float32Array(numSamples);
   353→  for (let i = 0; i < numSamples; i++) {
   354→    let sum = 0;
   355→    for (let t = 0; t < numTracks; t++) {
   356→      sum += trackLevels[t].levels[i];
   357→    }
   358→    averageLevels[i] = sum / numTracks;
   359→  }
   360→
   361→  // Subtract average from each track (relative loudness)
   362→  const cleaned = trackLevels.map(track => {
   363→    const cleanedLevels = new Float32Array(numSamples);
   364→    for (let i = 0; i < numSamples; i++) {
   365→      // Normalize to -100 to 0 range
   366→      cleanedLevels[i] = Math.max(-100, Math.min(0, track.levels[i] - averageLevels[i]));
   367→    }
   368→    return {
   369→      ...track,
   370→      levels: cleanedLevels
   371→    };
   372→  });
   373→
   374→  return cleaned;
   375→}
   376→
   377→/**
   378→ * Apply per-speaker dB boosts
   379→ *
   380→ * @param {Array} trackLevels - Cleaned track levels
   381→ * @param {Object} boosts - Speaker boost mapping { "Speaker 1": 5, ... }
   382→ * @returns {Array} Boosted levels
   383→ */
   384→function applySpeakerBoosts(trackLevels, boosts = {}) {
   385→  return trackLevels.map(track => {
   386→    const boost = boosts[track.speakerName] || 0;
   387→    if (boost === 0) return track;
   388→
   389→    const boostedLevels = new Float32Array(track.levels.length);
   390→    for (let i = 0; i < track.levels.length; i++) {
   391→      boostedLevels[i] = Math.max(-100, Math.min(0, track.levels[i] + boost));
   392→    }
   393→
   394→    return { ...track, levels: boostedLevels };
   395→  });
   396→}
   397→
   398→/**
   399→ * Apply Gaussian smoothing to reduce noise
   400→ *
   401→ * @param {Array} trackLevels - Track levels
   402→ * @param {number} windowSize - Gaussian kernel size
   403→ * @returns {Array} Smoothed levels
   404→ */
   405→function smoothAudioLevels(trackLevels, windowSize = 5) {
   406→  const kernel = generateGaussianKernel(windowSize);
   407→
   408→  return trackLevels.map(track => {
   409→    const smoothed = applyConvolution(track.levels, kernel);
   410→    return { ...track, levels: smoothed };
   411→  });
   412→}
   413→
   414→/**
   415→ * Generate Gaussian kernel
   416→ */
   417→function generateGaussianKernel(size) {
   418→  const sigma = size / 3;
   419→  const kernel = new Float32Array(size);
   420→  const half = Math.floor(size / 2);
   421→  let sum = 0;
   422→
   423→  for (let i = 0; i < size; i++) {
   424→    const x = i - half;
   425→    kernel[i] = Math.exp(-(x * x) / (2 * sigma * sigma));
   426→    sum += kernel[i];
   427→  }
   428→
   429→  // Normalize
   430→  for (let i = 0; i < size; i++) {
   431→    kernel[i] /= sum;
   432→  }
   433→
   434→  return kernel;
   435→}
   436→
   437→/**
   438→ * Apply 1D convolution
   439→ */
   440→function applyConvolution(data, kernel) {
   441→  const result = new Float32Array(data.length);
   442→  const half = Math.floor(kernel.length / 2);
   443→
   444→  for (let i = 0; i < data.length; i++) {
   445→    let sum = 0;
   446→    for (let j = 0; j < kernel.length; j++) {
   447→      const idx = i + j - half;
   448→      if (idx >= 0 && idx < data.length) {
   449→        sum += data[idx] * kernel[j];
   450→      }
   451→    }
   452→    result[i] = sum;
   453→  }
   454→
   455→  return result;
   456→}
   457→
   458→// =============================================================================
   459→// Switching Decision Generation
   460→// =============================================================================
   461→
   462→/**
   463→ * Generate video switching decisions based on audio levels
   464→ *
   465→ * @param {Array} trackLevels - Processed track levels
   466→ * @param {Object} options - Decision options
   467→ * @returns {Array} Switching decisions
   468→ */
   469→function generateSwitchingDecisions(trackLevels, options = {}) {
   470→  const {
   471→    videoTrackMapping = {},
   472→    minShotDuration = 2.0,
   473→    switchingFrequency = 50,
   474→    wideShotEnabled = true,
   475→    wideShotTolerance = 6,
   476→    wideShotTracks = []
   477→    // frameRate reserved for future frame-aligned cutting
   478→  } = options;
   479→
   480→  const numSamples = trackLevels[0].levels.length;
   481→  const times = trackLevels[0].times;
   482→  const decisions = [];
   483→
   484→  // Calculate min samples between cuts based on switching frequency
   485→  const minSamplesPerCut = Math.ceil(minShotDuration / (times[1] - times[0]));
   486→  const switchingCooldown = Math.ceil(minSamplesPerCut * (1 - switchingFrequency / 100));
   487→
   488→  let currentSpeaker = -1;
   489→  // currentDecisionStart reserved for future decision grouping
   490→  let lastSwitchSample = 0;
   491→
   492→  for (let i = 0; i < numSamples; i++) {
   493→    // Find loudest speaker
   494→    let maxLevel = -100;
   495→    let loudestSpeaker = 0;
   496→    let speakersAboveThreshold = [];
   497→
   498→    for (let t = 0; t < trackLevels.length; t++) {
   499→      const level = trackLevels[t].levels[i];
   500→      if (level > maxLevel) {
   501→        maxLevel = level;
   502→        loudestSpeaker = t;
   503→      }
   504→
   505→      // Track speakers within tolerance of max (for wide shot detection)
   506→      if (wideShotEnabled && level >= maxLevel - wideShotTolerance) {
   507→        speakersAboveThreshold.push(t);
   508→      }
   509→    }
   510→
   511→    // Determine if this should be a wide shot
   512→    const isWideShot = wideShotEnabled && speakersAboveThreshold.length >= 2;
   513→    const effectiveSpeaker = isWideShot ? -1 : loudestSpeaker; // -1 = wide shot
   514→
   515→    // Check if we should switch
   516→    const canSwitch = (i - lastSwitchSample) >= switchingCooldown;
   517→    const shouldSwitch = canSwitch && effectiveSpeaker !== currentSpeaker;
   518→
   519→    if (shouldSwitch || i === 0) {
   520→      // Close previous decision
   521→      if (i > 0 && decisions.length > 0) {
   522→        decisions[decisions.length - 1].endTime = times[i];
   523→        decisions[decisions.length - 1].endSample = i;
   524→      }
   525→
   526→      // Start new decision
   527→      const speakerName = isWideShot
   528→        ? 'Wide Shot'
   529→        : trackLevels[loudestSpeaker].speakerName;
   530→
   531→      const videoTrack = isWideShot
   532→        ? (wideShotTracks[0] ?? 0)
   533→        : (videoTrackMapping[loudestSpeaker] ?? loudestSpeaker);
   534→
   535→      decisions.push({
   536→        speakerIndex: effectiveSpeaker,
   537→        speakerName,
   538→        speakers: speakersAboveThreshold.map(s => trackLevels[s].speakerName),
   539→        videoTrack,
   540→        startTime: times[i],
   541→        startSample: i,
   542→        endTime: times[numSamples - 1],
   543→        endSample: numSamples - 1,
   544→        isWideShot,
   545→        reason: isWideShot ? 'Multiple speakers' : 'Loudest speaker'
   546→      });
   547→
   548→      currentSpeaker = effectiveSpeaker;
   549→      lastSwitchSample = i;
   550→    }
   551→  }
   552→
   553→  // Finalize last decision
   554→  if (decisions.length > 0) {
   555→    decisions[decisions.length - 1].endTime = times[numSamples - 1];
   556→    decisions[decisions.length - 1].endSample = numSamples - 1;
   557→  }
   558→
   559→  // Add duration to each decision
   560→  return decisions.map(d => ({
   561→    ...d,
   562→    duration: parseFloat((d.endTime - d.startTime).toFixed(3))
   563→  }));
   564→}
   565→
   566→// =============================================================================
   567→// Wide Shot Tuning
   568→// =============================================================================
   569→
   570→/**
   571→ * Fine-tune wide shot percentage using tolerance adjustment
   572→ *
   573→ * @param {Array} decisions - Initial decisions
   574→ * @param {number} targetPercentage - Target wide shot percentage
   575→ * @returns {Array} Tuned decisions
   576→ */
   577→function tuneWideShotPercentage(decisions, targetPercentage) {
   578→  const totalDuration = decisions.reduce((sum, d) => sum + d.duration, 0);
   579→  const currentWideTime = decisions
   580→    .filter(d => d.isWideShot)
   581→    .reduce((sum, d) => sum + d.duration, 0);
   582→  const currentPercentage = (currentWideTime / totalDuration) * 100;
   583→
   584→  // If within 5% of target, don't adjust
   585→  if (Math.abs(currentPercentage - targetPercentage) <= 5) {
   586→    return decisions;
   587→  }
   588→
   589→  console.log(`[SPLICE Multitrack] Wide shot: ${currentPercentage.toFixed(1)}% (target: ${targetPercentage}%)`);
   590→
   591→  // Simple approach: mark some single-speaker decisions as wide shots
   592→  // or vice versa based on whether we need more or fewer wide shots
   593→  // (Full implementation would re-run with adjusted tolerance)
   594→
   595→  return decisions; // Return as-is for now
   596→}
   597→
   598→// =============================================================================
   599→// Cutaway Insertion
   600→// =============================================================================
   601→
   602→/**
   603→ * Insert cutaways into long clips
   604→ *
   605→ * @param {Array} decisions - Switching decisions
   606→ * @param {Object} options - Cutaway options
   607→ * @returns {Array} Decisions with cutaways inserted
   608→ */
   609→function insertCutaways(decisions, options = {}) {
   610→  const {
   611→    cutawayTracks = [],
   612→    minDuration = 1.5,
   613→    maxDuration = 4.0,
   614→    maxClipDuration = 30 // Insert cutaway if clip longer than this
   615→  } = options;
   616→
   617→  if (cutawayTracks.length === 0) return decisions;
   618→
   619→  const result = [];
   620→
   621→  for (const decision of decisions) {
   622→    if (decision.duration <= maxClipDuration) {
   623→      result.push(decision);
   624→      continue;
   625→    }
   626→
   627→    // Split long clip with cutaways
   628→    const numCutaways = Math.floor(decision.duration / maxClipDuration);
   629→    const interval = decision.duration / (numCutaways + 1);
   630→
   631→    let currentStart = decision.startTime;
   632→
   633→    for (let i = 0; i < numCutaways; i++) {
   634→      // Add main clip segment
   635→      const cutawayStart = currentStart + interval - minDuration / 2;
   636→      const cutawayDuration = minDuration + Math.random() * (maxDuration - minDuration);
   637→      const cutawayEnd = cutawayStart + cutawayDuration;
   638→
   639→      result.push({
   640→        ...decision,
   641→        startTime: currentStart,
   642→        endTime: cutawayStart,
   643→        duration: cutawayStart - currentStart
   644→      });
   645→
   646→      // Add cutaway
   647→      const cutawayTrack = cutawayTracks[i % cutawayTracks.length];
   648→      result.push({
   649→        speakerIndex: -2, // Special index for cutaway
   650→        speakerName: 'Cutaway',
   651→        speakers: [],
   652→        videoTrack: cutawayTrack,
   653→        startTime: cutawayStart,
   654→        endTime: cutawayEnd,
   655→        duration: cutawayDuration,
   656→        isWideShot: false,
   657→        isCutaway: true,
   658→        reason: 'Cutaway insert'
   659→      });
   660→
   661→      currentStart = cutawayEnd;
   662→    }
   663→
   664→    // Add final segment
   665→    result.push({
   666→      ...decision,
   667→      startTime: currentStart,
   668→      endTime: decision.endTime,
   669→      duration: decision.endTime - currentStart
   670→    });
   671→  }
   672→
   673→  return result;
   674→}
   675→
   676→// =============================================================================
   677→// Statistics
   678→// =============================================================================
   679→
   680→/**
   681→ * Calculate multitrack analysis statistics
   682→ *
   683→ * @param {Array} decisions - Final decisions
   684→ * @param {number} totalDuration - Total audio duration
   685→ * @returns {Object} Statistics
   686→ */
   687→function calculateMultitrackStats(decisions, totalDuration) {
   688→  const speakerTimes = {};
   689→  let wideShotTime = 0;
   690→  let cutawayTime = 0;
   691→
   692→  for (const d of decisions) {
   693→    if (d.isCutaway) {
   694→      cutawayTime += d.duration;
   695→    } else if (d.isWideShot) {
   696→      wideShotTime += d.duration;
   697→    } else {
   698→      speakerTimes[d.speakerName] = (speakerTimes[d.speakerName] || 0) + d.duration;
   699→    }
   700→  }
   701→
   702→  const speakerPercentages = {};
   703→  for (const [speaker, time] of Object.entries(speakerTimes)) {
   704→    speakerPercentages[speaker] = parseFloat(((time / totalDuration) * 100).toFixed(1));
   705→  }
   706→
   707→  return {
   708→    decisionCount: decisions.length,
   709→    wideShotPercentage: parseFloat(((wideShotTime / totalDuration) * 100).toFixed(1)),
   710→    cutawayPercentage: parseFloat(((cutawayTime / totalDuration) * 100).toFixed(1)),
   711→    speakerPercentages,
   712→    averageShotDuration: parseFloat((totalDuration / decisions.length).toFixed(2))
   713→  };
   714→}
   715→
   716→// =============================================================================
   717→// Auto-Balance Optimizer
   718→// =============================================================================
   719→
   720→/**
   721→ * Auto-balance speaker screentime
   722→ * Adjusts speaker boosts to achieve equal distribution
   723→ * PERF-008: Caches audio levels to avoid re-extraction on each iteration
   724→ *
   725→ * @param {Array<string>} audioPaths - Audio file paths
   726→ * @param {Object} options - Base analysis options
   727→ * @returns {Promise<Object>} Optimized analysis with best parameters
   728→ */
   729→async function autoBalanceMultitrack(audioPaths, options = {}) {
   730→  const opts = { ...DEFAULT_OPTIONS, ...options };
   731→  const numSpeakers = audioPaths.length;
   732→  const targetShare = 100 / numSpeakers;
   733→
   734→  console.log(`[SPLICE Multitrack] Auto-balancing for ${numSpeakers} speakers (target: ${targetShare.toFixed(1)}% each)`);
   735→
   736→  // PERF-008: Extract levels once and cache them (avoids re-running FFmpeg)
