    54→    throw new Error(`Invalid audio path: ${pathValidation.error}`);
    55→  }
    56→  const validatedPath = pathValidation.path;
    57→
    58→  console.log(`[SPLICE RMS] Analyzing: ${validatedPath}`);
    59→  console.log(`[SPLICE RMS] Options: threshold=${opts.threshold}dB, minLength=${opts.minSilenceLength}s, seekStep=${opts.seekStep}s`);
    60→
    61→  // Check if we should use chunked processing (for files longer than 45 seconds)
    62→  if (opts.chunkingEnabled !== false) {
    63→    const duration = await getAudioDuration(validatedPath);
    64→    if (duration > 45) {
    65→      console.log(`[SPLICE RMS] Long file detected (${duration}s), using chunked processing`);
    66→      return detectSilencesRMSChunked(validatedPath, options);
    67→    }
    68→  }
    69→
    70→  // Step 1: Extract raw audio data using FFmpeg
    71→  const audioData = await extractAudioData(validatedPath);
    72→
    73→  // Step 2: Calculate RMS values for sliding windows
    74→  const rmsValues = calculateRMSWindows(audioData.samples, {
    75→    windowSize: Math.floor(opts.seekStep * audioData.sampleRate),
    76→    hopSize: Math.floor(opts.seekStep * audioData.sampleRate / 2)
    77→  });
    78→
    79→  // Step 3: Convert to dBFS
    80→  const dBFSValues = rmsValues.map(rms => rmsToDBFS(rms));
    81→
    82→  // Step 4: Determine threshold (auto-detect or use provided)
    83→  let threshold = opts.threshold;
    84→  let thresholdInfo = { method: 'manual', value: threshold };
    85→
    86→  if (opts.autoThreshold) {
    87→    const autoResult = autoDetectThreshold(dBFSValues);
    88→    threshold = autoResult.threshold;
    89→    thresholdInfo = autoResult;
    90→    console.log(`[SPLICE RMS] Auto-detected threshold: ${threshold.toFixed(1)}dB (${autoResult.method})`);
    91→  }
    92→
    93→  // Step 5: Find silence regions
    94→  const rawSilences = findSilenceRegions(dBFSValues, {
    95→    threshold,
    96→    seekStep: opts.seekStep,
    97→    minSilenceLength: opts.minSilenceLength
    98→  });
    99→
   100→  // Step 6: Merge adjacent silences
   101→  const mergedSilences = mergeSilences(rawSilences, opts.mergeDistance);
   102→
   103→  // Step 7: Apply padding
   104→  const paddedSilences = applyPadding(mergedSilences, {
   105→    paddingStart: opts.paddingStart,
   106→    paddingEnd: opts.paddingEnd,
   107→    audioDuration: audioData.duration
   108→  });
   109→
   110→  // Calculate statistics
   111→  const totalSilenceDuration = paddedSilences.reduce((sum, s) => sum + s.duration, 0);
   112→  const speechDuration = audioData.duration - totalSilenceDuration;
   113→
   114→  console.log(`[SPLICE RMS] Detected ${paddedSilences.length} silence(s), total ${totalSilenceDuration.toFixed(2)}s`);
   115→
   116→  return {
   117→    silences: paddedSilences,
   118→    metadata: {
   119→      audioDuration: audioData.duration,
   120→      sampleRate: audioData.sampleRate,
   121→      channels: audioData.channels,
   122→      silenceCount: paddedSilences.length,
   123→      totalSilenceDuration,
   124→      speechDuration,
   125→      silencePercentage: ((totalSilenceDuration / audioData.duration) * 100).toFixed(1),
   126→      threshold: thresholdInfo,
   127→      rmsAnalysis: {
   128→        windowCount: dBFSValues.length,
   129→        ...computeDBFSStats(dBFSValues)
   130→      }
   131→    }
   132→  };
   133→}
