   427→// =============================================================================
   428→// RMS Calculation
   429→// =============================================================================
   430→
   431→/**
   432→ * Calculate RMS values for sliding windows across audio samples
   433→ *
   434→ * @param {Float32Array} samples - Audio samples (-1 to 1)
   435→ * @param {Object} options - Window and hop size
   436→ * @returns {Float32Array} RMS values for each window
   437→ */
   438→function calculateRMSWindows(samples, options = {}) {
   439→  const { windowSize = 800, hopSize = 400 } = options; // Default ~50ms at 16kHz
   440→
   441→  const numWindows = Math.floor((samples.length - windowSize) / hopSize) + 1;
   442→  const rmsValues = new Float32Array(numWindows);
   443→
   444→  for (let i = 0; i < numWindows; i++) {
   445→    const start = i * hopSize;
   446→    const end = Math.min(start + windowSize, samples.length);
   447→
   448→    // Calculate RMS for this window
   449→    let sumSquares = 0;
   450→    for (let j = start; j < end; j++) {
   451→      sumSquares += samples[j] * samples[j];
   452→    }
   453→
   454→    const rms = Math.sqrt(sumSquares / (end - start));
   455→    rmsValues[i] = rms;
   456→  }
   457→
   458→  return rmsValues;
   459→}
   460→
   461→/**
   462→ * Convert RMS value to dBFS (decibels relative to full scale)
   463→ *
   464→ * @param {number} rms - RMS value (0 to 1)
   465→ * @returns {number} dBFS value (negative, -inf for silence)
   466→ */
   467→function rmsToDBFS(rms) {
   468→  if (rms <= 0) return -100; // Effective silence floor
   469→  const dbfs = 20 * Math.log10(rms);
   470→  return Math.max(-100, dbfs); // Clamp to reasonable floor
   471→}
   472→
   473→/**
   474→ * Compute min, max, avg stats without using spread operator
   475→ * Avoids call stack overflow for large arrays (100k+ elements)
   476→ *
   477→ * @param {Array<number>|Float32Array} values - dBFS values
   478→ * @returns {Object} Stats object with minDB, maxDB, avgDB
   479→ */
   480→function computeDBFSStats(values) {
   481→  if (!values || values.length === 0) {
   482→    return { minDB: '-100.0', maxDB: '-100.0', avgDB: '-100.0' };
   483→  }
   484→
   485→  let minDB = Infinity;
   486→  let maxDB = -Infinity;
   487→  let sum = 0;
   488→
   489→  for (let i = 0; i < values.length; i++) {
   490→    const v = values[i];
   491→    if (v < minDB) minDB = v;
   492→    if (v > maxDB) maxDB = v;
   493→    sum += v;
   494→  }
   495→
   496→  return {
   497→    minDB: minDB.toFixed(1),
   498→    maxDB: maxDB.toFixed(1),
   499→    avgDB: (sum / values.length).toFixed(1)
   500→  };
   501→}
   502→
   503→// =============================================================================
   504→// Auto-Threshold Detection
   505→// =============================================================================
   506→
   507→/**
   508→ * Auto-detect optimal silence threshold from audio histogram
   509→ * Uses Gaussian blur + local maxima detection
   510→ *
   511→ * @param {Float32Array} dBFSValues - Array of dBFS values
   512→ * @returns {Object} Detected threshold and method info
   513→ */
   514→function autoDetectThreshold(dBFSValues) {
   515→  // Build histogram of dBFS values
   516→  const histBins = 100;
   517→  const minDB = -80;
   518→  const maxDB = 0;
   519→  const binWidth = (maxDB - minDB) / histBins;
   520→
   521→  const histogram = new Array(histBins).fill(0);
   522→
   523→  for (const db of dBFSValues) {
   524→    const binIndex = Math.min(histBins - 1, Math.max(0, Math.floor((db - minDB) / binWidth)));
   525→    histogram[binIndex]++;
   526→  }
   527→
   528→  // Apply Gaussian blur to smooth histogram
   529→  const smoothed = gaussianBlur(histogram, 5);
   530→
   531→  // Find local maxima (peaks)
   532→  const peaks = findLocalMaxima(smoothed, 3);
   533→
   534→  // Sort peaks by value (highest first)
   535→  peaks.sort((a, b) => b.value - a.value);
   536→
   537→  if (peaks.length >= 2) {
   538→    // Interpolate between the two highest peaks
   539→    // Assume: higher peak = speech, lower peak = silence
   540→    const peak1DB = minDB + peaks[0].index * binWidth;
   541→    const peak2DB = minDB + peaks[1].index * binWidth;
   542→
   543→    const silencePeakDB = Math.min(peak1DB, peak2DB);
   544→    const speechPeakDB = Math.max(peak1DB, peak2DB);
   545→
   546→    // Set threshold between peaks (closer to silence peak)
   547→    const threshold = silencePeakDB + (speechPeakDB - silencePeakDB) * 0.3;
   548→
   549→    return {
   550→      threshold: Math.max(-60, Math.min(-20, threshold)),
   551→      method: 'dual-peak',
   552→      silencePeak: silencePeakDB.toFixed(1),
   553→      speechPeak: speechPeakDB.toFixed(1)
   554→    };
   555→  } else if (peaks.length === 1) {
   556→    // Single peak - use percentile-based fallback
   557→    const sorted = [...dBFSValues].sort((a, b) => a - b);
   558→    const percentile25 = sorted[Math.floor(sorted.length * 0.25)];
   559→
   560→    return {
   561→      threshold: Math.max(-60, Math.min(-20, percentile25)),
   562→      method: 'percentile-25',
   563→      value: percentile25.toFixed(1)
   564→    };
   565→  } else {
   566→    // No peaks found - use conservative default
   567→    return {
   568→      threshold: -35,
   569→      method: 'default-fallback'
   570→    };
   571→  }
   572→}
   573→
   574→/**
   575→ * Apply Gaussian blur to an array
   576→ *
   577→ * @param {Array<number>} arr - Input array
   578→ * @param {number} sigma - Standard deviation
   579→ * @returns {Array<number>} Smoothed array
   580→ */
   581→function gaussianBlur(arr, sigma = 5) {
   582→  const kernelSize = Math.ceil(sigma * 3) * 2 + 1;
   583→  const kernel = [];
   584→  const halfSize = Math.floor(kernelSize / 2);
   585→
   586→  // Generate Gaussian kernel
   587→  let sum = 0;
   588→  for (let i = 0; i < kernelSize; i++) {
   589→    const x = i - halfSize;
   590→    const g = Math.exp(-(x * x) / (2 * sigma * sigma));
   591→    kernel.push(g);
   592→    sum += g;
   593→  }
   594→
   595→  // Normalize kernel
   596→  for (let i = 0; i < kernelSize; i++) {
   597→    kernel[i] /= sum;
   598→  }
   599→
   600→  // Apply convolution
   601→  const result = new Array(arr.length).fill(0);
   602→  for (let i = 0; i < arr.length; i++) {
   603→    let value = 0;
   604→    for (let j = 0; j < kernelSize; j++) {
   605→      const idx = i + j - halfSize;
   606→      if (idx >= 0 && idx < arr.length) {
   607→        value += arr[idx] * kernel[j];
   608→      }
   609→    }
   610→    result[i] = value;
   611→  }
   612→
   613→  return result;
   614→}
   615→
   616→/**
   617→ * Find local maxima in an array
   618→ *
   619→ * @param {Array<number>} arr - Input array
   620→ * @param {number} windowSize - Neighborhood size for local maximum detection
   621→ * @returns {Array<{index: number, value: number}>} Array of peak positions and values
   622→ */
   623→function findLocalMaxima(arr, windowSize = 3) {
   624→  const peaks = [];
   625→  const halfWindow = Math.floor(windowSize / 2);
   626→
   627→  for (let i = halfWindow; i < arr.length - halfWindow; i++) {
   628→    let isMax = true;
   629→    for (let j = i - halfWindow; j <= i + halfWindow; j++) {
   630→      if (j !== i && arr[j] >= arr[i]) {
   631→        isMax = false;
   632→        break;
   633→      }
   634→    }
   635→    if (isMax && arr[i] > 0) {
   636→      peaks.push({ index: i, value: arr[i] });
   637→    }
   638→  }
   639→
   640→  return peaks;
   641→}
   642→
   643→// =============================================================================
   644→// Silence Region Detection
   645→// =============================================================================
   646→
   647→/**
   648→ * Find silence regions based on dBFS threshold
   649→ *
   650→ * @param {Float32Array} dBFSValues - Array of dBFS values
   651→ * @param {Object} options - Detection options
   652→ * @returns {Array<{start: number, end: number, duration: number}>} Silence regions
   653→ */
   654→function findSilenceRegions(dBFSValues, options) {
   655→  const { threshold, seekStep, minSilenceLength } = options;
   656→
   657→  const silences = [];
   658→  let silenceStart = null;
   659→
   660→  for (let i = 0; i < dBFSValues.length; i++) {
   661→    const time = i * seekStep;
   662→    const isSilent = dBFSValues[i] < threshold;
   663→
   664→    if (isSilent && silenceStart === null) {
   665→      // Start of silence
   666→      silenceStart = time;
   667→    } else if (!isSilent && silenceStart !== null) {
   668→      // End of silence
   669→      const duration = time - silenceStart;
   670→      if (duration >= minSilenceLength) {
   671→        silences.push({
   672→          start: silenceStart,
   673→          end: time,
   674→          duration
   675→        });
   676→      }
   677→      silenceStart = null;
   678→    }
   679→  }
   680→
   681→  // Handle silence at end of audio
   682→  if (silenceStart !== null) {
   683→    const endTime = dBFSValues.length * seekStep;
   684→    const duration = endTime - silenceStart;
   685→    if (duration >= minSilenceLength) {
   686→      silences.push({
   687→        start: silenceStart,
   688→        end: endTime,
   689→        duration
   690→      });
   691→    }
   692→  }
   693→
   694→  return silences;
   695→}
   696→
   697→// =============================================================================
   698→// Post-Processing
   699→// =============================================================================
   700→
   701→/**
   702→ * Merge silences that are close together
   703→ *
   704→ * @param {Array} silences - Array of silence regions
   705→ * @param {number} mergeDistance - Maximum gap to merge (seconds)
   706→ * @returns {Array} Merged silence regions
   707→ */
   708→function mergeSilences(silences, mergeDistance) {
   709→  if (silences.length <= 1) return silences;
   710→
   711→  const merged = [];
   712→  let current = { ...silences[0] };
   713→
   714→  for (let i = 1; i < silences.length; i++) {
   715→    const next = silences[i];
   716→
   717→    // If gap is small enough, merge
   718→    if (next.start - current.end <= mergeDistance) {
   719→      current.end = next.end;
   720→      current.duration = current.end - current.start;
   721→    } else {
   722→      merged.push(current);
   723→      current = { ...next };
   724→    }
   725→  }
   726→
   727→  merged.push(current);
   728→  return merged;
   729→}
   730→
   731→/**
   732→ * Apply padding to silence boundaries
   733→ *
   734→ * @param {Array} silences - Array of silence regions
   735→ * @param {Object} options - Padding options
   736→ * @returns {Array} Padded silence regions
   737→ */
   738→function applyPadding(silences, options) {
   739→  const { paddingStart, paddingEnd, audioDuration } = options;
   740→
   741→  return silences
   742→    .map(silence => {
   743→      const paddedStart = Math.max(0, silence.start + paddingStart);
   744→      const paddedEnd = Math.min(audioDuration, silence.end - paddingEnd);
   745→      const duration = paddedEnd - paddedStart;
   746→
   747→      // Skip if padding made the silence invalid
   748→      if (duration <= 0) return null;
   749→
   750→      return {
   751→        start: parseFloat(paddedStart.toFixed(3)),
   752→        end: parseFloat(paddedEnd.toFixed(3)),
   753→        duration: parseFloat(duration.toFixed(3))
   754→      };
   755→    })
   756→    .filter(s => s !== null);
   757→}
   758→
   759→// =============================================================================
   760→// Sensitivity Mapping
   761→// =============================================================================
   762→
   763→/**
   764→ * Map UI sensitivity (0-100) to detection parameters
   765→ * Based on Fireside's aggressiveness mapping
   766→ *
   767→ * @param {number} sensitivity - UI sensitivity value (0-100)
   768→ * @returns {Object} Detection parameters
   769→ */
   770→function sensitivityToParams(sensitivity) {
   771→  // Clamp to 0-100
   772→  const s = Math.max(0, Math.min(100, sensitivity));
   773→
   774→  // Linear interpolation helper
   775→  const lerp = (min, max, t) => min + (max - min) * (t / 100);
   776→
   777→  return {
   778→    // Threshold: -50dB (conservative) to -20dB (aggressive)
   779→    threshold: lerp(-50, -20, s),
   780→
   781→    // Min silence length: 2.0s (conservative) to 0.3s (aggressive)
   782→    minSilenceLength: lerp(2.0, 0.3, s),
   783→
   784→    // Padding start: 0.2s (conservative) to 0.05s (aggressive)
   785→    paddingStart: lerp(0.2, 0.05, s),
   786→
   787→    // Padding end: 0.15s (conservative) to 0.03s (aggressive)
   788→    paddingEnd: lerp(0.15, 0.03, s),
   789→
   790→    // Seek step stays constant
   791→    seekStep: 0.05
   792→  };
   793→}
   794→
   795→// =============================================================================
   796→// Exports
   797→// =============================================================================
   798→
   799→/**
   800→ * Get waveform data for visualization
   801→ * Returns downsampled RMS values suitable for canvas rendering
   802→ *
   803→ * @param {string} wavPath - Path to WAV file
   804→ * @param {Object} options - Options
   805→ * @returns {Promise<Object>} Waveform data with values and metadata
   806→ */
   807→async function getWaveformData(wavPath, options = {}) {
   808→  const {
   809→    targetPoints = 400,   // Number of points for visualization
   810→    windowMs = 50         // RMS window size
   811→  } = options;
   812→
   813→  const fs = require('fs');
   814→  const path = require('path');
   815→
   816→  if (!fs.existsSync(wavPath)) {
   817→    throw new Error(`File not found: ${wavPath}`);
   818→  }
   819→
   820→  // Read audio file
   821→  const buffer = fs.readFileSync(wavPath);
   822→  const audioData = parseWavBuffer(buffer);
   823→
   824→  // Calculate RMS values
   825→  const rmsValues = calculateRMSWindows(audioData.samples, {
   826→    sampleRate: audioData.sampleRate,
   827→    windowMs
   828→  });
   829→
   830→  // Downsample to target points
   831→  const step = Math.max(1, Math.floor(rmsValues.length / targetPoints));
   832→  const downsampled = [];
   833→
   834→  for (let i = 0; i < rmsValues.length; i += step) {
   835→    // Take max value in this chunk for peak display
   836→    let maxVal = 0;
   837→    for (let j = i; j < Math.min(i + step, rmsValues.length); j++) {
   838→      if (rmsValues[j] > maxVal) maxVal = rmsValues[j];
   839→    }
   840→    downsampled.push(maxVal);
   841→  }
   842→
   843→  // Normalize to 0-1 range
   844→  const maxRMS = Math.max(...downsampled, 0.001);
   845→  const normalized = downsampled.map(v => v / maxRMS);
   846→
   847→  const duration = audioData.samples.length / audioData.sampleRate;
   848→
   849→  return {
   850→    waveform: normalized,
   851→    pointCount: normalized.length,
   852→    duration,
   853→    sampleRate: audioData.sampleRate,
   854→    maxRMS,
   855→    fileName: path.basename(wavPath)
   856→  };
   857→}
   858→
   859→/**
   860→ * Parse WAV buffer to extract audio samples
