   887→    } else if (bitsPerSample === 32) {
   888→      sample = buffer.readFloatLE(i);
   889→    }
   890→    samples.push(sample);
   891→  }
   892→
   893→  return { samples, sampleRate, bitsPerSample, numChannels };
   894→}
   895→
   896→module.exports = {
   897→  detectSilencesRMS,
   898→  detectSilencesRMSChunked,
   899→  sensitivityToParams,
   900→  autoDetectThreshold,
   901→  getAudioDuration,
