   104→/**
   105→ * Find phrase occurrences using sliding window comparison
   106→ *
   107→ * @param {Array<string>} normalizedWords - Normalized word strings
   108→ * @param {Object} options - Search options
   109→ * @returns {Array} Groups of similar phrases
   110→ */
   111→function findPhraseOccurrences(normalizedWords, options) {
   112→  const { phraseSize, searchRadius, tolerance, minOccurrences } = options;
   113→  const groups = [];
   114→  const usedIndices = new Set();
   115→
   116→  for (let i = 0; i <= normalizedWords.length - phraseSize; i++) {
   117→    if (usedIndices.has(i)) continue;
