   292→/**
   293→ * Detect single-word stutters (e.g., "I I I think")
   294→ *
   295→ * @param {Object} transcript - Transcript with words array
   296→ * @param {Object} options - Detection options
   297→ * @returns {Object} Stutter detection results
   298→ */
   299→function detectStutters(transcript, options = {}) {
   300→  const {
   301→    minRepeats = 2,      // Minimum times word must repeat
   302→    maxGapMs = 500,      // Max gap between words (milliseconds)
   303→    ignoreFillers = true, // Ignore common filler words
   304→    minWordLength = 1    // Minimum word length (1 allows "I", "a", etc.)
   305→  } = options;
   306→
   307→  const words = transcript.words || [];
   308→  const stutters = [];
