   313→  let i = 0;
   314→  while (i < words.length) {
   315→    const word = words[i];
   316→    const normalized = normalizeWord(word.word || word.text || '');
   317→
   318→    // Skip fillers if configured
   319→    if (ignoreFillers && fillerWords.has(normalized)) {
