    71→                            isolationHoursRemaining: data.isolationHoursRemaining || 0,
    72→                            isolationHoursTotal: data.isolationHoursTotal || 0,
    73→                            hasIsolationAccess: data.hasIsolationAccess || false,
    74→                            isolationOverageRate: data.isolationOverageRate || 0.08,
    75→                            featureAccess: data.featureAccess || [],
