   150→    // Handle error state - show error badge with retry
   151→    if (credits._error) {
   152→        currentCredits = null;
   153→        creditBadge.style.display = 'flex';
   154→        creditBadge.textContent = 'Retry';
   155→        creditBadge.title = `Error: ${credits.message}\nClick to retry`;
