   166→    const referralPromo = document.getElementById('referralPromo');
   167→    if (referralPromo) {
   168→        referralPromo.classList.remove('hidden');
   169→    }
   170→
   171→    // Show trial status if on trial
   172→    if (credits.isOnTrial && credits.trialDaysRemaining) {
   173→        creditBadge.textContent = `Trial: ${credits.trialDaysRemaining}d`;
   174→        creditBadge.classList.remove('error', 'login', 'ok', 'low');
   175→        creditBadge.classList.add('trial');
   176→    } else {
   177→        creditBadge.textContent = `${credits.hoursRemaining.toFixed(1)} hrs`;
   178→        creditBadge.classList.remove('error', 'login', 'trial');
   179→    }
   180→
   181→    // Build tooltip with isolation info if available
   182→    let tooltip = `${credits.tierName}: ${credits.hoursRemaining.toFixed(1)} / ${credits.hoursTotal} hours`;
   183→    if (credits.isOnTrial && credits.trialDaysRemaining) {
   184→        tooltip = `Trial: ${credits.trialDaysRemaining} days remaining\n` + tooltip;
   185→    }
   186→    if (credits.hasIsolationAccess) {
   187→        tooltip += `\nIsolation: ${(credits.isolationHoursRemaining * 60).toFixed(0)} / ${(credits.isolationHoursTotal * 60).toFixed(0)} min`;
   188→    }
   189→    creditBadge.title = tooltip;
   190→
   191→    // Color based on remaining hours
   192→    if (credits.hoursRemaining <= 1) {
   193→        creditBadge.classList.add('low');
   194→        creditBadge.classList.remove('ok');
   195→    } else {
   196→        creditBadge.classList.add('ok');
   197→        creditBadge.classList.remove('low');
   198→    }
   199→
   200→    // Update isolation checkbox state based on tier access
   201→    updateIsolationCheckboxState(credits);
   202→}
