   204→/**
   205→ * Update the isolation checkbox based on tier access
   206→ */
   207→function updateIsolationCheckboxState(credits) {
   208→    const isolatedCheckbox = document.getElementById('sourceIsolated');
   209→    const tierBadge = isolatedCheckbox?.parentElement?.querySelector('.tier-badge');
   210→
   211→    if (!isolatedCheckbox) return;
   212→
   213→    if (!credits || !credits.hasIsolationAccess) {
   214→        // Disable isolation for non-Pro/Team users
   215→        isolatedCheckbox.disabled = true;
   216→        isolatedCheckbox.checked = false;
   217→        if (tierBadge) {
   218→            tierBadge.textContent = 'Pro+';
   219→            tierBadge.style.opacity = '1';
   220→        }
   221→    } else {
   222→        // Enable isolation for Pro/Team users
   223→        isolatedCheckbox.disabled = false;
   224→        if (tierBadge) {
   225→            // Show remaining isolation time
   226→            const minsRemaining = (credits.isolationHoursRemaining * 60).toFixed(0);
   227→            tierBadge.textContent = `${minsRemaining} min`;
   228→            tierBadge.style.opacity = credits.isolationHoursRemaining > 0 ? '1' : '0.6';
   229→        }
   230→    }
   231→}
   232→
   233→/**
   234→ * Check if user can use isolation and get cost estimate
   235→ * @param {number} estimatedMinutes - Estimated audio duration in minutes
   236→ * @returns {Object} Access info with allowed, message, overageCost
   237→ */
   238→function checkIsolationAccess(estimatedMinutes = 0) {
   239→    if (!currentCredits) {
   240→        return { allowed: false, message: 'Credits not loaded', overageCost: 0 };
   241→    }
   242→
   243→    if (!currentCredits.hasIsolationAccess) {
   244→        return {
   245→            allowed: false,
   246→            message: 'Vocal isolation requires Pro or Team tier',
   247→            upgradeRequired: true,
   248→            overageCost: 0
   249→        };
   250→    }
   251→
   252→    const estimatedHours = estimatedMinutes / 60;
   253→    const remaining = currentCredits.isolationHoursRemaining;
   254→
   255→    if (remaining >= estimatedHours) {
   256→        return {
   257→            allowed: true,
   258→            message: `Using ${estimatedMinutes.toFixed(1)} min of included isolation time`,
   259→            overageCost: 0
   260→        };
   261→    }
   262→
   263→    // Calculate overage
   264→    const overageMinutes = estimatedMinutes - (remaining * 60);
   265→    const overageCost = overageMinutes * currentCredits.isolationOverageRate;
   266→
   267→    return {
   268→        allowed: true,
   269→        message: `${remaining > 0 ? `${(remaining * 60).toFixed(0)} included + ` : ''}${overageMinutes.toFixed(0)} min overage ($${overageCost.toFixed(2)})`,
   270→        overageCost,
   271→        overageMinutes
   272→    };
   273→}
   274→
   275→/**
   276→ * Get current credits (for access checks)
   277→ */
   278→function getCurrentCredits() {
   279→    return currentCredits;
   280→}
   281→
   282→/**
   283→ * Check if user has access to a specific feature based on their tier
   284→ * @param {string} featureName - The feature to check (e.g., 'social_reframe', 'text_editing', 'multitrack')
   285→ * @returns {boolean} - True if user has access
   286→ */
   287→function hasFeatureAccess(featureName) {
   288→    if (!currentCredits || !currentCredits.featureAccess) {
   289→        return false; // Not logged in = no premium features
   290→    }
   291→    return currentCredits.featureAccess.includes(featureName);
   292→}
   293→
   294→/**
   295→ * Get the required tier for a feature
   296→ * @param {string} featureName - The feature name
   297→ * @returns {string} - Required tier name
   298→ */
   299→function getRequiredTier(featureName) {
   300→    const teamOnlyFeatures = ['multitrack', 'music_to_cut', 'mood_timeline', 'youtube_metadata', 'profanity_bleeping'];
   301→    const proFeatures = ['vocal_isolation', 'social_reframe', 'text_editing', 'all_captions', 'profanity_detection', 'chapter_detection'];
   302→
   303→    if (teamOnlyFeatures.includes(featureName)) {
   304→        return 'Team';
   305→    } else if (proFeatures.includes(featureName)) {
   306→        return 'Pro';
   307→    }
   308→    return 'Starter';
   309→}
   310→
   311→/**
   312→ * Initialize credits display
   313→ * Fetches credits on load and sets up periodic refresh
   314→ */
   315→async function initCredits() {
   316→    // Clear any existing interval to prevent duplicates
   317→    if (creditsRefreshInterval) {
   318→        clearInterval(creditsRefreshInterval);
   319→    }
   320→
   321→    // Initial fetch
   322→    const credits = await fetchCredits();
   323→    updateCreditDisplay(credits);
   324→
   325→    // Refresh credits every 5 minutes
   326→    creditsRefreshInterval = setInterval(async () => {
   327→        const refreshedCredits = await fetchCredits();
   328→        updateCreditDisplay(refreshedCredits);
   329→    }, 5 * 60 * 1000);
   330→}
   331→
   332→/**
   333→ * Cleanup credits module - call on plugin unload
   334→ */
   335→function cleanupCredits() {
   336→    if (creditsRefreshInterval) {
   337→        clearInterval(creditsRefreshInterval);
   338→        creditsRefreshInterval = null;
   339→    }
   340→}
   341→
   342→/**
   343→ * Manually refresh credits (e.g., after a processing operation)
   344→ */
   345→async function refreshCredits() {
   346→    const credits = await fetchCredits();
   347→    updateCreditDisplay(credits);
   348→    return credits;
   349→}
   350→
   351→/**
   352→ * Retry fetching credits with exponential backoff
   353→ * Called when user clicks the "Retry" badge
   354→ */
   355→async function retryFetchCredits() {
   356→    if (creditsRetryCount >= MAX_CREDITS_RETRIES) {
   357→        console.warn('[SPLICE] Max retry attempts reached for credits fetch');
   358→        updateCreditDisplay({
   359→            _error: true,
   360→            message: 'Max retries exceeded. Check your connection and try again later.'
   361→        });
   362→        creditsRetryCount = 0; // Reset for next attempt
   363→        return null;
   364→    }
   365→
   366→    creditsRetryCount++;
   367→    console.log(`[SPLICE] Retrying credits fetch (attempt ${creditsRetryCount}/${MAX_CREDITS_RETRIES})`);
   368→
   369→    // Exponential backoff: 1s, 2s, 4s
   370→    const delay = Math.pow(2, creditsRetryCount - 1) * 1000;
   371→
   372→    // Show retrying state
   373→    const creditBadge = document.getElementById('creditBadge');
   374→    if (creditBadge) {
   375→        creditBadge.textContent = `Retry ${creditsRetryCount}...`;
   376→        creditBadge.classList.add('retrying');
   377→    }
   378→
   379→    await new Promise(resolve => setTimeout(resolve, delay));
   380→
   381→    const credits = await fetchCredits();
   382→
   383→    if (creditBadge) {
   384→        creditBadge.classList.remove('retrying');
   385→    }
   386→
   387→    updateCreditDisplay(credits);
   388→    return credits;
   389→}
   390→
   391→/**
   392→ * Handle click on credit badge
   393→ * - If error state: retry fetching
   394→ * - If login state: open login modal
   395→ * - Otherwise: refresh credits
   396→ */
   397→function handleCreditBadgeClick() {
   398→    const creditBadge = document.getElementById('creditBadge');
   399→    if (!creditBadge) return;
   400→
   401→    if (creditBadge.classList.contains('error')) {
   402→        retryFetchCredits();
   403→    } else if (creditBadge.classList.contains('login')) {
   404→        // Open login modal (handled in main.js)
   405→        const loginModal = document.getElementById('loginModal');
   406→        if (loginModal) {
   407→            loginModal.classList.remove('hidden');
   408→        }
   409→    } else {
   410→        // Normal state - just refresh
   411→        refreshCredits();
   412→    }
   413→}
   414→
   415→// Export for window access
   416→window.fetchCredits = fetchCredits;
   417→window.clearCreditsCache = clearCreditsCache;
   418→window.updateCreditDisplay = updateCreditDisplay;
   419→window.checkIsolationAccess = checkIsolationAccess;
   420→window.getCurrentCredits = getCurrentCredits;
   421→window.hasFeatureAccess = hasFeatureAccess;
   422→window.getRequiredTier = getRequiredTier;
   423→window.initCredits = initCredits;
   424→window.cleanupCredits = cleanupCredits;
   425→window.refreshCredits = refreshCredits;
   426→window.retryFetchCredits = retryFetchCredits;
   427→window.handleCreditBadgeClick = handleCreditBadgeClick;
   428→
