   505→/**
   506→ * Encrypt sensitive fields in settings object
   507→ *
   508→ * @param {Object} settings - Settings object with plaintext sensitive fields
   509→ * @returns {Object} Settings with encrypted sensitive fields
   510→ */
   511→function encryptSensitiveFields(settings) {
   512→    const encrypted = { ...settings };
   513→
   514→    for (const field of SENSITIVE_FIELDS) {
   515→        if (encrypted[field] !== null && encrypted[field] !== undefined) {
   516→            // Only encrypt if not already encrypted
   517→            if (!isEncryptedValue(encrypted[field])) {
   518→                encrypted[field] = encryptForStorage(encrypted[field]);
   519→                debugLog(`[Config] Encrypted field: ${field}`);
   520→            }
   521→        }
   522→    }
   523→
   524→    return encrypted;
   525→}
   526→
   527→/**
   528→ * Decrypt sensitive fields in settings object
   529→ *
   530→ * @param {Object} settings - Settings object with encrypted sensitive fields
   531→ * @returns {Object} Settings with decrypted sensitive fields
   532→ */
   533→function decryptSensitiveFields(settings) {
   534→    const decrypted = { ...settings };
   535→
   536→    for (const field of SENSITIVE_FIELDS) {
   537→        if (decrypted[field] !== null && decrypted[field] !== undefined) {
   538→            // Check if it looks encrypted before attempting to decrypt
   539→            if (isEncryptedValue(decrypted[field])) {
   540→                const decryptedValue = decryptFromStorage(decrypted[field]);
   541→                if (decryptedValue !== null) {
   542→                    decrypted[field] = decryptedValue;
   543→                    debugLog(`[Config] Decrypted field: ${field}`);
   544→                }
   545→            }
   546→            // If not encrypted (legacy data), keep the original value
   547→        }
   548→    }
   549→
   550→    return decrypted;
   551→}
   552→
   553→/**
   554→ * Internal save function to avoid recursion during migration
   555→ * @private
   556→ */
   557→function _saveSettingsInternal(settings) {
   558→    try {
   559→        // Encrypt sensitive fields before storing
   560→        const toStore = encryptSensitiveFields(settings);
   561→
   562→        // Add version marker for future migrations
   563→        toStore._storageVersion = SETTINGS_STORAGE_VERSION;
   564→
   565→        localStorage.setItem('spliceSettings', JSON.stringify(toStore));
   566→        return true;
   567→    } catch (e) {
   568→        console.warn('[Config] Error saving settings:', e);
   569→        return false;
   570→    }
   571→}
   572→
   573→/**
   574→ * Get settings from localStorage
   575→ * Handles migration from unencrypted to encrypted storage format
   576→ */
   577→function getSettings() {
   578→    try {
   579→        const stored = localStorage.getItem('spliceSettings');
   580→        if (stored) {
   581→            const parsed = JSON.parse(stored);
   582→
   583→            // Decrypt sensitive fields (handles both encrypted and legacy unencrypted data)
   584→            const decrypted = decryptSensitiveFields(parsed);
   585→
   586→            // Check if migration is needed (no version marker = legacy data)
   587→            if (!parsed._storageVersion) {
   588→                // Migrate legacy data: save with encryption and version marker
   589→                debugLog('[Config] Migrating legacy settings to encrypted format');
   590→                const migratedSettings = { ...DEFAULT_SETTINGS, ...decrypted };
   591→                // Use internal save to avoid infinite recursion
   592→                _saveSettingsInternal(migratedSettings);
   593→            }
   594→
   595→            // Return merged settings without internal fields
   596→            const result = { ...DEFAULT_SETTINGS, ...decrypted };
   597→            delete result._storageVersion;
   598→            return result;
   599→        }
   600→    } catch (e) {
   601→        console.warn('[Config] Error loading settings:', e);
   602→    }
   603→    return { ...DEFAULT_SETTINGS };
   604→}
   605→
   606→/**
   607→ * Save settings to localStorage
   608→ * Encrypts sensitive fields before storing
   609→ */
   610→function saveSettings(settings) {
   611→    try {
   612→        // Get current settings (decrypted) and merge with new settings
   613→        const current = getSettings();
   614→        const merged = { ...current, ...settings };
   615→
   616→        return _saveSettingsInternal(merged);
   617→    } catch (e) {
   618→        console.warn('[Config] Error saving settings:', e);
   619→        return false;
   620→    }
   621→}
   622→
   623→/**
   624→ * Clear specific setting
   625→ */
   626→function clearSetting(key) {
   627→    const settings = getSettings();
   628→    delete settings[key];
   629→    _saveSettingsInternal(settings);
   630→}
