    tutorial: {
        name: 'Tutorial',
        description: 'Preserves teaching pace, J-Cuts enabled',
        icon: 'school',
        settings: {
            sensitivity: 30,
            threshold: -38,
            minSilenceLength: 1.0,
            paddingStart: 0.2,
            paddingEnd: 0.15,
            enableTakesDetection: true,
            enableJCut: true,
            jCutLeadIn: 0.35,
            jCutLeadOut: 0.25
        }
    },

    // Vlog/YouTube - punchy edits, engagement-focused
    vlog: {
        name: 'Vlog',
        description: 'Punchy edits for YouTube engagement',
