   598→    if (ui.enableZoom) ui.enableZoom.checked = settings.enableZoom;
   599→    if (ui.enableChapters) ui.enableChapters.checked = settings.enableChapters;
   600→
   601→    if (ui.jcutSettings) ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   602→    if (ui.zoomSettings) ui.zoomSettings.classList.toggle('collapsed', !settings.enableZoom);
   603→    if (ui.chapterSettings) ui.chapterSettings.classList.toggle('collapsed', !settings.enableChapters);
   604→
