     1→/**
     2→ * SPLICE Plugin Main Entry Point
     3→ *
     4→ * Initializes all functionality when the plugin loads.
     5→ * v3.4 - Timeline Markers + Unified Take-Aware Workflow.
     6→ */
     7→
     8→// Initialize Premiere Pro API
     9→const ppro = require('premierepro');
    10→
    11→// ============================================================================
    12→// CACHED DOM ELEMENTS (Performance: avoid repeated getElementById calls)
    13→// ============================================================================
    14→const ui = {};
    15→
    16→function cacheUIElements() {
    17→  ui.status = document.getElementById('status');
    18→
    19→  // Main GO button (unified workflow)
    20→  ui.goBtn = document.getElementById('goBtn');
    21→  ui.optionsToggle = document.getElementById('optionsToggle');
    22→  ui.optionsPanel = document.getElementById('optionsPanel');
    23→
    24→  // Sliders and options
    25→  ui.sensitivitySlider = document.getElementById('sensitivitySlider');
    26→  ui.sourceOriginal = document.getElementById('sourceOriginal');
    27→  ui.sourceIsolated = document.getElementById('sourceIsolated');
    28→
    29→  // Settings checkboxes
    30→  ui.enableTakesDetection = document.getElementById('enableTakesDetection');
    31→  ui.autoMarkBest = document.getElementById('autoMarkBest');
    32→
    33→  // Progress
    34→  ui.progressContainer = document.getElementById('progressContainer');
    35→  ui.progressText = document.getElementById('progressText');
    36→  ui.resultsEmpty = document.getElementById('resultsEmpty');
    37→
    38→  // Combined Preview
    39→  ui.combinedPreview = document.getElementById('combinedPreview');
    40→  ui.previewList = document.getElementById('previewList');
    41→  ui.silenceCountDisplay = document.getElementById('silenceCount');
    42→  ui.takeCountDisplay = document.getElementById('takeCount');
    43→  ui.selectedCount = document.getElementById('selectedCount');
    44→  ui.selectAllSilences = document.getElementById('selectAllSilences');
    45→  ui.applyPreviewBtn = document.getElementById('applyPreviewBtn');
    46→  ui.cancelPreviewBtn = document.getElementById('cancelPreviewBtn');
    47→  ui.invertSelectionBtn = document.getElementById('invertSelectionBtn');
    48→  ui.reRunDetectionBtn = document.getElementById('reRunDetectionBtn');
    49→
    50→  // Results
    51→  ui.silenceResults = document.getElementById('silenceResults');
    52→  ui.resultsCount = document.getElementById('resultsCount');
    53→  ui.timeSaved = document.getElementById('timeSaved');
    54→  ui.clipsModified = document.getElementById('clipsModified');
    55→
    56→  // Buttons
    57→  ui.undoBtn = document.getElementById('undoBtn');
    58→  ui.advancedSection = document.getElementById('advancedSection');
    59→
    60→  // Settings modal
    61→  ui.settingsBtn = document.getElementById('settingsBtn');
    62→  ui.settingsModal = document.getElementById('settingsModal');
    63→  ui.closeSettingsBtn = document.getElementById('closeSettingsBtn');
    64→}
    65→
    66→// ============================================================================
    67→// STATE MANAGEMENT
    68→// ============================================================================
    69→let previewSilences = [];         // All detected silences
    70→let safeSilences = [];            // Silences that don't overlap takes (safe to remove)
    71→let protectedSilences = [];       // Silences that overlap takes (protected)
    72→let previewTakes = [];            // Detected takes (for display)
    73→let selectedSilenceIndices = new Set();
    74→let isOperationInProgress = false;
    75→let pendingUIUpdate = null;
    76→
    77→// Export preview state for other modules (Razor integration)
    78→window.splicePreviewState = {
    79→  getSelectedSilences: () => safeSilences.filter((_, i) => selectedSilenceIndices.has(i)),
    80→  getSelectedTakes: () => previewTakes,
    81→  hasPreview: () => safeSilences.length > 0 || previewTakes.length > 0
    82→};
    83→
    84→// ============================================================================
    85→// INITIALIZATION
    86→// ============================================================================
    87→document.addEventListener('DOMContentLoaded', () => {
    88→  // Cache all DOM elements first
    89→  cacheUIElements();
    90→
    91→  // Settings & UI
    92→  initSettingsUI();
    93→  initSettingsModal();
    94→  initOptionsToggles();
    95→  initHelpButton();
    96→
    97→  // Credits display
    98→  initCredits();
    99→
   100→  // Unified workflow (silences + takes)
   101→  initUnifiedWorkflow();
   102→
   103→  // Preview handlers (event delegation)
   104→  initPreviewHandlers();
   105→
   106→  // Undo handlers
   107→  initUndoHandlers();
   108→
   109→  // Keyboard shortcuts
   110→  initKeyboardShortcuts();
   111→
   112→  // Advanced features (Razor)
   113→  initSlice9();
   114→
   115→  console.log('[SPLICE] Plugin initialized v3.4 (Timeline Markers + Unified Workflow)');
   116→});
   117→
   118→// ============================================================================
   119→// DEBOUNCED UI UPDATES (Performance: batch DOM updates)
   120→// ============================================================================
   121→function scheduleUIUpdate(callback) {
   122→  if (pendingUIUpdate) {
   123→    cancelAnimationFrame(pendingUIUpdate);
   124→  }
   125→  pendingUIUpdate = requestAnimationFrame(() => {
   126→    callback();
   127→    pendingUIUpdate = null;
   128→  });
   129→}
   130→
   131→function updateSelectionCounts() {
   132→  scheduleUIUpdate(() => {
   133→    if (ui.selectedCount) {
   134→      ui.selectedCount.textContent = selectedSilenceIndices.size;
   135→    }
   136→    updateApplyButton();
   137→    updateSelectAllCheckbox();
   138→  });
   139→}
   140→
   141→// ============================================================================
   142→// KEYBOARD SHORTCUTS
   143→// ============================================================================
   144→function initKeyboardShortcuts() {
   145→  document.addEventListener('keydown', (e) => {
   146→    // Only handle when combined preview is visible
   147→    const previewVisible = ui.combinedPreview?.style.display !== 'none' &&
   148→                           ui.combinedPreview?.style.display !== '';
   149→
   150→    if (!previewVisible) return;
   151→
   152→    const isMac = navigator.platform.toUpperCase().indexOf('MAC') >= 0;
   153→    const cmdKey = isMac ? e.metaKey : e.ctrlKey;
   154→
   155→    // Enter - Apply selected
   156→    if (e.key === 'Enter' && !e.shiftKey) {
   157→      e.preventDefault();
   158→      if (selectedSilenceIndices.size > 0) {
   159→        ui.applyPreviewBtn?.click();
   160→      }
   161→    }
   162→
   163→    // Escape - Cancel
   164→    if (e.key === 'Escape') {
   165→      e.preventDefault();
   166→      ui.cancelPreviewBtn?.click();
   167→    }
   168→
   169→    // Cmd/Ctrl+A - Select all
   170→    if (e.key === 'a' && cmdKey) {
   171→      e.preventDefault();
   172→      if (ui.selectAllSilences) {
   173→        ui.selectAllSilences.checked = true;
   174→        selectAllSilences(true);
   175→      }
   176→    }
   177→
   178→    // Cmd/Ctrl+D - Deselect all
   179→    if (e.key === 'd' && cmdKey) {
   180→      e.preventDefault();
   181→      if (ui.selectAllSilences) {
   182→        ui.selectAllSilences.checked = false;
   183→        selectAllSilences(false);
   184→      }
   185→    }
   186→
   187→    // Cmd/Ctrl+I - Invert selection
   188→    if (e.key === 'i' && cmdKey) {
   189→      e.preventDefault();
   190→      invertSilenceSelection();
   191→    }
   192→  });
   193→}
   194→
   195→// ============================================================================
   196→// UNIFIED WORKFLOW (Silence Detection + Takes Detection)
   197→// ============================================================================
   198→function initUnifiedWorkflow() {
   199→  if (!ui.goBtn) return;
   200→
   201→  ui.goBtn.addEventListener('click', async () => {
   202→    // Prevent double-click
   203→    if (isOperationInProgress) {
   204→      setStatus('Operation in progress...');
   205→      return;
   206→    }
   207→
   208→    isOperationInProgress = true;
   209→    ui.goBtn.disabled = true;
   210→
   211→    try {
   212→      showProgress('Preparing audio...');
   213→
   214→      // Step 1: Export audio
   215→      const exportSuccess = await exportAudioInternal();
   216→      if (!exportSuccess) {
   217→        throw new Error('Audio export failed');
   218→      }
   219→
   220→      // Step 2: Get settings
   221→      const sensitivity = parseInt(ui.sensitivitySlider?.value || 50);
   222→      const useIsolated = ui.sourceIsolated?.checked ?? false;
   223→      const detectTakes = ui.enableTakesDetection?.checked ?? true;
   224→
   225→      // Step 3: Vocal isolation if needed
   226→      let audioPath = WAV_PATH;
   227→      if (useIsolated) {
   228→        // Check isolation access before proceeding
   229→        const credits = getCurrentCredits();
   230→        if (!credits || !credits.hasIsolationAccess) {
   231→          throw new Error('Vocal isolation requires Pro or Team tier. Upgrade to access this feature.');
   232→        }
   233→
   234→        // Show overage warning if applicable
   235→        const isolationMins = credits.isolationHoursRemaining * 60;
   236→        if (isolationMins <= 0) {
   237→          const overageRate = credits.isolationOverageRate || 0.08;
   238→          showProgress(`Isolating vocals (overage: $${overageRate}/min)...`);
   239→        } else {
   240→          showProgress(`Isolating vocals (${isolationMins.toFixed(0)} min remaining)...`);
   241→        }
   242→
   243→        const isolateResult = await isolateVocals();
   244→        if (isolateResult.success) {
   245→          audioPath = isolateResult.outputPath;
   246→          // Update credits display after isolation
   247→          await refreshCredits();
   248→        } else {
   249→          throw new Error(isolateResult.error || 'Vocal isolation failed');
   250→        }
   251→      }
   252→
   253→      // Step 4: Run detection (parallel if takes enabled)
   254→      const params = mapSensitivity(sensitivity);
   255→      let silences = [];
   256→      let takes = [];
   257→
   258→      if (detectTakes) {
   259→        // Parallel detection for performance
   260→        showProgress('Detecting silences and analyzing takes...');
   261→        const [silenceResult, transcriptResult] = await Promise.all([
   262→          detectSilences(audioPath, params),
   263→          transcribeAudio()
   264→        ]);
   265→
   266→        silences = silenceResult;
   267→
   268→        // Extract takes from transcript result
   269→        if (transcriptResult.success && transcriptResult.takes) {
   270→          if (transcriptResult.takes.takes) {
   271→            takes = transcriptResult.takes.takes;
   272→          } else if (Array.isArray(transcriptResult.takes)) {
   273→            takes = transcriptResult.takes;
   274→          }
   275→        }
   276→
   277→        // Store transcript for reference
   278→        window.currentTranscript = transcriptResult.transcript;
   279→      } else {
   280→        // Just detect silences
   281→        showProgress('Detecting silences...');
   282→        silences = await detectSilences(audioPath, params);
   283→      }
   284→
   285→      if (silences.length === 0) {
   286→        showEmptyState();
   287→        setStatus('No silences detected. Try increasing sensitivity.');
   288→        return;
   289→      }
   290→
   291→      // Step 5: Filter silences by takes (protect speech)
   292→      const filtered = filterSilencesByTakes(silences, takes);
   293→
   294→      // Step 6: Add timeline markers (clear old ones first)
   295→      showProgress('Adding timeline markers...');
   296→      await clearSpliceMarkers();
   297→      const markerResult = await addTimelineMarkers(filtered.safe, filtered.protected, takes);
   298→
   299→      // Step 7: Show combined preview
   300→      showCombinedPreview(filtered.safe, filtered.protected, takes);
   301→
   302→      const markerInfo = markerResult.added > 0 ? ` (${markerResult.added} markers added)` : '';
   303→      const statusMsg = takes.length > 0
   304→        ? `Found ${silences.length} silences, ${filtered.protected.length} protected by ${takes.length} takes${markerInfo}`
   305→        : `Found ${silences.length} silence(s) - review and apply${markerInfo}`;
   306→      setStatus(statusMsg);
   307→
   308→    } catch (err) {
   309→      showEmptyState();
   310→      setStatus('Error: ' + err.message);
   311→      console.error('[SPLICE] Unified workflow error:', err);
   312→    } finally {
   313→      isOperationInProgress = false;
   314→      ui.goBtn.disabled = false;
   315→    }
   316→  });
   317→}
   318→
   319→// ============================================================================
   320→// TAKE-AWARE SILENCE FILTERING
   321→// ============================================================================
   322→/**
   323→ * Filter silences to protect those that overlap with takes (speech segments).
   324→ * Returns { safe: [], protected: [] }
   325→ */
   326→function filterSilencesByTakes(silences, takes) {
   327→  if (!takes || takes.length === 0) {
   328→    // No takes = all silences are safe to remove
   329→    return { safe: silences, protected: [] };
   330→  }
   331→
   332→  const safe = [];
   333→  const protected_ = [];
   334→
   335→  silences.forEach(silence => {
   336→    // Check if this silence overlaps with any take
   337→    const overlaps = takes.some(take => {
   338→      const takeStart = take.startTime || take.start;
   339→      const takeEnd = take.endTime || take.end;
   340→      // Overlap exists if silence starts before take ends AND silence ends after take starts
   341→      return silence.start < takeEnd && silence.end > takeStart;
   342→    });
   343→
   344→    if (overlaps) {
   345→      protected_.push(silence);
   346→    } else {
   347→      safe.push(silence);
   348→    }
   349→  });
   350→
   351→  console.log(`[SPLICE] Filtered silences: ${safe.length} safe, ${protected_.length} protected`);
   352→  return { safe, protected: protected_ };
   353→}
   354→
   355→// ============================================================================
   356→// TIMELINE MARKERS (Visual indicators on Premiere Pro timeline)
   357→// ============================================================================
   358→/**
   359→ * Add colored markers to the timeline for silences and takes.
   360→ * - Red markers: silences safe to remove
   361→ * - Yellow markers: silences protected by takes
   362→ * - Blue markers: takes (speech segments)
   363→ *
   364→ * @param {Array} safeToRemove - Silences safe to remove
   365→ * @param {Array} protectedSilences - Silences overlapping speech
   366→ * @param {Array} takes - Detected speech segments
   367→ * @returns {Promise<{added: number, errors: number}>}
   368→ */
   369→async function addTimelineMarkers(safeToRemove, protectedSilences, takes) {
   370→  const results = { added: 0, errors: 0 };
   371→
   372→  try {
   373→    const context = await getActiveSequence();
   374→    if (!context) {
   375→      console.warn('[SPLICE] No sequence available for markers');
   376→      return results;
   377→    }
   378→
   379→    const { sequence, project } = context;
   380→
   381→    // Get markers object for the sequence
   382→    const markers = await ppro.Markers.getMarkers(sequence);
   383→    if (!markers) {
   384→      console.warn('[SPLICE] Markers API not available');
   385→      return results;
   386→    }
   387→
   388→    // Collect all marker actions
   389→    const markerActions = [];
   390→
   391→    // Red markers for safe-to-remove silences
   392→    for (const silence of safeToRemove) {
   393→      try {
   394→        const startTicks = Math.floor(silence.start * TICKS_PER_SECOND);
   395→        const durationTicks = Math.floor((silence.end - silence.start) * TICKS_PER_SECOND);
   396→        const duration = (silence.end - silence.start).toFixed(2);
   397→
   398→        const action = markers.createAddMarkerAction(
   399→          `SPLICE: Remove (${duration}s)`,
   400→          ppro.Marker.MARKER_TYPE_COMMENT,
   401→          startTicks,
   402→          durationTicks,
   403→          `Silence to remove: ${formatTime(silence.start)} - ${formatTime(silence.end)}`
   404→        );
   405→        markerActions.push({ action, colorIndex: 1, type: 'silence' }); // Red = index 1
   406→      } catch (err) {
   407→        console.error('[SPLICE] Error creating silence marker:', err);
   408→        results.errors++;
   409→      }
   410→    }
   411→
   412→    // Yellow markers for protected silences
   413→    for (const silence of protectedSilences) {
   414→      try {
   415→        const startTicks = Math.floor(silence.start * TICKS_PER_SECOND);
   416→        const durationTicks = Math.floor((silence.end - silence.start) * TICKS_PER_SECOND);
   417→        const duration = (silence.end - silence.start).toFixed(2);
   418→
   419→        const action = markers.createAddMarkerAction(
   420→          `SPLICE: Protected (${duration}s)`,
   421→          ppro.Marker.MARKER_TYPE_COMMENT,
   422→          startTicks,
   423→          durationTicks,
   424→          `Protected silence (overlaps speech): ${formatTime(silence.start)} - ${formatTime(silence.end)}`
   425→        );
   426→        markerActions.push({ action, colorIndex: 4, type: 'protected' }); // Yellow = index 4
   427→      } catch (err) {
   428→        console.error('[SPLICE] Error creating protected marker:', err);
   429→        results.errors++;
   430→      }
   431→    }
   432→
   433→    // Blue markers for takes (speech segments)
   434→    for (const take of takes) {
   435→      try {
   436→        const start = take.startTime || take.start;
   437→        const end = take.endTime || take.end;
   438→        const startTicks = Math.floor(start * TICKS_PER_SECOND);
   439→        const durationTicks = Math.floor((end - start) * TICKS_PER_SECOND);
   440→        const textPreview = take.text?.substring(0, 30) || 'Speech';
   441→
   442→        const action = markers.createAddMarkerAction(
   443→          `SPLICE: Take`,
   444→          ppro.Marker.MARKER_TYPE_COMMENT,
   445→          startTicks,
   446→          durationTicks,
   447→          `Speech segment: ${textPreview}${take.text?.length > 30 ? '...' : ''}`
   448→        );
   449→        markerActions.push({ action, colorIndex: 5, type: 'take' }); // Blue = index 5
   450→      } catch (err) {
   451→        console.error('[SPLICE] Error creating take marker:', err);
   452→        results.errors++;
   453→      }
   454→    }
   455→
   456→    // Execute all marker actions in a single transaction
   457→    if (markerActions.length > 0) {
   458→      await project.lockedAccess(async () => {
   459→        await project.executeTransaction((compoundAction) => {
   460→          for (const { action } of markerActions) {
   461→            compoundAction.addAction(action);
   462→          }
   463→        }, 'SPLICE: Add Timeline Markers');
   464→      });
   465→
   466→      results.added = markerActions.length;
   467→      console.log(`[SPLICE] Added ${results.added} timeline markers`);
   468→    }
   469→
   470→  } catch (err) {
   471→    console.error('[SPLICE] Timeline markers error:', err);
   472→    results.errors++;
   473→  }
   474→
   475→  return results;
   476→}
   477→
   478→/**
   479→ * Read current SPLICE marker positions from timeline.
   480→ * This allows users to manually adjust markers before applying.
   481→ * Returns silences based on current marker positions (not original detection).
   482→ */
   483→async function readMarkersAssilences() {
   484→  const silences = [];
   485→
   486→  try {
   487→    const context = await getActiveSequence();
   488→    if (!context) return silences;
   489→
   490→    const { sequence } = context;
   491→    const markers = await ppro.Markers.getMarkers(sequence);
   492→    if (!markers) return silences;
   493→
   494→    const allMarkers = await markers.getMarkers();
   495→    if (!allMarkers || allMarkers.length === 0) return silences;
   496→
   497→    for (const marker of allMarkers) {
   498→      const name = await marker.getName();
   499→
   500→      // Only read "Remove" markers (red ones that will be cut)
   501→      if (name && name.startsWith('SPLICE: Remove')) {
   502→        const startTicks = await marker.getStart();
   503→        const durationTicks = await marker.getDuration();
   504→
   505→        // Convert ticks to seconds
   506→        const start = startTicks / TICKS_PER_SECOND;
   507→        const duration = durationTicks / TICKS_PER_SECOND;
   508→        const end = start + duration;
   509→
   510→        silences.push({
   511→          start,
   512→          end,
   513→          duration,
   514→          fromMarker: true  // Flag to indicate this came from marker adjustment
   515→        });
   516→      }
   517→    }
   518→
   519→    // Sort by start time
   520→    silences.sort((a, b) => a.start - b.start);
   521→    console.log(`[SPLICE] Read ${silences.length} silences from adjusted markers`);
   522→
   523→  } catch (err) {
   524→    console.error('[SPLICE] Error reading markers:', err);
   525→  }
   526→
   527→  return silences;
   528→}
   529→
   530→/**
   531→ * Remove all SPLICE markers from the timeline.
   532→ * Call this before adding new markers to avoid duplicates.
   533→ */
   534→async function clearSpliceMarkers() {
   535→  try {
   536→    const context = await getActiveSequence();
   537→    if (!context) return 0;
   538→
   539→    const { sequence, project } = context;
   540→    const markers = await ppro.Markers.getMarkers(sequence);
   541→    if (!markers) return 0;
   542→
   543→    // Get all markers
   544→    const allMarkers = await markers.getMarkers();
   545→    if (!allMarkers || allMarkers.length === 0) return 0;
   546→
   547→    // Filter SPLICE markers
   548→    const spliceMarkers = [];
   549→    for (const marker of allMarkers) {
   550→      const name = await marker.getName();
   551→      if (name && name.startsWith('SPLICE:')) {
   552→        spliceMarkers.push(marker);
   553→      }
   554→    }
   555→
   556→    if (spliceMarkers.length === 0) return 0;
   557→
   558→    // Remove all SPLICE markers in a single transaction
   559→    await project.lockedAccess(async () => {
   560→      await project.executeTransaction((compoundAction) => {
   561→        for (const marker of spliceMarkers) {
   562→          const removeAction = markers.createRemoveMarkerAction(marker);
   563→          compoundAction.addAction(removeAction);
   564→        }
   565→      }, 'SPLICE: Clear Markers');
   566→    });
   567→
   568→    console.log(`[SPLICE] Cleared ${spliceMarkers.length} previous markers`);
   569→    return spliceMarkers.length;
   570→
   571→  } catch (err) {
   572→    console.error('[SPLICE] Error clearing markers:', err);
   573→    return 0;
   574→  }
   575→}
   576→
   577→// ============================================================================
   578→// COMBINED PREVIEW (Silences + Takes with colored markers)
   579→// ============================================================================
   580→/**
   581→ * Show combined preview with:
   582→ * - Red markers: silences safe to remove
   583→ * - Yellow markers: silences protected by takes (won't be removed)
   584→ * - Blue markers: takes (for reference)
   585→ */
   586→function showCombinedPreview(safeToRemove, protectedSilences, takes) {
   587→  // Update global state
   588→  safeSilences = safeToRemove;
   589→  previewSilences = [...safeToRemove, ...protectedSilences];
   590→  previewTakes = takes;
   591→  selectedSilenceIndices = new Set(safeToRemove.map((_, i) => i));
   592→
   593→  // Hide other views
   594→  hideAllViews();
   595→
   596→  // Update summary counts
   597→  if (ui.silenceCountDisplay) ui.silenceCountDisplay.textContent = safeToRemove.length;
   598→  if (ui.takeCountDisplay) ui.takeCountDisplay.textContent = takes.length;
   599→  if (ui.selectedCount) ui.selectedCount.textContent = safeToRemove.length;
   600→
   601→  // Build combined items list (sorted by time)
   602→  const items = [
   603→    ...safeToRemove.map((s, i) => ({ type: 'silence', index: i, start: s.start, end: s.end, data: s })),
   604→    ...protectedSilences.map(s => ({ type: 'protected', start: s.start, end: s.end, data: s })),
   605→    ...takes.map(t => ({ type: 'take', start: t.startTime || t.start, end: t.endTime || t.end, data: t }))
   606→  ].sort((a, b) => a.start - b.start);
   607→
   608→  // Build preview list using DocumentFragment
   609→  const fragment = document.createDocumentFragment();
   610→
   611→  items.forEach((item) => {
   612→    const duration = item.end - item.start;
   613→    const div = document.createElement('div');
   614→
   615→    if (item.type === 'silence') {
   616→      // Safe to remove - red marker with checkbox
   617→      div.className = 'preview-item silence-marker';
   618→      div.dataset.index = item.index;
   619→      div.innerHTML = `
   620→        <input type="checkbox" class="preview-item-check" checked data-index="${item.index}">
   621→        <div class="preview-item-info">
   622→          <div class="preview-item-time">${formatTime(item.start)} - ${formatTime(item.end)}</div>
   623→          <div class="preview-item-duration">${duration.toFixed(2)}s silence</div>
   624→        </div>
   625→        <button class="preview-item-seek" data-time="${item.start}" title="Seek to this silence">&gt;</button>
   626→      `;
   627→    } else if (item.type === 'protected') {
   628→      // Protected by take - yellow marker, no checkbox
   629→      div.className = 'preview-item protected-silence';
   630→      div.innerHTML = `
   631→        <div style="width: 20px; margin-right: 10px;"></div>
   632→        <div class="preview-item-info">
   633→          <div class="preview-item-time">${formatTime(item.start)} - ${formatTime(item.end)}</div>
   634→          <div class="preview-item-duration">${duration.toFixed(2)}s (overlaps speech)</div>
   635→        </div>
   636→        <button class="preview-item-seek" data-time="${item.start}" title="Seek">&gt;</button>
   637→      `;
   638→    } else if (item.type === 'take') {
   639→      // Take (speech segment) - blue marker, no checkbox
   640→      div.className = 'preview-item take-marker';
   641→      const textPreview = item.data.text?.substring(0, 40) || 'Speech segment';
   642→      div.innerHTML = `
   643→        <div style="width: 20px; margin-right: 10px;"></div>
   644→        <div class="preview-item-info">
   645→          <div class="preview-item-time">${formatTime(item.start)} - ${formatTime(item.end)}</div>
   646→          <div class="preview-item-duration">${textPreview}${item.data.text?.length > 40 ? '...' : ''}</div>
   647→        </div>
   648→        <button class="preview-item-seek" data-time="${item.start}" title="Seek to take">&gt;</button>
   649→      `;
   650→    }
   651→
   652→    fragment.appendChild(div);
   653→  });
   654→
   655→  if (ui.previewList) {
   656→    ui.previewList.textContent = '';
   657→    ui.previewList.appendChild(fragment);
   658→  }
   659→
   660→  // Reset UI state
   661→  if (ui.selectAllSilences) ui.selectAllSilences.checked = true;
   662→  updateApplyButton();
   663→
   664→  // Show combined preview section
   665→  if (ui.combinedPreview) ui.combinedPreview.style.display = 'block';
   666→}
   667→
   668→function initPreviewHandlers() {
   669→  // EVENT DELEGATION: Single listener on parent instead of many on children
   670→  if (ui.previewList) {
   671→    // Handle checkbox changes
   672→    ui.previewList.addEventListener('change', (e) => {
   673→      if (e.target.classList.contains('preview-item-check')) {
   674→        const index = parseInt(e.target.dataset.index);
   675→        const item = e.target.closest('.preview-item');
   676→
   677→        if (e.target.checked) {
   678→          selectedSilenceIndices.add(index);
   679→          item?.classList.remove('excluded');
   680→        } else {
   681→          selectedSilenceIndices.delete(index);
   682→          item?.classList.add('excluded');
   683→        }
   684→        updateSelectionCounts();
   685→      }
   686→    });
   687→
   688→    // Handle seek button clicks
   689→    ui.previewList.addEventListener('click', async (e) => {
   690→      if (e.target.classList.contains('preview-item-seek')) {
   691→        e.stopPropagation();
   692→        const btn = e.target;
   693→        const time = parseFloat(btn.dataset.time);
   694→
   695→        // Show loading state
   696→        btn.classList.add('loading');
   697→        btn.textContent = '...';
   698→
   699→        try {
   700→          await seekToTime(time);
   701→        } finally {
   702→          btn.classList.remove('loading');
   703→          btn.textContent = '>';
   704→        }
   705→      }
   706→    });
   707→  }
   708→
   709→  // Apply button
   710→  if (ui.applyPreviewBtn) {
   711→    ui.applyPreviewBtn.addEventListener('click', async () => {
   712→      if (isOperationInProgress) return;
   713→      isOperationInProgress = true;
   714→      ui.applyPreviewBtn.disabled = true;
   715→
   716→      const originalText = ui.applyPreviewBtn.textContent;
   717→      ui.applyPreviewBtn.textContent = 'Applying...';
   718→
   719→      try {
   720→        await applySelectedSilences();
   721→      } catch (err) {
   722→        setStatus('Error: ' + err.message);
   723→        console.error('[SPLICE] Apply error:', err);
   724→      } finally {
   725→        ui.applyPreviewBtn.textContent = originalText;
   726→        ui.applyPreviewBtn.disabled = false;
   727→        isOperationInProgress = false;
   728→      }
   729→    });
   730→  }
   731→
   732→  // Cancel button
   733→  if (ui.cancelPreviewBtn) {
   734→    ui.cancelPreviewBtn.addEventListener('click', async () => {
   735→      await cancelPreview();
   736→    });
   737→  }
   738→
   739→  // Select all checkbox
   740→  if (ui.selectAllSilences) {
   741→    ui.selectAllSilences.addEventListener('change', (e) => {
   742→      selectAllSilences(e.target.checked);
   743→    });
   744→  }
   745→
   746→  // Invert selection button
   747→  if (ui.invertSelectionBtn) {
   748→    ui.invertSelectionBtn.addEventListener('click', () => {
   749→      invertSilenceSelection();
   750→    });
   751→  }
   752→
   753→  // Duration filter buttons
   754→  document.querySelectorAll('.preview-filters .filter-btn[data-min]').forEach(btn => {
   755→    btn.addEventListener('click', () => {
   756→      const min = parseFloat(btn.dataset.min);
   757→      const max = parseFloat(btn.dataset.max);
   758→      selectByDuration(min, max);
   759→    });
   760→  });
   761→
   762→  // Re-run detection button
   763→  if (ui.reRunDetectionBtn) {
   764→    ui.reRunDetectionBtn.addEventListener('click', () => {
   765→      // Hide preview, show options, let user adjust sensitivity
   766→      if (ui.combinedPreview) ui.combinedPreview.style.display = 'none';
   767→      if (ui.optionsPanel) ui.optionsPanel.classList.remove('collapsed');
   768→      if (ui.optionsToggle) ui.optionsToggle.classList.add('expanded');
   769→      showEmptyState();
   770→      setStatus('Adjust sensitivity and click GO again');
   771→    });
   772→  }
   773→}
   774→
   775→function selectAllSilences(checked) {
   776→  selectedSilenceIndices.clear();
   777→
   778→  if (checked) {
   779→    safeSilences.forEach((_, i) => selectedSilenceIndices.add(i));
   780→  }
   781→
   782→  // Batch update DOM (only silence-marker items have checkboxes)
   783→  document.querySelectorAll('.preview-item.silence-marker .preview-item-check').forEach((checkbox) => {
   784→    const index = parseInt(checkbox.dataset.index);
   785→    checkbox.checked = checked;
   786→    const item = checkbox.closest('.preview-item');
   787→    if (checked) {
   788→      item?.classList.remove('excluded');
   789→    } else {
   790→      item?.classList.add('excluded');
   791→    }
   792→  });
   793→
   794→  updateSelectionCounts();
   795→}
   796→
   797→function invertSilenceSelection() {
   798→  const allIndices = new Set(safeSilences.map((_, i) => i));
   799→  const newSelection = new Set([...allIndices].filter(i => !selectedSilenceIndices.has(i)));
   800→  selectedSilenceIndices = newSelection;
   801→
   802→  // Update DOM
   803→  document.querySelectorAll('.preview-item.silence-marker .preview-item-check').forEach((checkbox) => {
   804→    const index = parseInt(checkbox.dataset.index);
   805→    const isSelected = selectedSilenceIndices.has(index);
   806→    checkbox.checked = isSelected;
   807→    const item = checkbox.closest('.preview-item');
   808→    if (isSelected) {
   809→      item?.classList.remove('excluded');
   810→    } else {
   811→      item?.classList.add('excluded');
   812→    }
   813→  });
   814→
   815→  updateSelectionCounts();
   816→}
   817→
   818→function selectByDuration(minSec, maxSec) {
   819→  selectedSilenceIndices.clear();
   820→
   821→  safeSilences.forEach((silence, index) => {
   822→    const duration = silence.end - silence.start;
   823→    if (duration >= minSec && duration < maxSec) {
   824→      selectedSilenceIndices.add(index);
   825→    }
   826→  });
   827→
   828→  // Update DOM
   829→  document.querySelectorAll('.preview-item.silence-marker .preview-item-check').forEach((checkbox) => {
   830→    const index = parseInt(checkbox.dataset.index);
   831→    const isSelected = selectedSilenceIndices.has(index);
   832→    checkbox.checked = isSelected;
   833→    const item = checkbox.closest('.preview-item');
   834→    if (isSelected) {
   835→      item?.classList.remove('excluded');
   836→    } else {
   837→      item?.classList.add('excluded');
   838→    }
   839→  });
   840→
   841→  updateSelectionCounts();
   842→  setStatus(`Selected ${selectedSilenceIndices.size} silences (${minSec}s - ${maxSec}s)`);
   843→}
   844→
   845→async function cancelPreview() {
   846→  // Keep silences available for Razor if detected
   847→  const hadSilences = safeSilences.length > 0;
   848→
   849→  previewSilences = [];
   850→  safeSilences = [];
   851→  protectedSilences = [];
   852→  previewTakes = [];
   853→  selectedSilenceIndices.clear();
   854→  showEmptyState();
   855→
   856→  // Clear timeline markers
   857→  const cleared = await clearSpliceMarkers();
   858→  const markerInfo = cleared > 0 ? ` (${cleared} markers removed)` : '';
   859→  setStatus('Preview cancelled' + markerInfo);
   860→
   861→  // Show Razor section if silences were detected
   862→  if (hadSilences && ui.advancedSection) {
   863→    ui.advancedSection.classList.remove('hidden');
   864→  }
   865→}
   866→
   867→async function applySelectedSilences() {
   868→  if (selectedSilenceIndices.size === 0) {
   869→    setStatus('No silences selected');
   870→    return;
   871→  }
   872→
   873→  showProgress('Reading marker positions...');
   874→
   875→  // Read current marker positions from timeline
   876→  // This allows users to manually adjust markers before applying
   877→  const markerSilences = await readMarkersAssilences();
   878→
   879→  let silencesToApply;
   880→  let adjustedCount = 0;
   881→
   882→  if (markerSilences.length > 0) {
   883→    // Use marker positions (may have been adjusted by user)
   884→    silencesToApply = markerSilences;
   885→    adjustedCount = markerSilences.length;
   886→    console.log(`[SPLICE] Using ${adjustedCount} silences from adjusted markers`);
   887→  } else {
   888→    // Fallback to original detection data
   889→    silencesToApply = safeSilences.filter((_, i) => selectedSilenceIndices.has(i));
   890→    console.log(`[SPLICE] Using ${silencesToApply.length} silences from original detection`);
   891→  }
   892→
   893→  if (silencesToApply.length === 0) {
   894→    setStatus('No silences to apply');
   895→    return;
   896→  }
   897→
   898→  // Update global silences for the apply function
   899→  setCurrentSilences(silencesToApply);
   900→
   901→  showProgress('Applying changes...');
   902→
   903→  // Apply to timeline
   904→  const result = await removeSilencesFromTimeline();
   905→
   906→  // Clear markers after applying
   907→  await clearSpliceMarkers();
   908→
   909→  // Hide preview
   910→  if (ui.combinedPreview) ui.combinedPreview.style.display = 'none';
   911→
   912→  // Show results
   913→  showSilenceResults({
   914→    count: silencesToApply.length,
   915→    timeSaved: silencesToApply.reduce((sum, s) => sum + (s.end - s.start), 0),
   916→    clipsModified: result
   917→  });
   918→
   919→  // Show advanced section
   920→  if (ui.advancedSection) ui.advancedSection.classList.remove('hidden');
   921→
   922→  const adjustedInfo = adjustedCount > 0 ? ' (from adjusted markers)' : '';
   923→  setStatus(`Removed ${silencesToApply.length} silence(s) - ${result} clip(s) modified${adjustedInfo}`);
   924→
   925→  // Refresh credits (await to prevent race conditions)
   926→  try {
   927→    await refreshCredits();
   928→  } catch (err) {
   929→    console.warn('[SPLICE] Credits refresh failed:', err);
   930→  }
   931→
   932→  // Clear preview state
   933→  previewSilences = [];
   934→  safeSilences = [];
   935→  protectedSilences = [];
   936→  previewTakes = [];
   937→  selectedSilenceIndices.clear();
   938→}
   939→
   940→// ============================================================================
   941→// UI HELPERS
   942→// ============================================================================
   943→function updateApplyButton() {
   944→  if (ui.applyPreviewBtn) {
   945→    const count = selectedSilenceIndices.size;
   946→    ui.applyPreviewBtn.disabled = count === 0;
   947→    ui.applyPreviewBtn.textContent = count === 0 ? 'Select silences' : `Apply ${count} Selected`;
   948→  }
   949→}
   950→
   951→function updateSelectAllCheckbox() {
   952→  if (ui.selectAllSilences) {
   953→    ui.selectAllSilences.checked = selectedSilenceIndices.size === safeSilences.length && safeSilences.length > 0;
   954→  }
   955→}
   956→
   957→function hideAllViews() {
   958→  if (ui.progressContainer) ui.progressContainer.style.display = 'none';
   959→  if (ui.resultsEmpty) ui.resultsEmpty.style.display = 'none';
   960→  if (ui.silenceResults) ui.silenceResults.style.display = 'none';
   961→  if (ui.combinedPreview) ui.combinedPreview.style.display = 'none';
   962→}
   963→
   964→function showProgress(message) {
   965→  hideAllViews();
   966→  if (ui.progressContainer) ui.progressContainer.style.display = 'block';
   967→  if (ui.progressText) ui.progressText.textContent = message;
   968→}
   969→
   970→function showEmptyState() {
   971→  hideAllViews();
   972→  if (ui.resultsEmpty) ui.resultsEmpty.style.display = 'block';
   973→  if (ui.advancedSection) ui.advancedSection.classList.add('hidden');
   974→}
   975→
   976→function showSilenceResults({ count, timeSaved, clipsModified }) {
   977→  hideAllViews();
   978→
   979→  // Update the results display
   980→  if (ui.resultsCount) ui.resultsCount.textContent = count;
   981→  if (ui.timeSaved) ui.timeSaved.textContent = timeSaved.toFixed(1) + 's';
   982→  if (ui.clipsModified) ui.clipsModified.textContent = clipsModified;
   983→
   984→  if (ui.silenceResults) ui.silenceResults.style.display = 'block';
   985→}
   986→
   987→function formatTime(seconds) {
   988→  if (typeof seconds !== 'number' || isNaN(seconds)) return '0:00';
   989→  const mins = Math.floor(seconds / 60);
   990→  const secs = Math.floor(seconds % 60);
   991→  return `${mins}:${secs.toString().padStart(2, '0')}`;
   992→}
   993→
   994→function setStatus(message) {
   995→  if (ui.status) ui.status.textContent = message;
   996→}
   997→
   998→// ============================================================================
   999→// UNDO HANDLERS
  1000→// ============================================================================
  1001→function initUndoHandlers() {
  1002→  if (ui.undoBtn) {
  1003→    ui.undoBtn.addEventListener('click', () => {
  1004→      try {
  1005→        ppro.app.executeCommand('Edit.Undo');
  1006→        showEmptyState();
  1007→        setStatus('Changes undone');
  1008→      } catch (e) {
  1009→        setStatus('Undo failed - use Cmd+Z');
  1010→      }
  1011→    });
  1012→  }
  1013→}
  1014→
  1015→// ============================================================================
  1016→// TIMELINE SEEK (with proper error handling)
  1017→// ============================================================================
  1018→async function seekToTime(seconds) {
  1019→  // Validate input
  1020→  if (typeof seconds !== 'number' || isNaN(seconds) || !isFinite(seconds)) {
  1021→    setStatus('Invalid seek time');
  1022→    return;
  1023→  }
  1024→
  1025→  if (seconds < 0) {
  1026→    seconds = 0;
  1027→  }
  1028→
  1029→  try {
  1030→    const context = await getActiveSequence();
  1031→    if (!context) {
  1032→      setStatus('No sequence available');
  1033→      return;
  1034→    }
  1035→
  1036→    const { sequence } = context;
  1037→    const ticks = Math.floor(seconds * TICKS_PER_SECOND);
  1038→
  1039→    await sequence.setPlayerPosition(ticks);
  1040→    setStatus(`Seeked to ${formatTime(seconds)}`);
  1041→  } catch (err) {
  1042→    console.error('[SPLICE] Seek error:', err);
  1043→    setStatus('Seek failed: ' + (err.message || 'Unknown error'));
  1044→  }
  1045→}
  1046→
  1047→// ============================================================================
  1048→// AUDIO EXPORT & BACKEND CALLS
  1049→// ============================================================================
  1050→async function exportAudioInternal() {
  1051→  try {
  1052→    const context = await getActiveSequence();
  1053→    if (!context) {
  1054→      throw new Error('No project or sequence open');
  1055→    }
  1056→
  1057→    const { sequence } = context;
  1058→    const encoderManager = ppro.EncoderManager.getManager();
  1059→    if (!encoderManager) {
  1060→      throw new Error('EncoderManager not available');
  1061→    }
  1062→
  1063→    const result = await encoderManager.exportSequence(
  1064→      sequence,
  1065→      ppro.EncoderManager.EXPORT_IMMEDIATELY,
  1066→      WAV_PATH,
  1067→      WAV_PRESET_PATH,
  1068→      true
  1069→    );
  1070→
  1071→    return result;
  1072→  } catch (err) {
  1073→    console.error('[SPLICE] Export error:', err);
  1074→    return false;
  1075→  }
  1076→}
  1077→
  1078→async function isolateVocals() {
  1079→  const response = await fetch(`${BACKEND_URL}/isolate-vocals`, {
  1080→    method: 'POST',
  1081→    headers: { 'Content-Type': 'application/json' },
  1082→    body: JSON.stringify({ audioPath: WAV_PATH })
  1083→  });
  1084→
  1085→  if (!response.ok) {
  1086→    const err = await response.json();
  1087→    return { success: false, error: err.error || 'Isolation request failed' };
  1088→  }
  1089→
  1090→  return await response.json();
  1091→}
  1092→
  1093→async function detectSilences(audioPath, params) {
  1094→  const response = await fetch(`${BACKEND_URL}/silences-audio`, {
  1095→    method: 'POST',
  1096→    headers: { 'Content-Type': 'application/json' },
  1097→    body: JSON.stringify({
  1098→      wavPath: audioPath,
  1099→      threshold: params.dbThreshold,
  1100→      minDuration: params.minDuration,
  1101→      padding: params.padding
  1102→    })
  1103→  });
  1104→
  1105→  if (!response.ok) {
  1106→    const err = await response.json();
  1107→    throw new Error(err.error || 'Silence detection failed');
  1108→  }
  1109→
  1110→  const data = await response.json();
  1111→  if (!data.success) {
  1112→    throw new Error(data.error || 'Silence detection failed');
  1113→  }
  1114→
  1115→  // Store silences globally for razor workflow
  1116→  currentSilences = data.silences;
  1117→
  1118→  return data.silences;
  1119→}
  1120→
  1121→async function transcribeAudio() {
  1122→  const response = await fetch(`${BACKEND_URL}/analyze`, {
  1123→    method: 'POST',
  1124→    headers: { 'Content-Type': 'application/json' },
  1125→    body: JSON.stringify({ wavPath: WAV_PATH })
  1126→  });
  1127→
  1128→  if (!response.ok) {
  1129→    const err = await response.json();
  1130→    return { success: false, error: err.error || 'Transcription failed' };
  1131→  }
  1132→
  1133→  const data = await response.json();
  1134→  return {
  1135→    success: data.success,
  1136→    transcript: data.transcript,
  1137→    takes: data.takes,
  1138→    error: data.error
  1139→  };
  1140→}
