     1→/**
     2→ * Slice 9: XML Processor for Premiere Pro
     3→ *
     4→ * Handles XMEML format (Final Cut Pro 7 XML) which is what
     5→ * Premiere Pro exports via File > Export > Final Cut Pro XML.
     6→ *
     7→ * Uses regex-based string manipulation to preserve original XML formatting
     8→ * while modifying clip boundaries.
     9→ *
    10→ * Key insight: Video and audio clips are LINKED - when splitting, we must
    11→ * coordinate the new IDs across all linked clips.
    12→ */
    13→
    14→const fs = require('fs').promises;
    15→
    16→/**
    17→ * XMEML Processor Class - String-based manipulation
    18→ */
    19→class XMEMLProcessor {
    20→  constructor() {
    21→    this.xmlContent = null;
    22→    this.timebase = 30;
    23→  }
    24→
    25→  async loadFromFile(filePath) {
    26→    this.xmlContent = await fs.readFile(filePath, 'utf8');
    27→    this.detectTimebase();
    28→    return this.xmlContent;
    29→  }
    30→
    31→  detectTimebase() {
    32→    const match = this.xmlContent.match(/<sequence[^>]*>[\s\S]*?<timebase>(\d+)<\/timebase>/);
    33→    if (match) {
    34→      this.timebase = parseInt(match[1]);
    35→      console.log(`[SPLICE] Detected timebase: ${this.timebase} fps`);
    36→    }
    37→  }
    38→
    39→  framesToSeconds(frames) {
    40→    return frames / this.timebase;
    41→  }
    42→
    43→  secondsToFrames(seconds) {
    44→    return Math.round(seconds * this.timebase);
    45→  }
    46→
    47→  /**
    48→   * Find all sequence clipitems with their positions and link info
    49→   */
    50→  findSequenceClipitems() {
    51→    const clips = [];
    52→    const clipRegex = /<clipitem\s+id="([^"]+)"[^>]*>([\s\S]*?)<\/clipitem>/g;
    53→    let match;
    54→
    55→    while ((match = clipRegex.exec(this.xmlContent)) !== null) {
    56→      const clipContent = match[2];
    57→
    58→      const startMatch = clipContent.match(/<start>(\d+)<\/start>/);
    59→      const endMatch = clipContent.match(/<end>(\d+)<\/end>/);
    60→
    61→      if (startMatch && endMatch) {
    62→        const inMatch = clipContent.match(/<in>(\d+)<\/in>/);
    63→        const outMatch = clipContent.match(/<out>(\d+)<\/out>/);
    64→        const nameMatch = clipContent.match(/<name>([^<]*)<\/name>/);
    65→        const durationMatch = clipContent.match(/<duration>(\d+)<\/duration>/);
    66→
    67→        // Extract all link references
    68→        const linkRefs = [];
    69→        const linkRegex = /<linkclipref>([^<]+)<\/linkclipref>/g;
    70→        let linkMatch;
    71→        while ((linkMatch = linkRegex.exec(clipContent)) !== null) {
    72→          linkRefs.push(linkMatch[1]);
    73→        }
    74→
    75→        clips.push({
    76→          id: match[1],
    77→          fullMatch: match[0],
    78→          startIndex: match.index,
    79→          endIndex: match.index + match[0].length,
    80→          content: clipContent,
    81→          start: parseInt(startMatch[1]),
    82→          end: parseInt(endMatch[1]),
    83→          in: inMatch ? parseInt(inMatch[1]) : 0,
    84→          out: outMatch ? parseInt(outMatch[1]) : parseInt(endMatch[1]),
    85→          duration: durationMatch ? parseInt(durationMatch[1]) : null,
    86→          name: nameMatch ? nameMatch[1] : 'Unknown',
    87→          linkRefs: linkRefs
    88→        });
    89→      }
    90→    }
    91→
    92→    return clips;
    93→  }
    94→
    95→  /**
    96→   * Group clips by their start time (linked clips have same start)
    97→   */
    98→  groupLinkedClips(clips) {
    99→    const groups = {};
   100→    for (const clip of clips) {
   101→      const key = clip.start;
   102→      if (!groups[key]) {
   103→        groups[key] = [];
   104→      }
   105→      groups[key].push(clip);
   106→    }
   107→    return Object.values(groups);
   108→  }
   109→
   110→  /**
   111→   * Create a modified clip with new timing and updated link references
   112→   */
   113→  createModifiedClip(originalClip, newStart, newEnd, newIn, newOut, idMapping) {
   114→    let modified = originalClip.fullMatch;
   115→
   116→    // Update timing values
   117→    modified = modified.replace(/<start>\d+<\/start>/, `<start>${newStart}</start>`);
   118→    modified = modified.replace(/<end>\d+<\/end>/, `<end>${newEnd}</end>`);
   119→    modified = modified.replace(/<in>\d+<\/in>/, `<in>${newIn}</in>`);
   120→    modified = modified.replace(/<out>\d+<\/out>/, `<out>${newOut}</out>`);
   121→
   122→    const newDuration = newOut - newIn;
   123→    modified = modified.replace(/<duration>\d+<\/duration>/, `<duration>${newDuration}</duration>`);
   124→
   125→    // Update pproTicks if present
   126→    const ticksPerFrame = 254016000000 / this.timebase;
   127→    modified = modified.replace(
   128→      /<pproTicksIn>\d+<\/pproTicksIn>/,
   129→      `<pproTicksIn>${Math.round(newIn * ticksPerFrame)}</pproTicksIn>`
   130→    );
   131→    modified = modified.replace(
   132→      /<pproTicksOut>\d+<\/pproTicksOut>/,
   133→      `<pproTicksOut>${Math.round(newOut * ticksPerFrame)}</pproTicksOut>`
   134→    );
   135→
   136→    // Update clip ID if in mapping
   137→    if (idMapping[originalClip.id]) {
   138→      modified = modified.replace(
   139→        new RegExp(`id="${originalClip.id}"`),
   140→        `id="${idMapping[originalClip.id]}"`
   141→      );
   142→    }
   143→
   144→    // Update ALL link references using the mapping
   145→    for (const [oldId, newId] of Object.entries(idMapping)) {
   146→      modified = modified.replace(
   147→        new RegExp(`<linkclipref>${oldId}</linkclipref>`, 'g'),
   148→        `<linkclipref>${newId}</linkclipref>`
   149→      );
   150→    }
   151→
   152→    return modified;
   153→  }
   154→
   155→  /**
   156→   * Split clips at silence boundaries - handles linked clips together
   157→   */
   158→  splitClipsAtSilences(silences, removeGaps = true) {
   159→    const stats = {
   160→      clipsProcessed: 0,
   161→      clipsSplit: 0,
   162→      silencesRemoved: 0,
   163→      totalTimeRemoved: 0,
   164→      cuts: []
   165→    };
   166→
   167→    if (!silences || silences.length === 0) {
   168→      return stats;
   169→    }
   170→
   171→    const sortedSilences = [...silences].sort((a, b) => a.start - b.start);
   172→    const clips = this.findSequenceClipitems();
   173→    console.log(`[SPLICE] Found ${clips.length} sequence clips`);
   174→
   175→    // Group linked clips (same start time = linked)
