   282→/**
   283→ * Check if a sequence is currently open/active in the Timeline
   284→ * Uses multiple fallbacks to ensure accurate detection
   285→ * @returns {boolean} true if a sequence is open
   286→ */
   287→function checkSequenceOpen() {
   288→    try {
   289→        $.writeln('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC START ==========');
   290→        
   291→        // Check if app exists
   292→        if (!app) {
   293→            $.writeln('[SPLICE] checkSequenceOpen: ERROR - app object does not exist');
   294→            return false;
   295→        }
   296→        $.writeln('[SPLICE] checkSequenceOpen: app object exists');
   297→        
   298→        // Check if project exists
   299→        if (!app.project) {
   300→            $.writeln('[SPLICE] checkSequenceOpen: ERROR - app.project does not exist');
   301→            return false;
   302→        }
   303→        $.writeln('[SPLICE] checkSequenceOpen: app.project exists');
   304→        
   305→        // Check if sequences collection exists
   306→        if (!app.project.sequences) {
   307→            $.writeln('[SPLICE] checkSequenceOpen: ERROR - app.project.sequences does not exist');
   308→            return false;
   309→        }
   310→        $.writeln('[SPLICE] checkSequenceOpen: app.project.sequences exists');
   311→        
   312→        // Log total number of sequences
   313→        var numSequences = app.project.sequences.numSequences;
   314→        $.writeln('[SPLICE] checkSequenceOpen: Total sequences in project: ' + numSequences);
   315→        
   316→        // List all sequences with their details
   317→        for (var i = 0; i < numSequences; i++) {
   318→            var seq = app.project.sequences[i];
   319→            if (seq) {
   320→                $.writeln('[SPLICE] checkSequenceOpen:   - Sequence ' + i + ': name="' + seq.name + '", id=' + seq.sequenceID);
   321→            }
   322→        }
   323→        
   324→        // Primary check: Standard DOM - Check for activeSequence
   325→        $.writeln('[SPLICE] checkSequenceOpen: Checking app.project.activeSequence...');
   326→        if (app.project.activeSequence) {
   327→            var activeSeq = app.project.activeSequence;
   328→            $.writeln('[SPLICE] checkSequenceOpen: SUCCESS - Found active sequence via DOM');
   329→            $.writeln('[SPLICE] checkSequenceOpen:   - name: ' + activeSeq.name);
   330→            $.writeln('[SPLICE] checkSequenceOpen:   - sequenceID: ' + activeSeq.sequenceID);
   331→            $.writeln('[SPLICE] checkSequenceOpen:   - videoTracks: ' + activeSeq.videoTracks.numTracks);
   332→            $.writeln('[SPLICE] checkSequenceOpen:   - audioTracks: ' + activeSeq.audioTracks.numTracks);
   333→            $.writeln('[SPLICE] checkSequenceOpen:   - frameSize: ' + activeSeq.frameSizeHorizontal + 'x' + activeSeq.frameSizeVertical);
   334→            $.writeln('[SPLICE] checkSequenceOpen: ========== checkSequenceOpen DIAGNOSTIC END (TRUE) ==========');
   335→            return true;
   336→        }
   337→        $.writeln('[SPLICE] checkSequenceOpen: app.project.activeSequence is null/undefined');
   338→
   339→        // Fallback: Try QE DOM (requires enableQE first)
   340→        $.writeln('[SPLICE] checkSequenceOpen: Trying QE DOM fallback...');
   341→        if (enableQE()) {
   342→            try {
   343→                $.writeln('[SPLICE] checkSequenceOpen: QE DOM is enabled');
   344→                var qeSeq = qe.project.getActiveSequence();
   345→                if (qeSeq && qeSeq.name) {
   346→                    $.writeln('[SPLICE] checkSequenceOpen: SUCCESS - Found active sequence via QE DOM: ' + qeSeq.name);
   347→                    $.writeln('[SPLICE] checkSequenceOpen: ========== checkSequenceOpen DIAGNOSTIC END (TRUE) ==========');
   348→                    return true;
   349→                }
   350→                $.writeln('[SPLICE] checkSequenceOpen: QE DOM returned null or sequence without name');
   351→            } catch (qeErr) {
   352→                $.writeln('[SPLICE] checkSequenceOpen: QE sequence check failed: ' + qeErr.message);
   353→            }
   354→        } else {
   355→            $.writeln('[SPLICE] checkSequenceOpen: QE DOM is NOT available/enabled');
   356→        }
   357→
   358→        $.writeln('[SPLICE] checkSequenceOpen: No active sequence found in either DOM or QE DOM');
   359→        $.writeln('[SPLICE] checkSequenceOpen: ========== checkSequenceOpen DIAGNOSTIC END (FALSE) ==========');
   360→        return false;
   361→    } catch (e) {
   362→        $.writeln('[SPLICE] checkSequenceOpen: EXCEPTION - ' + e.message);
   363→        $.writeln('[SPLICE] checkSequenceOpen: Stack: ' + (e.stack || 'no stack available'));
   364→        $.writeln('[SPLICE] checkSequenceOpen: ========== checkSequenceOpen DIAGNOSTIC END (ERROR) ==========');
   365→        return false;
   366→    }
   367→}
