   373→// ============================================================================
   374→// TIME CONVERSION UTILITIES
   375→// ============================================================================
   376→
   377→/**
   378→ * Get the actual frames per second from the sequence
   379→ * videoFrameRate.seconds gives seconds per frame, so we need to invert it
   380→ */
   381→function getActualFrameRate() {
   382→    var secsPerFrame = app.project.activeSequence.getSettings().videoFrameRate.seconds;
   383→    // secsPerFrame is e.g., 0.03333... for 30fps, so 1/secsPerFrame = 30
   384→    return 1 / secsPerFrame;
   385→}
   386→
   387→function quantise_time(time_seconds) {
   388→    // Get seconds per frame (e.g., 0.0333 for 30fps)
   389→    var secsPerFrame = app.project.activeSequence.getSettings().videoFrameRate.seconds;
   390→    // Round to nearest frame boundary
   391→    return secsPerFrame * Math.round(time_seconds / secsPerFrame);
   392→}
   393→
   394→function frames_to_time(number_of_frames) {
   395→    // seconds per frame * number of frames = total seconds
   396→    var secsPerFrame = app.project.activeSequence.getSettings().videoFrameRate.seconds;
   397→    return number_of_frames * secsPerFrame;
   398→}
   399→
   400→function frames_to_ticks(number_of_frames) {
   401→    // Convert frames to ticks: frames * seconds_per_frame * ticks_per_second
   402→    var secsPerFrame = app.project.activeSequence.getSettings().videoFrameRate.seconds;
   403→    return number_of_frames * secsPerFrame * TICKS_PER_SECOND;
   404→}
   405→
   406→function seconds_to_ticks(seconds) {
   407→    return seconds * TICKS_PER_SECOND;
   408→}
   409→
   410→function ticks_to_seconds(ticks) {
   411→    return ticks / TICKS_PER_SECOND;
   412→}
