   414→function getVideoFrameRateInSeconds() {
   415→    try {
   416→        var seq = app.project.activeSequence;
   417→        if (!seq) {
   418→            $.writeln('[SPLICE] getVideoFrameRateInSeconds: No active sequence');
   419→            return null;
   420→        }
   421→        return seq.getSettings().videoFrameRate.seconds;
   422→    } catch (e) {
   423→        $.writeln('[SPLICE] getVideoFrameRateInSeconds error: ' + e.message);
   424→        return null;
   425→    }
